'use client'

/**
 * Settings Page - v6.0 统一布局版
 *
 * v6.0 变更:
 * - 使用与 Chat 页相同的 NavBar 组件，保持左侧栏一致性
 * - 移除 PCLayout，使用自定义布局
 * - 修复退出登录功能
 *
 * PC端布局: NavBar + 设置菜单 + 设置内容
 * 移动端布局: 单栏设置列表
 */

import { useState, useEffect, useCallback, memo, useMemo, useId, Suspense } from 'react'
import { useRouter, useSearchParams } from 'next/navigation'
import { NavBar } from '@/components/layout/NavBar'
import { VibeGlyph } from '@/components/core'
import { BirthDateInputs } from '@/components/ui/Input'
import { getLocalStorageJSON, getAccessToken, setLocalStorageJSON, invalidateStorageCache } from '@/utils/storage'
import { AccountDeletionSection } from '@/components/settings/AccountDeletionSection'
import { SkillCard } from '@/components/skill'
import { useSkillSubscription, useSkills } from '@/hooks/useSkillSubscription'
import { useAuth } from '@/providers/AuthProvider'
import { cn } from '@/lib/utils'

// Use Next.js API routes for server communication to avoid exposing backend URL
const API_BASE = '/api'

const SETTINGS_MENU = [
  { id: 'profile', icon: '👤', label: '个人资料' },
  { id: 'skills', icon: '🎯', label: 'Skill 管理' },
  { id: 'notifications', icon: '🔔', label: '通知设置' },
  { id: 'membership', icon: '💎', label: '会员管理' },
  { id: 'preferences', icon: '⚙️', label: '偏好设置' },
  { id: 'account', icon: '⚠️', label: '账户管理' },
  { id: 'help', icon: '❓', label: '帮助与反馈' },
  { id: 'about', icon: '📄', label: '关于' },
]

interface UserProfile {
  nickname: string
  email: string
  avatar: string
  birthDate: string
  birthTime: string
  timezone: string
}

// ═══════════════════════════════════════════════════════════════════════════
// Skill 管理区块
// ═══════════════════════════════════════════════════════════════════════════

function SkillSettingsSection({ router }: { router: ReturnType<typeof useRouter> }) {
  const { skills } = useSkills()
  const { subscriptions, togglePush, unsubscribe, userStatuses } = useSkillSubscription()

  // 分组
  const { subscribed, available } = useMemo(() => {
    const subscribedIds = new Set(
      subscriptions.filter(s => s.status === 'subscribed').map(s => s.skill_id)
    )
    return {
      subscribed: skills.filter(s => subscribedIds.has(s.id)),
      available: skills.filter(s => !subscribedIds.has(s.id) && s.category !== 'core'),
    }
  }, [skills, subscriptions])

  const handleTogglePush = async (skillId: string, enabled: boolean) => {
    try {
      await togglePush(skillId, enabled)
    } catch (err) {
      console.error('Toggle push failed:', err)
    }
  }

  const handleUnsubscribe = async (skillId: string) => {
    try {
      await unsubscribe(skillId)
    } catch (err) {
      console.error('Unsubscribe failed:', err)
    }
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="font-serif text-xl text-text-primary">Skill 管理</h2>
        <button
          onClick={() => router.push('/skills')}
          className="text-sm text-accent hover:underline focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent focus-visible:ring-offset-2 rounded"
          aria-label="探索更多 Skill"
        >
          探索更多 →
        </button>
      </div>

      {/* 已订阅 */}
      <div>
        <h3 className="text-sm font-medium text-text-secondary mb-3">
          已订阅 ({subscribed.length})
        </h3>
        <div className="space-y-2">
          {subscribed.map((skill) => {
            const sub = subscriptions.find(s => s.skill_id === skill.id)
            const isCore = skill.category === 'core'
            return (
              <div key={skill.id} className="flex items-center gap-3 p-3 rounded-lg bg-[var(--bg-card-alt)]">
                <div
                  className="w-10 h-10 rounded-xl flex items-center justify-center text-xl"
                  style={{ backgroundColor: `${skill.color}20` }}
                >
                  {skill.icon}
                </div>
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2">
                    <span className="font-medium text-text-primary">{skill.name}</span>
                    {isCore && (
                      <span className="px-1.5 py-0.5 text-xs rounded bg-accent/10 text-accent">
                        始终激活
                      </span>
                    )}
                  </div>
                  <p className="text-xs text-text-secondary truncate">
                    {skill.features?.slice(0, 2).map(f => f.name).join(' · ')}
                  </p>
                </div>
                {!isCore && (
                  <div className="flex items-center gap-3">
                    <div className="flex items-center gap-2">
                      <label
                        htmlFor={`push-${skill.id}`}
                        className="text-xs text-text-tertiary cursor-pointer"
                      >
                        推送
                      </label>
                      <div className="relative inline-flex items-center">
                        <input
                          id={`push-${skill.id}`}
                          type="checkbox"
                          checked={sub?.push_enabled ?? false}
                          onChange={(e) => handleTogglePush(skill.id, e.target.checked)}
                          className="sr-only peer"
                          aria-describedby={`push-desc-${skill.id}`}
                        />
                        <div className="w-9 h-5 bg-[var(--bg-secondary)] peer-focus-visible:ring-2 peer-focus-visible:ring-accent/50 peer-focus-visible:ring-offset-2 rounded-full peer peer-checked:after:translate-x-full after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-4 after:w-4 after:transition-transform peer-checked:bg-accent cursor-pointer" />
                        <span id={`push-desc-${skill.id}`} className="sr-only">
                          {sub?.push_enabled ? '关闭' : '开启'} {skill.name} 推送通知
                        </span>
                      </div>
                    </div>
                    <button
                      onClick={() => handleUnsubscribe(skill.id)}
                      className="text-xs text-error hover:underline focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-error focus-visible:ring-offset-2 rounded"
                      aria-label={`取消订阅 ${skill.name}`}
                    >
                      取消
                    </button>
                  </div>
                )}
              </div>
            )
          })}
        </div>
      </div>

      {/* 可订阅 */}
      {available.length > 0 && (
        <div>
          <h3 className="text-sm font-medium text-text-secondary mb-3">
            可订阅 ({available.length})
          </h3>
          <div className="space-y-2">
            {available.slice(0, 3).map((skill) => (
              <div
                key={skill.id}
                role="button"
                tabIndex={0}
                onClick={() => router.push(`/skills/${skill.id}`)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' || e.key === ' ') {
                    e.preventDefault()
                    router.push(`/skills/${skill.id}`)
                  }
                }}
                className="flex items-center gap-3 p-3 rounded-lg bg-[var(--bg-card-alt)] cursor-pointer hover:bg-[var(--bg-secondary)] transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent focus-visible:ring-offset-2"
                aria-label={`查看 ${skill.name} 详情`}
              >
                <div
                  className="w-10 h-10 rounded-xl flex items-center justify-center text-xl"
                  style={{ backgroundColor: `${skill.color}20` }}
                  aria-hidden="true"
                >
                  {skill.icon}
                </div>
                <div className="flex-1 min-w-0">
                  <span className="font-medium text-text-primary">{skill.name}</span>
                  <p className="text-xs text-text-secondary truncate">
                    {skill.showcase?.tagline || skill.description}
                  </p>
                </div>
                <span className="px-3 py-1 text-xs rounded-full bg-accent text-white" aria-hidden="true">
                  订阅
                </span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  )
}

// ═══════════════════════════════════════════════════════════════════════════
// 通知设置区块 - 使用 Skill 级别的推送设置
// ═══════════════════════════════════════════════════════════════════════════

function NotificationSettingsSection() {
  const { skills } = useSkills()
  const { subscriptions, togglePush } = useSkillSubscription()

  // 只显示已订阅的 Skills 的推送设置
  const subscribedSkills = useMemo(() => {
    const subscribedIds = new Set(
      subscriptions.filter(s => s.status === 'subscribed').map(s => s.skill_id)
    )
    return skills.filter(s => subscribedIds.has(s.id))
  }, [skills, subscriptions])

  const handleTogglePush = async (skillId: string, enabled: boolean) => {
    try {
      await togglePush(skillId, enabled)
    } catch (err) {
      console.error('Toggle push failed:', err)
    }
  }

  return (
    <div className="space-y-6">
      <h2 className="font-serif text-xl text-text-primary">通知设置</h2>
      <p className="text-sm text-text-secondary">
        管理你订阅的 Skills 的推送通知。
      </p>

      {subscribedSkills.length === 0 ? (
        <div className="text-center py-8 text-text-secondary">
          <p>暂无已订阅的 Skill</p>
          <p className="text-sm mt-2">订阅 Skill 后可在此管理推送通知</p>
        </div>
      ) : (
        <div className="space-y-3">
          {subscribedSkills.map((skill) => {
            const sub = subscriptions.find(s => s.skill_id === skill.id)
            const toggleId = `notify-${skill.id}`
            return (
              <div key={skill.id} className="flex items-center justify-between p-4 bg-[var(--bg-card-alt)] rounded-lg">
                <label htmlFor={toggleId} className="flex items-center gap-3 cursor-pointer flex-1">
                  <div
                    className="w-10 h-10 rounded-xl flex items-center justify-center text-xl"
                    style={{ backgroundColor: `${skill.color}20` }}
                    aria-hidden="true"
                  >
                    {skill.icon}
                  </div>
                  <div>
                    <p className="text-text-primary font-medium">{skill.name}</p>
                    <p className="text-sm text-text-secondary">接收 {skill.name} 相关推送</p>
                  </div>
                </label>
                <div className="relative inline-flex items-center">
                  <input
                    id={toggleId}
                    type="checkbox"
                    checked={sub?.push_enabled ?? false}
                    onChange={(e) => handleTogglePush(skill.id, e.target.checked)}
                    className="sr-only peer"
                    aria-label={`${sub?.push_enabled ? '关闭' : '开启'} ${skill.name} 推送通知`}
                  />
                  <div className="w-11 h-6 bg-[var(--bg-secondary)] peer-focus-visible:ring-2 peer-focus-visible:ring-accent peer-focus-visible:ring-offset-2 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-5 after:w-5 after:transition-transform peer-checked:bg-accent cursor-pointer"></div>
                </div>
              </div>
            )
          })}
        </div>
      )}

      <div className="pt-4 border-t border-[var(--border-light)]">
        <p className="text-xs text-text-tertiary">
          推送通知用于提醒你重要的运势变化和个性化建议。你可以随时在这里调整设置。
        </p>
      </div>
    </div>
  )
}

// ═══════════════════════════════════════════════════════════════════════════
// 记忆管理区块 - 显示用户洞察
// ═══════════════════════════════════════════════════════════════════════════
// 帮助与反馈区块
// ═══════════════════════════════════════════════════════════════════════════

function HelpFeedbackSection() {
  return (
    <div className="space-y-6">
      <h2 className="font-serif text-xl text-text-primary">帮助与反馈</h2>

      <div className="space-y-4">
        <a
          href="mailto:support@vibelife.app"
          className="block p-4 bg-[var(--bg-card-alt)] rounded-lg hover:bg-[var(--bg-secondary)] transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent focus-visible:ring-offset-2"
        >
          <div className="flex items-center gap-3">
            <span className="text-xl" aria-hidden="true">📧</span>
            <div>
              <p className="text-text-primary font-medium">联系客服</p>
              <p className="text-sm text-text-secondary">support@vibelife.app</p>
            </div>
          </div>
        </a>

        <a
          href="https://vibelife.app/faq"
          target="_blank"
          rel="noopener noreferrer"
          className="block p-4 bg-[var(--bg-card-alt)] rounded-lg hover:bg-[var(--bg-secondary)] transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent focus-visible:ring-offset-2"
        >
          <div className="flex items-center gap-3">
            <span className="text-xl" aria-hidden="true">❓</span>
            <div>
              <p className="text-text-primary font-medium">常见问题</p>
              <p className="text-sm text-text-secondary">查看常见问题解答</p>
            </div>
          </div>
        </a>

        <a
          href="https://vibelife.app/feedback"
          target="_blank"
          rel="noopener noreferrer"
          className="block p-4 bg-[var(--bg-card-alt)] rounded-lg hover:bg-[var(--bg-secondary)] transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent focus-visible:ring-offset-2"
        >
          <div className="flex items-center gap-3">
            <span className="text-xl" aria-hidden="true">💡</span>
            <div>
              <p className="text-text-primary font-medium">功能建议</p>
              <p className="text-sm text-text-secondary">告诉我们你希望看到的功能</p>
            </div>
          </div>
        </a>

        <a
          href="https://vibelife.app/bug-report"
          target="_blank"
          rel="noopener noreferrer"
          className="block p-4 bg-[var(--bg-card-alt)] rounded-lg hover:bg-[var(--bg-secondary)] transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent focus-visible:ring-offset-2"
        >
          <div className="flex items-center gap-3">
            <span className="text-xl" aria-hidden="true">🐛</span>
            <div>
              <p className="text-text-primary font-medium">报告问题</p>
              <p className="text-sm text-text-secondary">发现问题？告诉我们</p>
            </div>
          </div>
        </a>
      </div>
    </div>
  )
}

// 登录检查 Wrapper
function SettingsAuthGuard({ children }: { children: React.ReactNode }) {
  const router = useRouter()
  const { user, isLoaded: authLoaded } = useAuth()

  if (!authLoaded) {
    return (
      <div className="h-screen flex items-center justify-center bg-background">
        <div className="animate-pulse text-muted-foreground">加载中...</div>
      </div>
    )
  }

  if (!user) {
    return (
      <div className="h-screen flex flex-col bg-background">
        <div className="flex-1 flex flex-col items-center justify-center p-8">
          <div className="w-20 h-20 rounded-2xl bg-gradient-to-br from-vellum-200 to-vellum-300 flex items-center justify-center mb-6">
            <span className="text-4xl">⚙️</span>
          </div>
          <h2 className="text-2xl font-bold text-foreground mb-2">设置</h2>
          <p className="text-muted-foreground text-center max-w-md mb-6">
            登录后即可管理你的个人资料、订阅和偏好设置。
          </p>
          <button
            onClick={() => router.push('/auth/login?redirect=/settings')}
            className="px-6 py-3 rounded-full bg-accent-primary text-white font-medium hover:bg-accent-primary/90 transition-colors"
          >
            登录
          </button>
        </div>
      </div>
    )
  }

  return <>{children}</>
}

function SettingsPageContent() {
  const router = useRouter()
  const searchParams = useSearchParams()

  // URL 状态同步 - 从 URL 读取 section 参数
  const sectionFromUrl = searchParams.get('section')
  const validSections = SETTINGS_MENU.map(m => m.id)
  const initialSection = sectionFromUrl && validSections.includes(sectionFromUrl) ? sectionFromUrl : 'profile'

  const [activeSection, setActiveSection] = useState(initialSection)

  // 同步 activeSection 到 URL
  const handleSectionChange = useCallback((sectionId: string) => {
    setActiveSection(sectionId)
    const url = new URL(window.location.href)
    url.searchParams.set('section', sectionId)
    router.replace(url.pathname + url.search, { scroll: false })
  }, [router])
  const [loading, setLoading] = useState(false)
  const [voiceMode, setVoiceModeState] = useState<"warm" | "sarcastic" | "wise">("warm")
  const [profile, setProfile] = useState<UserProfile>(() => ({
    nickname: '用户',
    email: 'user@example.com',
    avatar: '👤',
    // Do not prefill with fake defaults; leave empty until loaded
    birthDate: '',
    birthTime: '',
    timezone: 'Asia/Shanghai',
  }))
  // Local Y/M/D states for better date UX (year select solves typing issue)
  const [birthYear, setBirthYear] = useState('')
  const [birthMonth, setBirthMonth] = useState('')
  const [birthDay, setBirthDay] = useState('')

  useEffect(() => {
    // Prefer authoritative profile from API; fall back to cached localStorage
    const user = getLocalStorageJSON<Record<string, string> | null>('user', null)
    if (user) {
      setProfile(prev => ({
        ...prev,
        nickname: user.nickname || user.display_name || '用户',
        email: user.email || '',
        avatar: user.avatar || '👤',
        timezone: user.timezone || 'Asia/Shanghai',
      }))
    }

    const token = getAccessToken()
    if (!token) return

    // Fetch current profile to populate birth date/time accurately
    fetch(`${API_BASE}/profile`, {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(async (res) => {
        if (!res.ok) return null
        return res.json()
      })
      .then((data) => {
        if (!data) return
        // Parse ISO like "YYYY-MM-DDTHH:MM:SS" without TZ shift
        const iso: string | undefined = data.birth_datetime
        let birthDate = ''
        let birthTime = ''
        if (iso && typeof iso === 'string') {
          const [d, t] = iso.split('T')
          birthDate = d || ''
          if (t) birthTime = t.slice(0, 5) // HH:MM
        }

        setProfile(prev => ({
          ...prev,
          nickname: data.display_name ?? prev.nickname,
          avatar: data.avatar_url ? '👤' : prev.avatar,
          birthDate,
          birthTime,
          timezone: data.timezone || prev.timezone,
        }))
      })
      .catch(() => {})

    // Load voice mode preference
    fetch(`${API_BASE}/preferences`, {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.ok ? res.json() : null)
      .then(data => {
        if (data?.voice_mode) {
          setVoiceModeState(data.voice_mode as "warm" | "sarcastic" | "wise")
        }
      })
      .catch(() => {})
  }, [])

  // Save voice mode to API
  const setVoiceMode = useCallback(async (mode: "warm" | "sarcastic" | "wise") => {
    setVoiceModeState(mode)
    const token = getAccessToken()
    if (!token) return
    try {
      await fetch(`${API_BASE}/preferences`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ voice_mode: mode })
      })
    } catch (error) {
      console.error('Failed to save voice mode:', error)
    }
  }, [])

  // Keep local Y/M/D in sync when profile.birthDate changes (e.g., loaded from API)
  useEffect(() => {
    const [y, m, d] = (profile.birthDate || '').split('-')
    setBirthYear(y || '')
    setBirthMonth(m || '')
    setBirthDay(d || '')
  }, [profile.birthDate])

  // Use useCallback for stable reference (Vercel rule: rerender-functional-setstate)
  const handleProfileChange = useCallback((field: keyof UserProfile, value: string) => {
    setProfile(prev => ({ ...prev, [field]: value }))
  }, [])

  const handleSaveProfile = useCallback(async () => {
    setLoading(true)
    try {
      const user = getLocalStorageJSON<Record<string, string> | null>('user', null)
      const token = getAccessToken()

      if (user?.user_id && token) {
        const body: any = {
          display_name: profile.nickname,
          timezone: profile.timezone,
        }
        // When both date and time present -> set; otherwise explicitly clear on server
        if (profile.birthDate && profile.birthTime) {
          body.birth_datetime = `${profile.birthDate}T${profile.birthTime}:00`
        } else {
          body.birth_datetime = null
        }

        const res = await fetch(`${API_BASE}/profile`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify(body),
        })

        if (res.ok) {
          // Update local cache with normalized values
          setLocalStorageJSON('user', {
            ...user,
            nickname: profile.nickname,
            display_name: profile.nickname,
            birth_date: profile.birthDate || undefined,
            birth_time: profile.birthTime || undefined,
            timezone: profile.timezone,
          })
        }
      }
    } catch (err) {
      console.error('Failed to save profile:', err)
    } finally {
      setLoading(false)
    }
  }, [profile])

  // 设置内容区域
  const SettingsContent = () => {
    switch (activeSection) {
      case 'profile':
        return (
          <div className="space-y-6">
            <h2 className="font-serif text-xl text-text-primary">个人资料</h2>
            <div className="space-y-4">
              <div>
                <label className="block text-sm text-text-secondary mb-2">头像</label>
                <div className="flex items-center gap-4">
                  <div className="w-16 h-16 rounded-full bg-[var(--bg-secondary)] flex items-center justify-center text-3xl">
                    {profile.avatar}
                  </div>
                  <button className="btn btn-secondary">更换头像</button>
                </div>
              </div>
              <div>
                <label htmlFor="nickname" className="block text-sm text-text-secondary mb-2">昵称</label>
                <input
                  id="nickname"
                  type="text"
                  value={profile.nickname}
                  onChange={(e) => handleProfileChange('nickname', e.target.value)}
                  className="input"
                  autoComplete="nickname"
                />
              </div>
              <div>
                <label htmlFor="email" className="block text-sm text-text-secondary mb-2">邮箱</label>
                <input
                  id="email"
                  type="email"
                  value={profile.email}
                  disabled
                  className="input bg-[var(--bg-secondary)]"
                  autoComplete="email"
                  aria-describedby="email-hint"
                />
                <p id="email-hint" className="sr-only">邮箱地址不可修改</p>
              </div>
              <div>
                <label className="block text-sm text-text-secondary mb-2">出生日期</label>
                <BirthDateInputs
                  year={birthYear}
                  month={birthMonth}
                  day={birthDay}
                  onYearChange={(v) => {
                    setBirthYear(v)
                    const dateStr = v && birthMonth && birthDay ? `${v}-${birthMonth}-${birthDay}` : ''
                    handleProfileChange('birthDate', dateStr)
                  }}
                  onMonthChange={(v) => {
                    setBirthMonth(v)
                    const dateStr = birthYear && v && birthDay ? `${birthYear}-${v}-${birthDay}` : ''
                    handleProfileChange('birthDate', dateStr)
                  }}
                  onDayChange={(v) => {
                    setBirthDay(v)
                    const dateStr = birthYear && birthMonth && v ? `${birthYear}-${birthMonth}-${v}` : ''
                    handleProfileChange('birthDate', dateStr)
                  }}
                  showHour={false}
                />
              </div>
              <div>
                <label htmlFor="birthTime" className="block text-sm text-text-secondary mb-2">出生时间</label>
                <div className="flex items-center gap-3">
                  <input
                    id="birthTime"
                    type="time"
                    step={60}
                    value={profile.birthTime}
                    onChange={(e) => handleProfileChange('birthTime', e.target.value)}
                    className="input"
                    placeholder="--:--"
                    autoComplete="off"
                  />
                  <label htmlFor="birthTimeUnknown" className="inline-flex items-center gap-2 text-sm text-text-secondary cursor-pointer">
                    <input
                      id="birthTimeUnknown"
                      type="checkbox"
                      checked={!profile.birthTime}
                      onChange={(e) => handleProfileChange('birthTime', e.target.checked ? '' : '12:00')}
                      className="rounded border-gray-300 focus:ring-accent"
                    />
                    不确定
                  </label>
                </div>
              </div>
              <div>
                <label htmlFor="timezone" className="block text-sm text-text-secondary mb-2">时区</label>
                <select
                  id="timezone"
                  value={profile.timezone}
                  onChange={(e) => handleProfileChange('timezone', e.target.value)}
                  className="input"
                >
                  <option value="Asia/Shanghai">Asia/Shanghai (UTC+8)</option>
                  <option value="Asia/Hong_Kong">Asia/Hong_Kong (UTC+8)</option>
                  <option value="Asia/Tokyo">Asia/Tokyo (UTC+9)</option>
                  <option value="America/New_York">America/New_York (UTC-5)</option>
                  <option value="America/Los_Angeles">America/Los_Angeles (UTC-8)</option>
                  <option value="Europe/London">Europe/London (UTC+0)</option>
                </select>
              </div>
              <button onClick={handleSaveProfile} disabled={loading} className="btn btn-primary">
                {loading ? '保存中…' : '保存更改'}
              </button>
            </div>
          </div>
        )
      case 'skills':
        return <SkillSettingsSection router={router} />
      case 'notifications':
        return <NotificationSettingsSection />
      case 'membership':
        return (
          <div className="space-y-6">
            <h2 className="font-serif text-xl text-text-primary">会员管理</h2>
            <div className="card p-6">
              <div className="flex items-center justify-between mb-4">
                <div>
                  <p className="text-text-primary font-medium">当前状态</p>
                  <p className="text-sm text-text-secondary">免费用户</p>
                </div>
                <span className="px-3 py-1 bg-[var(--bg-secondary)] rounded-full text-sm text-text-secondary">Free</span>
              </div>
              <button onClick={() => router.push('/membership')} className="btn btn-premium w-full">
                ✨ 升级 Premium
              </button>
            </div>
            <div className="space-y-3">
              <p className="text-sm text-text-secondary">Premium 会员权益：</p>
              <ul className="space-y-2 text-sm text-text-secondary">
                <li className="flex items-center gap-2"><span className="text-success">✓</span> 完整每日运势</li>
                <li className="flex items-center gap-2"><span className="text-success">✓</span> 30&#x00A0;次/天 AI对话</li>
                <li className="flex items-center gap-2"><span className="text-success">✓</span> 完整大运/流年分析</li>
                <li className="flex items-center gap-2"><span className="text-success">✓</span> Identity Prism 深度解读</li>
              </ul>
            </div>
          </div>
        )
      case 'account':
        return (
          <div className="space-y-6">
            <h2 className="font-serif text-xl text-text-primary">账户管理</h2>
            <AccountDeletionSection />
          </div>
        )
      case 'preferences':
        return (
          <div className="space-y-6">
            <h2 className="font-serif text-xl text-text-primary">偏好设置</h2>
            <div className="space-y-4">
              {/* 语气模式 */}
              <div className="p-4 bg-[var(--bg-card-alt)] rounded-lg">
                <div className="mb-3">
                  <h3 className="text-text-primary font-medium mb-1">语气模式</h3>
                  <p className="text-sm text-text-secondary">选择 AI 的对话风格</p>
                </div>
                <div className="flex gap-3" role="radiogroup" aria-label="选择语气模式">
                  <button
                    role="radio"
                    aria-checked={voiceMode === 'warm'}
                    onClick={() => setVoiceMode('warm')}
                    className={`flex-1 px-4 py-3 rounded-lg text-sm font-medium transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent focus-visible:ring-offset-2 ${
                      voiceMode === 'warm'
                        ? 'bg-orange-100 text-orange-700 dark:bg-orange-900/30 dark:text-orange-300 shadow-sm'
                        : 'bg-[var(--bg-secondary)] text-text-secondary hover:bg-[var(--bg-tertiary)]'
                    }`}
                  >
                    <div className="text-2xl mb-1" aria-hidden="true">🌞</div>
                    <div>温暖支持</div>
                  </button>
                  <button
                    role="radio"
                    aria-checked={voiceMode === 'sarcastic'}
                    onClick={() => setVoiceMode('sarcastic')}
                    className={`flex-1 px-4 py-3 rounded-lg text-sm font-medium transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent focus-visible:ring-offset-2 ${
                      voiceMode === 'sarcastic'
                        ? 'bg-purple-100 text-purple-700 dark:bg-purple-900/30 dark:text-purple-300 shadow-sm'
                        : 'bg-[var(--bg-secondary)] text-text-secondary hover:bg-[var(--bg-tertiary)]'
                    }`}
                  >
                    <div className="text-2xl mb-1" aria-hidden="true">😈</div>
                    <div>直接挑战</div>
                  </button>
                  <button
                    role="radio"
                    aria-checked={voiceMode === 'wise'}
                    onClick={() => setVoiceMode('wise')}
                    className={`flex-1 px-4 py-3 rounded-lg text-sm font-medium transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent focus-visible:ring-offset-2 ${
                      voiceMode === 'wise'
                        ? 'bg-blue-100 text-blue-700 dark:bg-blue-900/30 dark:text-blue-300 shadow-sm'
                        : 'bg-[var(--bg-secondary)] text-text-secondary hover:bg-[var(--bg-tertiary)]'
                    }`}
                  >
                    <div className="text-2xl mb-1" aria-hidden="true">🧙</div>
                    <div>睿智导师</div>
                  </button>
                </div>
                <div className="mt-3 p-3 bg-[var(--bg-callout)] rounded text-xs text-text-secondary">
                  {voiceMode === 'warm' && '💭 像一个理解你的老朋友，温暖但不失洞察'}
                  {voiceMode === 'sarcastic' && '💭 用幽默和犀利的方式戳破你的自欺欺人，推动你面对真相'}
                  {voiceMode === 'wise' && '💭 像一位经历丰富的长辈，用深度和理性引导思考'}
                </div>
              </div>
            </div>
          </div>
        )
      case 'help':
        return <HelpFeedbackSection />
      case 'about':
        return (
          <div className="space-y-6">
            <h2 className="font-serif text-xl text-text-primary">关于 VibeLife</h2>
            <div className="text-center py-8">
              <span className="text-5xl mb-4 block">🌟</span>
              <h3 className="font-serif text-2xl text-text-primary mb-2">VibeLife</h3>
              <p className="text-text-secondary mb-4">探索内心世界</p>
              <p className="text-sm text-text-secondary">版本&#x00A0;6.0.0</p>
