        <div className="flex items-center gap-2">
          <VibeGlyph size="sm" skill="bazi" showAura={false} animate={false} />
          <span className="font-serif font-semibold text-foreground">设置</span>
        </div>
      </header>

      <main className="max-w-lg mx-auto px-4 py-6">
        <h1 className="font-serif text-xl text-foreground mb-4">设置</h1>
        <nav className="space-y-2" aria-label="设置菜单">
          {SETTINGS_MENU.map((item) => (
            <button
              key={item.id}
              onClick={() => {
                if (item.id === 'membership') {
                  router.push('/membership')
                } else {
                  handleSectionChange(item.id)
                }
              }}
              aria-current={activeSection === item.id ? 'page' : undefined}
              className="w-full bg-card p-4 flex items-center gap-3 rounded-lg shadow-sm focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent focus-visible:ring-offset-2"
            >
              <div className="w-9 h-9 rounded-lg bg-gradient-to-br from-vellum-100 to-vellum-200 flex items-center justify-center flex-shrink-0">
                <span className="text-lg" aria-hidden="true">{item.icon}</span>
              </div>
              <span className="text-foreground">{item.label}</span>
              <span className="ml-auto text-muted-foreground" aria-hidden="true">›</span>
            </button>
          ))}
        </nav>

        <div className="mt-8">
          <button
            onClick={handleLogout}
            className="w-full p-4 text-center text-red-500 bg-card rounded-lg shadow-sm focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-red-500 focus-visible:ring-offset-2"
            aria-label="退出登录当前账户"
          >
            退出登录
          </button>
        </div>

        <div className="mt-8 text-center text-sm text-muted-foreground">
          <p>VibeLife v&#x00A0;6.0.0</p>
        </div>
      </main>
    </div>
  )

  return (
    <div className="h-screen flex flex-col bg-background">
      {/* PC端布局 */}
      <div className="hidden lg:flex h-full">
        {/* 左侧 NavBar - 与 Chat 页保持一致 */}
        <NavBar
          skill="bazi"
          activeTab="me"
          onTabChange={(tab) => {
            if (tab === 'chat') router.push('/chat')
            else if (tab === 'journey') router.push('/chat?tab=journey')
            else if (tab === 'discover') router.push('/chat?tab=discover')
            else if (tab === 'me') router.push('/chat?tab=me')
          }}
          activeConversationId={null}
          onSelectConversation={() => {}}
          onNewChat={() => router.push('/chat')}
        />

        {/* 设置菜单区块 */}
        <SettingsMenuSection />

        {/* 主内容区域 */}
        <PCMainContent />
      </div>

      {/* 移动端布局 */}
      <MobileContentView />
    </div>
  )
}

// 使用 Suspense 包裹，因为使用了 useSearchParams
export default function SettingsPage() {
  return (
    <Suspense fallback={
      <div className="h-screen flex items-center justify-center bg-background">
        <div className="animate-pulse text-muted-foreground">加载中...</div>
      </div>
    }>
      <SettingsAuthGuard>
        <SettingsPageContent />
      </SettingsAuthGuard>
    </Suspense>
  )
}

