    dashboard,
    isLoading: isDashboardLoading,
    checkIn: checkInFn,
    toggleLever,
    toggleRock,
  } = useDashboard();

  // Wrapper for checkIn to return void
  const checkIn = useCallback(async () => {
    await checkInFn();
  }, [checkInFn]);

  // 从 URL 参数获取 skill、scenario、prompt 和 intro
  const urlSkill = searchParams.get("skill");
  const urlScenario = searchParams.get("scenario");
  const urlPrompt = searchParams.get("prompt");
  const urlIntro = searchParams.get("intro");
  const [initialPrompt, setInitialPrompt] = useState<string | null>(null);
  const [scenario, setScenario] = useState<string | null>(null);
  const [forceIntro, setForceIntro] = useState(false);

  // 处理 URL 参数
  useEffect(() => {
    // 如果 URL 指定了 skill，切换到该 skill
    if (urlSkill && urlSkill !== skill) {
      setSkill(urlSkill as typeof skill);
    }

    // 如果 URL 指定了 scenario，设置 scenario
    if (urlScenario) {
      setScenario(urlScenario);
    }

    // 如果 URL 指定了 intro=1，强制显示 Intro Card
    if (urlIntro === "1") {
      setForceIntro(true);
    }

    // 如果 URL 指定了 prompt，设置初始消息
    if (urlPrompt) {
      const decoded = decodeURIComponent(urlPrompt);
      setInitialPrompt(decoded);
    }

    // 清理 URL 参数（prompt 和 intro），避免刷新时重复触发
    if (urlPrompt || urlIntro) {
      const newUrl = window.location.pathname + (urlSkill ? `?skill=${urlSkill}` : "");
      router.replace(newUrl, { scroll: false });
    }
  }, [urlSkill, urlScenario, urlPrompt, urlIntro, skill, setSkill, router]);

  return (
    <LuminousPaper skill={skill} variant="default" className="h-full relative">
      <BreathAura skill={skill} size="xl" position="top" intensity="low" className="opacity-20" />
      {/*
        v7.2: 自动路由模式
        - 不传 skillId，让后端 CoreAgent 通过 LLM 自动识别用户意图
        - skill 仅用于 UI 展示（主题、空状态等）
        - 用户说"星座分析"会自动路由到 zodiac，说"八字"会路由到 bazi
        - initialPrompt: 从通知跳转时的预设消息
        - scenario: 从 URL 传入的 scenario/rule ID

        v9: Dashboard 整合
        - 空状态展示 Dashboard 组件（Ambient、Lifecoach、MySkills）
        - 预加载 Dashboard 数据，提升用户体验
      */}
      <ChatContainer
        skill={skill}
        scenario={scenario || undefined}
        voiceMode={voiceMode}
        conversationId={activeConversationId || undefined}
        onConversationStart={(id) => {
          // When a new conversation starts, update the URL
          if (!activeConversationId) {
            setActiveConversationId(id);
          }
        }}
        initialPrompt={initialPrompt}
        onInitialPromptSent={() => setInitialPrompt(null)}
        dashboardData={dashboard}
        isDashboardLoading={isDashboardLoading}
        onCheckIn={checkIn}
        onToggleLever={toggleLever}
        onToggleRock={toggleRock}
        forceIntro={forceIntro}
      />
    </LuminousPaper>
  );
});

