/**
 * SkillIntroCard React Hooks (SWR)
 *
 * 使用 SWR 进行数据获取，遵循项目标准模式
 * @see rules/client-swr-dedup - Use SWR for automatic request deduplication
 */

'use client';

import { useCallback } from 'react';
import useSWR, { useSWRConfig } from 'swr';
import {
  fetchSkillIntro,
  subscribeSkill,
  unsubscribeSkill,
  updateSkillSettings,
  markFirstUse,
} from '../api';
import type { UserSkillSettings, SkillIntroData } from '../types';

const CACHE_KEY_PREFIX = 'skill-intro';

/**
 * 获取 Skill 介绍数据
 */
export function useSkillIntro(skillId: string) {
  const { data, error, isLoading, mutate } = useSWR<SkillIntroData>(
    skillId ? [CACHE_KEY_PREFIX, skillId] : null,
    () => fetchSkillIntro(skillId),
    {
      revalidateOnFocus: false,
      dedupingInterval: 5 * 60 * 1000, // 5 分钟内不重复请求
    }
  );

  return {
    data,
    error,
    isLoading,
    refresh: mutate,
  };
}

/**
 * 订阅 Skill
 */
export function useSubscribe(skillId: string) {
  const { mutate } = useSWRConfig();

  const subscribe = useCallback(
    async (pushEnabled: boolean = true) => {
      const result = await subscribeSkill(skillId, pushEnabled);
      // 刷新相关缓存
      mutate([CACHE_KEY_PREFIX, skillId]);
      mutate('skill-subscriptions');
      mutate('skill-list');
      return result;
    },
    [skillId, mutate]
  );

  return { subscribe };
}

/**
 * 取消订阅 Skill
 */
export function useUnsubscribe(skillId: string) {
  const { mutate } = useSWRConfig();

  const unsubscribe = useCallback(async () => {
    const result = await unsubscribeSkill(skillId);
    // 刷新相关缓存
    mutate([CACHE_KEY_PREFIX, skillId]);
    mutate('skill-subscriptions');
    mutate('skill-list');
    return result;
  }, [skillId, mutate]);

  return { unsubscribe };
}

/**
 * 更新 Skill 设置
 */
export function useUpdateSettings(skillId: string) {
  const { mutate } = useSWRConfig();

  const update = useCallback(
    async (settings: Partial<UserSkillSettings & { push_enabled?: boolean }>) => {
      const result = await updateSkillSettings(skillId, settings);
      // 刷新相关缓存
      mutate([CACHE_KEY_PREFIX, skillId]);
      mutate('skill-subscriptions');
      return result;
    },
    [skillId, mutate]
  );

  return { update };
}

/**
 * 标记首次使用
 */
export function useMarkFirstUse(skillId: string) {
  const { mutate } = useSWRConfig();

  const mark = useCallback(async () => {
    const result = await markFirstUse(skillId);
    // 刷新相关缓存
    mutate([CACHE_KEY_PREFIX, skillId]);
    return result;
  }, [skillId, mutate]);

  return { mark };
}

