     1→/**
     2→ * SkillIntroCard API Client
     3→ */
     4→
     5→import { apiClient } from '@/lib/api';
     6→import type { SkillIntroData, UserSubscription, UserSkillSettings } from './types';
     7→
     8→const API_BASE = '/skills';
     9→
    10→/**
    11→ * 获取 Skill 介绍数据
    12→ */
    13→export async function fetchSkillIntro(skillId: string): Promise<SkillIntroData> {
    14→  const response = await apiClient.get<SkillIntroData>(`${API_BASE}/${skillId}/intro`);
    15→  return response.data;
    16→}
    17→
    18→/**
    19→ * 订阅 Skill
    20→ */
    21→export async function subscribeSkill(
    22→  skillId: string,
    23→  pushEnabled: boolean = true
    24→): Promise<{ success: boolean; subscription: UserSubscription; message: string }> {
    25→  const response = await apiClient.post<{
    26→    success: boolean;
    27→    subscription: UserSubscription;
