/**
 * SkillIntroCard API Client
 */

import { apiClient } from '@/lib/api';
import type { SkillIntroData, UserSubscription, UserSkillSettings } from './types';

const API_BASE = '/skills';

/**
 * 获取 Skill 介绍数据
 */
export async function fetchSkillIntro(skillId: string): Promise<SkillIntroData> {
  const response = await apiClient.get<SkillIntroData>(`${API_BASE}/${skillId}/intro`);
  return response.data;
}

/**
 * 订阅 Skill
 */
export async function subscribeSkill(
  skillId: string,
  pushEnabled: boolean = true
): Promise<{ success: boolean; subscription: UserSubscription; message: string }> {
  const response = await apiClient.post<{
    success: boolean;
    subscription: UserSubscription;
    message: string;
  }>(`${API_BASE}/${skillId}/subscribe`, { push_enabled: pushEnabled });
  return response.data;
}

/**
 * 取消订阅 Skill
 */
export async function unsubscribeSkill(
  skillId: string
): Promise<{ success: boolean; subscription: UserSubscription; message: string }> {
  const response = await apiClient.post<{
    success: boolean;
    subscription: UserSubscription;
    message: string;
  }>(`${API_BASE}/${skillId}/unsubscribe`);
  return response.data;
}

/**
 * 更新 Skill 设置
 */
export async function updateSkillSettings(
  skillId: string,
  settings: Partial<UserSkillSettings & { push_enabled?: boolean }>
): Promise<{ success: boolean; settings: UserSkillSettings; subscription: UserSubscription }> {
  const response = await apiClient.patch<{
    success: boolean;
    settings: UserSkillSettings;
    subscription: UserSubscription;
  }>(`${API_BASE}/${skillId}/settings`, settings);
  return response.data;
}

/**
 * 标记首次使用
 */
export async function markFirstUse(
  skillId: string
): Promise<{ success: boolean; is_first_use: boolean; first_used_at: string }> {
  const response = await apiClient.post<{
    success: boolean;
    is_first_use: boolean;
    first_used_at: string;
  }>(`${API_BASE}/${skillId}/first-use`);
  return response.data;
}

