'use client'

/**
 * Settings Page - v6.0 统一布局版
 *
 * v6.0 变更:
 * - 使用与 Chat 页相同的 NavBar 组件，保持左侧栏一致性
 * - 移除 PCLayout，使用自定义布局
 * - 修复退出登录功能
 *
 * PC端布局: NavBar + 设置菜单 + 设置内容
 * 移动端布局: 单栏设置列表
 */

import { useState, useEffect, useCallback, memo, useMemo, useId, Suspense } from 'react'
import { useRouter, useSearchParams } from 'next/navigation'
import { NavBar } from '@/components/layout/NavBar'
import { VibeGlyph } from '@/components/core'
import { BirthDateInputs } from '@/components/ui/Input'
import { getLocalStorageJSON, getAccessToken, setLocalStorageJSON, invalidateStorageCache } from '@/utils/storage'
import { AccountDeletionSection } from '@/components/settings/AccountDeletionSection'
import { SkillCard } from '@/components/skill'
import { useSkillSubscription, useSkills } from '@/hooks/useSkillSubscription'
import { useAuth } from '@/providers/AuthProvider'
import { cn } from '@/lib/utils'

// Use Next.js API routes for server communication to avoid exposing backend URL
const API_BASE = '/api'

const SETTINGS_MENU = [
  { id: 'profile', icon: '👤', label: '个人资料' },
  { id: 'skills', icon: '🎯', label: 'Skill 管理' },
  { id: 'notifications', icon: '🔔', label: '通知设置' },
  { id: 'membership', icon: '💎', label: '会员管理' },
  { id: 'preferences', icon: '⚙️', label: '偏好设置' },
  { id: 'account', icon: '⚠️', label: '账户管理' },
  { id: 'help', icon: '❓', label: '帮助与反馈' },
  { id: 'about', icon: '📄', label: '关于' },
]

interface UserProfile {
  nickname: string
  email: string
  avatar: string
  birthDate: string
  birthTime: string
  timezone: string
}

// ═══════════════════════════════════════════════════════════════════════════
// Skill 管理区块
// ═══════════════════════════════════════════════════════════════════════════

function SkillSettingsSection({ router }: { router: ReturnType<typeof useRouter> }) {
  const { skills } = useSkills()
  const { subscriptions, togglePush, unsubscribe, userStatuses } = useSkillSubscription()

  // 分组
  const { subscribed, available } = useMemo(() => {
    const subscribedIds = new Set(
      subscriptions.filter(s => s.status === 'subscribed').map(s => s.skill_id)
    )
    return {
      subscribed: skills.filter(s => subscribedIds.has(s.id)),
      available: skills.filter(s => !subscribedIds.has(s.id) && s.category !== 'core'),
    }
  }, [skills, subscriptions])

  const handleTogglePush = async (skillId: string, enabled: boolean) => {
    try {
      await togglePush(skillId, enabled)
    } catch (err) {
      console.error('Toggle push failed:', err)
    }
  }

  const handleUnsubscribe = async (skillId: string) => {
    try {
      await unsubscribe(skillId)
    } catch (err) {
      console.error('Unsubscribe failed:', err)
    }
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="font-serif text-xl text-text-primary">Skill 管理</h2>
        <button
          onClick={() => router.push('/skills')}
          className="text-sm text-accent hover:underline focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent focus-visible:ring-offset-2 rounded"
          aria-label="探索更多 Skill"
        >
          探索更多 →
        </button>
      </div>

      {/* 已订阅 */}
      <div>
        <h3 className="text-sm font-medium text-text-secondary mb-3">
          已订阅 ({subscribed.length})
        </h3>
        <div className="space-y-2">
          {subscribed.map((skill) => {
            const sub = subscriptions.find(s => s.skill_id === skill.id)
            const isCore = skill.category === 'core'
            return (
              <div key={skill.id} className="flex items-center gap-3 p-3 rounded-lg bg-[var(--bg-card-alt)]">
                <div
                  role="button"
                  tabIndex={0}
                  onClick={() => router.push(`/chat?skill=${skill.id}`)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' || e.key === ' ') {
                      e.preventDefault()
                      router.push(`/chat?skill=${skill.id}`)
                    }
                  }}
                  className="flex items-center gap-3 flex-1 min-w-0 cursor-pointer hover:opacity-80 transition-opacity"
                  aria-label={`使用 ${skill.name}`}
                >
                  <div
                    className="w-10 h-10 rounded-xl flex items-center justify-center text-xl"
                    style={{ backgroundColor: `${skill.color}20` }}
                  >
                    {skill.icon}
                  </div>
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2">
                      <span className="font-medium text-text-primary">{skill.name}</span>
                      {isCore && (
                        <span className="px-1.5 py-0.5 text-xs rounded bg-accent/10 text-accent">
                          始终激活
                        </span>
                      )}
                    </div>
                    <p className="text-xs text-text-secondary truncate">
                      {skill.features?.slice(0, 2).map(f => f.name).join(' · ')}
                    </p>
                  </div>
                </div>
                {!isCore && (
                  <div className="flex items-center gap-3">
                    <div className="flex items-center gap-2">
                      <label
                        htmlFor={`push-${skill.id}`}
                        className="text-xs text-text-tertiary cursor-pointer"
                      >
                        推送
                      </label>
                      <div className="relative inline-flex items-center">
                        <input
                          id={`push-${skill.id}`}
                          type="checkbox"
                          checked={sub?.push_enabled ?? false}
                          onChange={(e) => handleTogglePush(skill.id, e.target.checked)}
                          className="sr-only peer"
                          aria-describedby={`push-desc-${skill.id}`}
                        />
                        <div className="w-9 h-5 bg-[var(--bg-secondary)] peer-focus-visible:ring-2 peer-focus-visible:ring-accent/50 peer-focus-visible:ring-offset-2 rounded-full peer peer-checked:after:translate-x-full after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-4 after:w-4 after:transition-transform peer-checked:bg-accent cursor-pointer" />
                        <span id={`push-desc-${skill.id}`} className="sr-only">
                          {sub?.push_enabled ? '关闭' : '开启'} {skill.name} 推送通知
                        </span>
                      </div>
                    </div>
                    <button
                      onClick={() => handleUnsubscribe(skill.id)}
                      className="text-xs text-error hover:underline focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-error focus-visible:ring-offset-2 rounded"
                      aria-label={`取消订阅 ${skill.name}`}
                    >
                      取消
                    </button>
                  </div>
                )}
              </div>
            )
          })}
        </div>
      </div>

      {/* 可订阅 */}
      {available.length > 0 && (
        <div>
          <h3 className="text-sm font-medium text-text-secondary mb-3">
            可订阅 ({available.length})
          </h3>
          <div className="space-y-2">
            {available.slice(0, 3).map((skill) => (
              <div
                key={skill.id}
                role="button"
                tabIndex={0}
                onClick={() => router.push(`/skills/${skill.id}`)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' || e.key === ' ') {
                    e.preventDefault()
                    router.push(`/skills/${skill.id}`)
                  }
                }}
                className="flex items-center gap-3 p-3 rounded-lg bg-[var(--bg-card-alt)] cursor-pointer hover:bg-[var(--bg-secondary)] transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-accent focus-visible:ring-offset-2"
                aria-label={`查看 ${skill.name} 详情`}
              >
                <div
                  className="w-10 h-10 rounded-xl flex items-center justify-center text-xl"
                  style={{ backgroundColor: `${skill.color}20` }}
                  aria-hidden="true"
                >
                  {skill.icon}
                </div>
                <div className="flex-1 min-w-0">
                  <span className="font-medium text-text-primary">{skill.name}</span>
                  <p className="text-xs text-text-secondary truncate">
                    {skill.showcase?.tagline || skill.description}
                  </p>
                </div>
                <span className="px-3 py-1 text-xs rounded-full bg-accent text-white" aria-hidden="true">
                  订阅
                </span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  )
}

