/* Claude.ai 风格：温暖浅色 + 现代无衬线 */
@import url("https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600&family=Noto+Sans+SC:wght@400;500;600&display=swap");

:root{
  /* Claude.ai 温暖配色 */
  --bg:#FAF9F7;--surface:#fff;--surface-2:#F5F4F2;--ink:#1a1a1a;--muted:#8B8B8B;
  --line:rgba(0,0,0,.08);--shadow:rgba(0,0,0,.08);
  --brand:#D97757;--brand-solid:#D97757;--brand-hover:#C4654A;
  --ok:#16A34A;--warn:#EA580C;--info:#2563EB;

  /* 强调色 - Claude 橙红 */
  --accent:#D97757;

  /* 光影层级 - 柔和 */
  --shadow-sm:0 1px 2px rgba(0,0,0,.05);
  --shadow-md:0 4px 12px rgba(0,0,0,.08);
  --shadow-lg:0 8px 24px rgba(0,0,0,.12);
  --shadow-input:0 1px 3px rgba(0,0,0,.08),0 4px 12px rgba(0,0,0,.05);

  /* 尺寸 - 圆润 */
  --header-h:56px;--page-pad:24px;--radius-xl:24px;--radius-lg:16px;--radius-md:12px;--radius-sm:8px;

  /* 现代无衬线字体 */
  --font-display:"Inter","Noto Sans SC",system-ui,-apple-system,sans-serif;
  --font-body:"Inter","Noto Sans SC",system-ui,-apple-system,sans-serif
}
*{box-sizing:border-box}html,body{height:100%}
body{
  margin:0;
  background:var(--bg);
  color:var(--ink);
  font:14px/1.6 var(--font-body);
  -webkit-font-smoothing:antialiased;-moz-osx-font-smoothing:grayscale
}
.ui-page{min-height:100%;display:flex;flex-direction:column}
.ui-header{
  display:flex;justify-content:space-between;align-items:center;padding:12px 24px;
  border-bottom:1px solid var(--line);
  min-height:var(--header-h);position:sticky;top:0;z-index:20;
  background:var(--bg)
}
.ui-brand{
  font:600 16px/1 var(--font-display);
  color:var(--ink)
}
.ui-topbar{display:flex;gap:12px;align-items:center;flex-wrap:wrap}
.ui-topbar__label{color:var(--muted);font-size:12px}
.ui-select{
  padding:8px 12px;border-radius:var(--radius-sm);border:1px solid var(--line);
  background:var(--surface);color:var(--ink);font:inherit;font-size:13px
}
.ui-select:focus{outline:none;border-color:var(--brand)}
.ui-btn{
  padding:8px 16px;border-radius:var(--radius-sm);border:1px solid var(--line);background:var(--surface);
  cursor:pointer;color:var(--ink);font:inherit;font-size:13px;font-weight:500;
  transition:all .15s ease
}
.ui-btn:hover{background:var(--surface-2);border-color:var(--muted)}
.ui-btn-ghost{background:transparent;border-color:transparent}
.ui-btn-ghost:hover{background:var(--surface-2);border-color:transparent}
.ui-btn-primary{background:var(--brand);border:none;color:#fff}
.ui-btn-primary:hover{background:var(--brand-hover)}

/* PC端布局 */
.ui-layout{
  display:grid;grid-template-columns:minmax(0,1fr) minmax(340px,420px);gap:0;
  max-width:1400px;margin:0 auto;padding:0;align-items:stretch;flex:1
}
.ui-col{
  background:var(--bg);min-height:0;border:none
}
.ui-chat{display:flex;flex-direction:column;min-height:0;position:relative}
.ui-workbench{max-height:calc(100vh - var(--header-h));overflow:hidden;display:flex;flex-direction:column;border-left:1px solid var(--line);background:var(--surface)}
.ui-thread{flex:1;padding:24px 32px 180px;overflow:auto;max-width:720px;width:100%;margin:0 auto}
.msg{display:flex;gap:12px;margin:20px 0;align-items:flex-start;animation:msgIn .2s ease both}
.msg-user{justify-content:flex-end}
.msg-user .msg__bubble{background:var(--surface-2);border-radius:var(--radius-lg);padding:12px 16px;max-width:85%;color:var(--ink)}
.msg-bot .msg__icon{width:28px;height:28px;border-radius:50%;background:var(--brand);display:flex;align-items:center;justify-content:center;font-size:12px;flex:0 0 auto;color:#fff}
.msg-bot .msg__content{flex:1;max-width:100%}
.msg__bubble,.msg__content{white-space:pre-wrap;word-break:break-word;overflow-wrap:anywhere;line-height:1.6}
.msg.card .msg__content{background:var(--surface);border-radius:var(--radius-lg);padding:14px 18px;box-shadow:var(--shadow-sm)}
.msg.progress .msg__content{background:var(--surface);border-radius:var(--radius-lg);border-left:3px solid var(--brand);padding:14px 18px}

/* ========== Result Card ========== */
.result-card{animation:resultCardIn .2s ease both}
@keyframes resultCardIn{0%{opacity:0;transform:translateY(8px)}100%{opacity:1;transform:translateY(0)}}
.result-card .result-card__icon{border-radius:50%;font-size:10px;background:var(--brand);color:#fff}
.result-card .result-card__body{
  background:var(--surface);border-radius:var(--radius-lg);overflow:hidden;max-width:100%;box-shadow:var(--shadow-sm)
}
.result-card__header{
  display:flex;justify-content:space-between;align-items:center;
  padding:12px 16px;border-bottom:1px solid var(--line)
}
.result-card__title{font-weight:500;font-size:13px;color:var(--ink)}
.result-card__actions{display:flex;gap:6px}
.result-card__actions .ui-btn{padding:4px 10px;font-size:12px;min-width:auto}
.result-card__content{padding:14px 16px;max-height:50vh;overflow:auto}
/* Progress state */
.result-card--progress .result-card__body{border-left:3px solid var(--warn)}
/* Error state */
.result-card--error .result-card__body{border-left:3px solid #EF4444}
.result-card--error .result-card__header{background:#FEF2F2}
/* Result state */
.result-card--result .result-card__body{border-left:3px solid var(--ok)}
/* Dismiss animation */
.result-card--dismissed{animation:resultCardOut .2s ease forwards}
@keyframes resultCardOut{to{opacity:0;transform:translateX(-10px);height:0;margin:0;padding:0}}
.msg a{color:var(--brand);text-decoration:none}
.msg a:hover{text-decoration:underline}
.msg blockquote{margin:12px 0;padding:12px 16px;border-left:3px solid var(--brand);background:var(--surface-2);border-radius:var(--radius-sm);color:var(--muted)}
.msg .md-code{background:var(--surface-2);color:var(--ink);padding:14px 16px;overflow:auto;border-radius:var(--radius-sm)}
.msg .md-code code{font-family:ui-monospace,SFMono-Regular,Menlo,monospace;font-size:13px;white-space:pre}
.msg .meta{color:var(--muted);font-size:12px;margin-top:8px}
.msg .actions{margin-top:10px;display:flex;gap:8px;flex-wrap:wrap}
/* 骨架屏 */
.msg.skel .msg__content{
  background:linear-gradient(90deg,var(--surface-2) 25%,var(--surface) 37%,var(--surface-2) 63%);
  background-size:400% 100%;animation:shine 1.4s ease infinite;min-height:48px;border-radius:var(--radius-lg)
}
@keyframes shine{0%{background-position:100% 0}100%{background-position:0 0}}
@keyframes spin{to{transform:rotate(360deg)}}
@keyframes msgIn{from{opacity:0;transform:translateY(6px)}to{opacity:1;transform:translateY(0)}}
/* 输入区 - Claude 风格悬浮卡片 */
.ui-input{
  display:grid;grid-template-columns:auto 1fr auto;gap:12px;align-items:center;padding:14px 18px;
  background:var(--surface);border-radius:var(--radius-xl);border:1px solid var(--line);
  box-shadow:var(--shadow-input);
  position:absolute;bottom:24px;left:32px;right:32px;max-width:720px;margin:0 auto;
  transition:box-shadow .2s ease,border-color .2s ease
}
.ui-input:focus-within{border-color:var(--muted);box-shadow:var(--shadow-md)}
.ui-input textarea{width:100%;resize:none;border:none;background:transparent;outline:none;font:inherit;color:var(--ink);min-height:24px;max-height:160px}
.ui-input textarea::placeholder{color:var(--muted)}
.ui-icon-btn{width:32px;height:32px;border-radius:var(--radius-sm);border:1px solid var(--line);background:var(--surface);cursor:pointer;color:var(--muted);font-size:16px;display:flex;align-items:center;justify-content:center;transition:all .15s ease}
.ui-icon-btn:hover{background:var(--surface-2);color:var(--ink);border-color:var(--muted)}
.ui-send-btn{position:relative;width:36px;height:36px;border-radius:50%;border:none;background:var(--brand);color:#fff;cursor:pointer;font-size:16px;display:flex;align-items:center;justify-content:center;transition:all .15s ease;overflow:hidden}
.ui-send-btn:hover{background:var(--brand-hover);transform:scale(1.05)}
/* 发送按钮加载态 */
.ui-send-btn.loading{pointer-events:none;background:var(--surface-2)}
.ui-send-btn.loading::after{
  content:"";position:absolute;top:50%;left:50%;margin:-8px 0 0 -8px;
  width:16px;height:16px;border:2px solid var(--line);border-top-color:var(--brand);
  border-radius:50%;animation:spin .7s linear infinite
}
.ui-context-pill{
  display:inline-flex;gap:10px;background:var(--surface);border:1px solid var(--line);padding:8px 14px;border-radius:999px;color:var(--muted);
  max-width:100%;width:fit-content;overflow:hidden;white-space:nowrap;text-overflow:ellipsis;margin:0 32px 100px;
  font-size:12px;box-shadow:var(--shadow-sm)
}
.ui-pill-dirty{color:var(--warn)}
.ui-workbench{
  padding:20px;min-height:0;display:flex;flex-direction:column;
  background:var(--surface)
}
.ui-tabs{display:flex;gap:4px;margin-bottom:16px;flex-wrap:wrap;align-items:center;padding:4px;background:var(--surface-2);border-radius:var(--radius-md)}
.ui-tab{padding:8px 16px;border:none;border-radius:var(--radius-sm);background:transparent;cursor:pointer;color:var(--muted);font:inherit;font-size:13px;font-weight:500;transition:all .15s ease}
.ui-tab:hover{color:var(--ink)}
.ui-tab--active{color:var(--ink);background:var(--surface);box-shadow:var(--shadow-sm)}
.ui-panels{position:relative;min-height:0;flex:1;overflow:hidden}
.ui-panel{padding:4px 0;height:100%;overflow:auto}
.ui-form label{display:flex;flex-direction:column;gap:6px;font-size:12px;color:var(--muted);font-weight:500}
.ui-form input,.ui-form select,.ui-form textarea{padding:10px 14px;border:1px solid var(--line);border-radius:var(--radius-sm);background:var(--bg);color:var(--ink);font:inherit;transition:border-color .15s ease}
.ui-form input:focus,.ui-form select:focus,.ui-form textarea:focus{border-color:var(--brand);outline:none}
.ui-grid-2{display:grid;grid-template-columns:1fr 1fr;gap:12px}
.ui-actions-row{margin-top:12px}
.ui-hint{margin-top:8px;color:var(--muted);font-size:12px}
.ui-reader{display:grid;grid-template-rows:auto 1fr;height:60vh}
.ui-reader__bar{display:flex;justify-content:space-between;align-items:center;border-bottom:1px solid var(--line);padding:8px 12px}
.ui-reader__content{padding:16px;overflow:auto;font-family:var(--font-body)}

/* Bento cards */
.ui-bento{display:grid;gap:14px}

/* 功能分组 */
.ui-bento-group{margin-bottom:12px}
.ui-bento-group__title{
  font-size:11px;font-weight:600;color:var(--muted);
  text-transform:uppercase;letter-spacing:.05em;
  margin-bottom:10px;padding-left:2px
}
.ui-bento-group__cards{display:grid;gap:10px}
/* 高优先级分组 */
.ui-bento-group[data-priority="high"] .ui-card{border-left:3px solid var(--brand)}
.ui-card{
  position:relative;
  border:1px solid var(--line);border-radius:var(--radius-md);
  background:var(--bg);
  overflow:hidden;
  transition:all .15s ease;
  box-shadow:var(--shadow-sm)
}
.ui-card:hover{box-shadow:var(--shadow-md);border-color:var(--muted)}
.ui-card--flat{margin-bottom:10px}
.ui-card__hd{display:flex;justify-content:space-between;align-items:center;padding:12px 14px;border-bottom:1px solid var(--line);gap:10px}
.ui-card__hd-left{display:flex;align-items:center;gap:10px}
.ui-card__title{font-weight:500;font-size:13px}
.ui-card__status{
  display:inline-flex;align-items:center;gap:4px;padding:3px 10px;
  font-size:11px;font-weight:500;border-radius:999px;
  background:var(--surface-2);color:var(--muted)
}
.ui-card__status--active{background:rgba(22,163,74,.1);color:var(--ok)}
.ui-card__status--pending{background:rgba(234,88,12,.1);color:var(--warn)}
.ui-card__hd-right{display:flex;align-items:center;gap:8px}
.ui-card__btn{padding:6px 12px;font-size:12px}
/* 折叠按钮 */
.ui-card__toggle{
  width:26px;height:26px;display:flex;align-items:center;justify-content:center;
  cursor:pointer;border:none;border-radius:var(--radius-sm);background:transparent;color:var(--muted);
  transition:all .15s ease
}
.ui-card__toggle:hover{background:var(--surface-2);color:var(--ink)}
.ui-card__toggle svg{width:14px;height:14px}
.ui-card.collapsed .ui-card__toggle{transform:rotate(-90deg)}
/* 卡片内容折叠 */
.ui-card__bd{padding:12px 14px;color:var(--ink);transition:max-height .25s ease,padding .25s ease,opacity .2s ease;overflow:hidden}
.ui-card.collapsed .ui-card__bd{max-height:0;padding:0 14px;opacity:0}
.ui-task{display:flex;justify-content:space-between;gap:10px;align-items:flex-start;padding:10px 0;border-bottom:1px solid var(--line)}
.ui-task:last-child{border-bottom:none}
.ui-task__meta{color:var(--muted);font-size:12px}

/* 进度条 - Claude.ai 风格 */
.ui-progress{height:3px;background:var(--surface-2);overflow:hidden;margin:10px 0;border-radius:999px}
.ui-progress__bar{height:100%;background:var(--brand);transition:width .3s ease;border-radius:999px}
.ui-progress--indeterminate .ui-progress__bar{
  width:30%;animation:progressIndeterminate 1.2s ease-in-out infinite
}
@keyframes progressIndeterminate{
  0%{transform:translateX(-100%)}
  100%{transform:translateX(400%)}
}

/* 步骤指示器 - Claude.ai 圆润风格 */
.ui-steps{display:flex;gap:8px;margin:12px 0;justify-content:center}
.ui-step{
  width:28px;height:28px;border:2px solid var(--line);border-radius:50%;
  display:flex;align-items:center;justify-content:center;font-size:11px;font-weight:600;
  transition:all .2s ease;background:var(--surface);color:var(--muted)
}
.ui-step.current{border-color:var(--brand);background:var(--brand);color:#fff}
.ui-step.completed{border-color:var(--ok);background:var(--ok);color:#fff}
.ui-step__dot{
  width:24px;height:24px;border:2px solid var(--line);border-radius:50%;
  display:flex;align-items:center;justify-content:center;font-size:10px;font-weight:600;
  transition:all .2s ease;background:var(--surface)
}
.ui-step.active .ui-step__dot{border-color:var(--brand);background:var(--brand);color:#fff}
.ui-step.completed .ui-step__dot{border-color:var(--ok);background:var(--ok);color:#fff}
.ui-step__label{font-size:10px;color:var(--muted);text-align:center;margin-top:4px}

/* 进度包装器 */
.ui-progress-wrapper{padding:8px 0}

/* Session drawer */
.ui-drawer{position:fixed;inset:0;display:none;z-index:40}
.ui-drawer.open{display:block}
.ui-drawer__overlay{position:absolute;inset:0;background:rgba(0,0,0,.8)}
.ui-drawer__panel{position:absolute;top:0;bottom:0;left:0;width:min(380px, 90vw);background:var(--bg);border-right:1px solid var(--line);padding:16px;display:grid;grid-template-rows:auto auto 1fr;gap:12px}
.ui-drawer__hd{display:flex;gap:10px;align-items:center;justify-content:space-between}
.ui-drawer__title{font-weight:500;font-size:12px;text-transform:uppercase;letter-spacing:.1em}
.ui-drawer__search input{width:100%;padding:8px 12px;border:1px solid var(--line);background:transparent;color:var(--ink);font:inherit}
.ui-drawer__search input::placeholder{color:var(--muted)}
.ui-drawer__search input:focus{border-color:var(--ink);outline:none}
.ui-drawer__list{overflow:auto;min-height:0;display:grid;gap:6px}
.ui-session-item{padding:10px 12px;cursor:pointer;border:1px solid var(--line);transition:all .15s ease;background:transparent}
