  }
  .ui-panel{height:auto;max-height:none}
  .ui-chat{min-height:auto}
  .ui-thread{padding:16px 16px 100px;max-height:none}

  /* 移动端输入框 - Claude.ai 风格悬浮 */
  .ui-input{
    position:fixed;bottom:16px;left:16px;right:16px;z-index:25;
    background:var(--surface);border:1px solid var(--line);
    border-radius:var(--radius-xl);
    box-shadow:var(--shadow-input);
    padding:12px 16px;
    max-width:none
  }

  /* Mobile: Bento bottom sheet - Claude.ai 风格 */
  .ui-workbench{
    position:fixed;left:0;right:0;bottom:0;
    height:auto;max-height:70vh;max-height:70dvh;
    transform:translateY(100%);
    transition:transform .3s cubic-bezier(0.4, 0, 0.2, 1);
    z-index:30;
    will-change:transform;
    background:var(--surface);
    border-top-left-radius:var(--radius-xl);
    border-top-right-radius:var(--radius-xl);
    box-shadow:0 -4px 24px rgba(0,0,0,.15);
    padding:20px 16px 16px
  }

  /* 拖拽指示器 - Claude.ai 风格 */
  .ui-workbench::before{
    content:"";position:absolute;top:8px;left:50%;transform:translateX(-50%);
    width:40px;height:4px;background:var(--line);
    border-radius:999px;z-index:1
  }

  /* 展开态 */
  body.show-bento .ui-workbench{transform:translateY(0)}

  /* 半展开态 */
  body.show-bento-peek .ui-workbench{transform:translateY(calc(100% - 160px))}

  /* 遮罩层 - Claude.ai 风格 */
  body.show-bento::before{
    content:"";position:fixed;inset:0;background:rgba(0,0,0,.4);z-index:29;
    animation:fadeIn .3s ease;backdrop-filter:blur(2px)
  }
  body.show-bento-peek::before{
    content:"";position:fixed;inset:0;background:rgba(0,0,0,.25);z-index:29;
    animation:fadeIn .3s ease
  }
  @keyframes fadeIn{from{opacity:0}to{opacity:1}}

  /* 移动端触摸反馈 - Claude.ai 风格 */
  .ui-card:hover{border-color:var(--line)}
  .ui-card:active{background:var(--surface-2);transform:scale(0.98);transition:all .1s ease}

  /* 移动端 Tabs - Claude.ai 风格 */
  .ui-tabs{gap:4px;padding:4px;margin-bottom:12px;background:var(--surface-2);border-radius:var(--radius-md)}
  .ui-tab{padding:8px 14px;font-size:11px;border-radius:var(--radius-sm)}

  /* 移动端 Bento */
  .ui-bento{gap:10px}
  .ui-bento-group{margin-bottom:8px}
  .ui-bento-group__title{font-size:10px;margin-bottom:8px;font-weight:600}
  .ui-bento-group__cards{gap:8px}

  /* 移动端卡片 - Claude.ai 风格 */
  .ui-card__hd{padding:10px 12px}
  .ui-card__bd{padding:10px 12px}
  .ui-card__title{font-size:12px}

  /* 关闭按钮 - Claude.ai 风格 */
  .ui-workbench-close{
    position:absolute;top:8px;right:12px;
    width:28px;height:28px;
    background:var(--surface-2);border:none;border-radius:50%;
    color:var(--muted);cursor:pointer;
    display:flex;align-items:center;justify-content:center;
    font-size:14px;z-index:2;transition:all .2s ease
  }
  .ui-workbench-close:hover{background:var(--line);color:var(--ink)}
  .ui-workbench-close:active{transform:scale(0.9)}
}

