"use client"

import { useMemo } from "react"
import { cn } from "@/lib/utils"

const AURA_STYLES: Record<
  string,
  { gradient: string; orb: { from: string; to: string } }
> = {
  calm: {
    gradient: "linear-gradient(135deg, hsl(var(--element-water)) 0%, hsl(var(--element-water-dark)) 100%)",
    orb: { from: "hsl(var(--element-water) / 0.35)", to: "hsl(var(--element-water-dark) / 0.15)" },
  },
  alert: {
    gradient: "linear-gradient(135deg, hsl(var(--element-fire)) 0%, hsl(var(--element-fire-dark)) 100%)",
    orb: { from: "hsl(var(--element-fire) / 0.35)", to: "hsl(var(--element-fire-dark) / 0.15)" },
  },
  happy: {
    gradient: "linear-gradient(135deg, hsl(var(--element-earth)) 0%, hsl(var(--element-earth-dark)) 100%)",
    orb: { from: "hsl(var(--element-earth) / 0.35)", to: "hsl(var(--element-earth-dark) / 0.15)" },
  },
  anxious: {
    gradient: "linear-gradient(135deg, hsl(var(--orb-insight-from)) 0%, hsl(var(--orb-insight-to)) 100%)",
    orb: { from: "hsl(var(--orb-insight-from) / 0.4)", to: "hsl(var(--orb-insight-to) / 0.2)" },
  },
  flow: {
    gradient: "linear-gradient(135deg, hsl(var(--element-wood)) 0%, hsl(var(--element-wood-dark)) 100%)",
    orb: { from: "hsl(var(--element-wood) / 0.35)", to: "hsl(var(--element-wood-dark) / 0.15)" },
  },
}

export function DynamicAura({ state }: { state: string }) {
  const aura = useMemo(() => AURA_STYLES[state] || AURA_STYLES.calm, [state])

  return (
    <div className="fixed inset-0 -z-10 transition-[background] duration-[2000ms] ease-out" style={{ background: aura.gradient }}>
      <div className="absolute inset-0 pointer-events-none">
        <div
          className={cn("absolute -top-16 -left-12 h-72 w-72 rounded-full blur-[110px] opacity-60")}
          style={{
            background: `radial-gradient(circle, ${aura.orb.from} 0%, ${aura.orb.to} 70%)`,
            animation: "float 10s ease-in-out infinite",
          }}
        />
        <div
          className={cn("absolute top-32 right-10 h-80 w-80 rounded-full blur-[120px] opacity-50")}
          style={{
            background: `radial-gradient(circle, ${aura.orb.from} 0%, ${aura.orb.to} 70%)`,
            animation: "float 12s ease-in-out infinite",
          }}
        />
        <div
          className={cn("absolute bottom-10 left-1/3 h-72 w-72 rounded-full blur-[130px] opacity-40")}
          style={{
            background: `radial-gradient(circle, ${aura.orb.from} 0%, ${aura.orb.to} 70%)`,
            animation: "float 14s ease-in-out infinite",
          }}
        />
      </div>
    </div>
  )
}

"use client"

import { useMemo } from "react"
import { cn } from "@/lib/utils"
import type { StreamEntry } from "@/stores/stream-store"

function formatTime(value: string) {
  try {
    return new Date(value).toLocaleTimeString("zh-CN", { hour: "2-digit", minute: "2-digit" })
  } catch {
    return ""
  }
}

export function StreamCard({ entry }: { entry: StreamEntry }) {
  const time = useMemo(() => formatTime(entry.created_at), [entry.created_at])
  const energyDelta = entry.energy_delta ?? 0
  const energyLabel = energyDelta > 0 ? `+${energyDelta}` : `${energyDelta}`

  return (
    <div className={cn("rounded-xl border border-border bg-card shadow-sm px-4 py-3")}>
      <div className="flex items-center justify-between text-xs text-muted-foreground">
        <span>👤 You</span>
        <span>{time}</span>
      </div>
      <p className="mt-2 text-sm leading-relaxed text-foreground">{entry.raw_content}</p>
      <div className="mt-3 flex flex-wrap items-center gap-2 text-xs text-muted-foreground">
        {(entry.tags || []).map((tag) => (
          <span key={tag} className="rounded-full bg-background-subtle px-2 py-0.5">
            {tag}
          </span>
        ))}
        <span className={cn("ml-auto font-medium", energyDelta >= 0 ? "text-success" : "text-destructive")}>
          ⚡ {energyLabel}
        </span>
      </div>
    </div>
  )
}

