    --background-subtle: 40 15% 96%;    /* #F9F7F4 - Light beige */
    --background-muted: 40 12% 94%;     /* #F5F3EF - Deeper beige */

    --foreground: 30 10% 22%;           /* #37352F - Notion dark text */
    --foreground-muted: 30 5% 55%;      /* #9B9A97 - Secondary text */
    --foreground-subtle: 30 5% 72%;     /* #BDB9B4 - Placeholder */

    --card: 0 0% 100%;                  /* #FFFFFF - Pure white */
    --card-hover: 40 10% 99%;           /* #FEFDFB - Subtle warm */

    --border: 40 10% 90%;               /* #E8E5E0 - Soft border */
    --border-hover: 40 10% 85%;

    --accent: 35 60% 52%;               /* #C9A05A - 金麦色 Golden Wheat */
    --accent-muted: 35 40% 92%;         /* #E8DCC8 - Soft accent bg */

    --muted: 40 10% 94%;
    --muted-foreground: 30 5% 55%;

    --success: 145 40% 45%;             /* #4A9B6D - Soft green */
    --destructive: 0 65% 55%;           /* #D65A5A - Warm red */
    --warning: 36 100% 50%;             /* Mustard yellow */

    /* ─────────────────────────────────────────────────────────────────────────
       五行元素色 (Wuxing Colors)
       ───────────────────────────────────────────────────────────────────────── */
    --element-water: 210 70% 45%;       /* 水 - Blue */
    --element-water-dark: 210 70% 25%;
    --element-fire: 0 80% 55%;          /* 火 - Red */
    --element-fire-dark: 0 80% 35%;
    --element-earth: 40 70% 50%;        /* 土 - Gold */
    --element-earth-dark: 40 70% 30%;
    --element-wood: 120 50% 40%;        /* 木 - Green */
    --element-wood-dark: 120 50% 25%;
    --element-metal: 30 5% 85%;         /* 金 - Silver */
    --element-metal-dark: 30 5% 65%;

    /* ─────────────────────────────────────────────────────────────────────────
       Soul Orb 渐变色
       ───────────────────────────────────────────────────────────────────────── */
    --orb-default-from: 25 60% 75%;     /* Soul Peach */
    --orb-default-to: 200 40% 80%;      /* Mist Blue */
    --orb-insight-from: 270 60% 55%;    /* Mentis Purple */
    --orb-insight-to: 290 40% 75%;      /* Morning Haze */

    /* ─────────────────────────────────────────────────────────────────────────
       Aura 状态色
       ───────────────────────────────────────────────────────────────────────── */
    --aura-calm-from: 210 50% 20%;
    --aura-calm-to: 220 40% 35%;
    --aura-alert-from: 0 50% 20%;
    --aura-alert-to: 350 40% 35%;
    --aura-happy-from: 40 50% 20%;
    --aura-happy-to: 45 40% 35%;
    --aura-anxious-from: 270 50% 20%;
    --aura-anxious-to: 280 40% 35%;
    --aura-flow-from: 120 40% 18%;
    --aura-flow-to: 130 35% 32%;

    /* ─────────────────────────────────────────────────────────────────────────
       其他 Tokens
       ───────────────────────────────────────────────────────────────────────── */
    --radius: 0.625rem;
    --radius-lg: 0.875rem;
    --radius-xl: 1.25rem;
  }

  /* ═══════════════════════════════════════════════════════════════════════════
     Dark Mode
     ═══════════════════════════════════════════════════════════════════════════ */
  .dark {
    --background: 220 10% 8%;           /* #121315 - Deep night */
    --background-subtle: 220 10% 11%;
    --background-muted: 220 10% 14%;

    --foreground: 40 10% 88%;           /* #E2E0DC - Light cream */
    --foreground-muted: 40 5% 60%;
    --foreground-subtle: 40 5% 45%;

    --card: 220 10% 12%;
    --card-hover: 220 10% 15%;

    --border: 220 8% 20%;
    --border-hover: 220 8% 28%;

    --accent: 35 55% 55%;
    --accent-muted: 35 30% 25%;

    --muted: 220 10% 18%;
    --muted-foreground: 40 5% 60%;
  }
}

/* ════════════════════════════════════════════════════════════════════════════
   2. TYPOGRAPHY - Fonts
   ════════════════════════════════════════════════════════════════════════════ */

@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&family=Playfair+Display:ital,wght@0,400;0,500;0,600;0,700;1,400;1,500&family=Noto+Serif+SC:wght@400;500;600;700&display=swap');

html {
  font-size: 16px;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body {
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  background: hsl(var(--background));
  color: hsl(var(--foreground));
  line-height: 1.6;
  letter-spacing: -0.011em;
}

/* Serif Headings */
.font-serif {
  font-family: 'Playfair Display', 'Noto Serif SC', Georgia, serif;
}

.serif-heading {
  font-family: 'Playfair Display', 'Noto Serif SC', Georgia, serif;
  font-weight: 500;
  letter-spacing: -0.02em;
}

.hero-title {
  font-family: 'Playfair Display', 'Noto Serif SC', Georgia, serif;
  font-size: clamp(2rem, 5vw, 3.5rem);
  font-weight: 400;
  font-style: italic;
  letter-spacing: -0.025em;
  line-height: 1.15;
}

.hero-subtitle {
  font-size: clamp(0.875rem, 1.5vw, 1.125rem);
  color: hsl(var(--foreground-muted));
  letter-spacing: 0.02em;
}

/* ════════════════════════════════════════════════════════════════════════════
   3. GLASSMORPHISM & EFFECTS
   ════════════════════════════════════════════════════════════════════════════ */

@layer utilities {
  /* 玻璃态效果 */
  .glass {
    background: hsl(var(--background) / 0.7);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid hsl(var(--border) / 0.5);
  }

  .glass-subtle {
    background: hsl(var(--background) / 0.8);
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
  }

  .glass-card {
    background: hsl(var(--card) / 0.8);
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    box-shadow:
      0 4px 24px hsl(var(--foreground) / 0.06),
      inset 0 1px 0 hsl(0 0% 100% / 0.1);
  }

  .capsule-glass {
    background: hsl(var(--card) / 0.6);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid hsl(var(--border) / 0.4);
  }

  .hyper-glass {
    background: hsl(var(--card) / 0.7);
    backdrop-filter: blur(24px);
    -webkit-backdrop-filter: blur(24px);
    border: 1px solid hsl(var(--border) / 0.6);
  }

  /* Glow 效果 */
  .glow-accent {
    box-shadow: 0 0 20px hsl(var(--accent) / 0.3);
  }

  .glow-orb {
    box-shadow:
      0 0 60px hsl(var(--orb-insight-from) / 0.4),
      0 0 120px hsl(var(--orb-insight-to) / 0.2);
  }
}

/* ════════════════════════════════════════════════════════════════════════════
   4. ANIMATIONS
   ════════════════════════════════════════════════════════════════════════════ */

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

@keyframes breathe {
  0%, 100% { transform: scale(1); opacity: 0.8; }
  50% { transform: scale(1.05); opacity: 1; }
}

@keyframes pulse-glow {
  0%, 100% { opacity: 0.6; }
  50% { opacity: 1; }
}

@keyframes gradient-shift {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

@keyframes shimmer {
  0% { transform: translateX(-100%); }
  100% { transform: translateX(100%); }
}

@keyframes card-enter {
  from {
    opacity: 0;
    transform: translateY(16px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* ════════════════════════════════════════════════════════════════════════════
   5. SCROLLBAR
   ════════════════════════════════════════════════════════════════════════════ */

::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: hsl(var(--background-subtle));
}

::-webkit-scrollbar-thumb {
  background: hsl(var(--border));
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--foreground-muted));
}

/* ════════════════════════════════════════════════════════════════════════════
   6. SELECTION
   ════════════════════════════════════════════════════════════════════════════ */

::selection {
  background: hsl(var(--accent) / 0.2);
  color: hsl(var(--foreground));
}

/* ════════════════════════════════════════════════════════════════════════════
   7. FOCUS STATES
   ════════════════════════════════════════════════════════════════════════════ */

@layer base {
  *:focus-visible {
    outline: 2px solid hsl(var(--accent));
    outline-offset: 2px;
  }
}

/* ════════════════════════════════════════════════════════════════════════════
   8. UTILITY CLASSES
   ════════════════════════════════════════════════════════════════════════════ */

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }

  .animate-in {
    animation: card-enter 0.4s ease-out forwards;
  }

  /* Gradient Text */
  .text-gradient {
    background: linear-gradient(135deg, hsl(var(--accent)) 0%, hsl(var(--orb-insight-from)) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Energy indicator colors */
  .energy-positive {
    color: hsl(var(--success));
  }

  .energy-negative {
    color: hsl(var(--destructive));
  }

  /* Aura state backgrounds */
  .aura-calm {
    background: linear-gradient(135deg, hsl(var(--aura-calm-from)) 0%, hsl(var(--aura-calm-to)) 100%);
  }

  .aura-alert {
    background: linear-gradient(135deg, hsl(var(--aura-alert-from)) 0%, hsl(var(--aura-alert-to)) 100%);
  }

  .aura-happy {
    background: linear-gradient(135deg, hsl(var(--aura-happy-from)) 0%, hsl(var(--aura-happy-to)) 100%);
  }

  .aura-anxious {
    background: linear-gradient(135deg, hsl(var(--aura-anxious-from)) 0%, hsl(var(--aura-anxious-to)) 100%);
  }

  .aura-flow {
    background: linear-gradient(135deg, hsl(var(--aura-flow-from)) 0%, hsl(var(--aura-flow-to)) 100%);
  }
}

