"use client"

import { useState, useCallback } from "react"
import { motion, AnimatePresence } from "framer-motion"
import { Calendar, Clock, User, Sparkles, Loader2 } from "lucide-react"
import { cn } from "@/lib/utils"

interface BirthInfo {
  date: string
  time: string
  gender: "male" | "female" | ""
}

interface BirthInputCapsuleProps {
  onSubmit: (info: BirthInfo) => void
  isLoading?: boolean
  className?: string
}

export function BirthInputCapsule({
  onSubmit,
  isLoading = false,
  className,
}: BirthInputCapsuleProps) {
  const [birthInfo, setBirthInfo] = useState<BirthInfo>({
    date: "",
    time: "",
    gender: "",
  })
  const [focused, setFocused] = useState<string | null>(null)

  const isValid = birthInfo.date && birthInfo.time && birthInfo.gender

  const handleSubmit = useCallback(() => {
    if (!isValid || isLoading) return
    onSubmit(birthInfo)
  }, [birthInfo, isValid, isLoading, onSubmit])

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5, ease: [0.16, 1, 0.3, 1] }}
      className={cn(
        "capsule-glass rounded-2xl sm:rounded-full p-3 sm:p-4",
        "flex flex-col sm:flex-row items-stretch sm:items-center gap-3 sm:gap-4",
        "transition-all duration-300",
        focused && "ring-2 ring-accent/20",
        className
      )}
    >
      {/* Date Input */}
      <div className="relative flex-1 min-w-0">
        <Calendar className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-foreground-muted" />
        <input
          type="date"
          value={birthInfo.date}
          onChange={(e) => setBirthInfo((prev) => ({ ...prev, date: e.target.value }))}
          onFocus={() => setFocused("date")}
          onBlur={() => setFocused(null)}
          className={cn(
            "w-full bg-transparent pl-10 pr-3 py-2.5 rounded-lg",
            "text-sm text-foreground outline-none",
            "placeholder:text-foreground-muted",
            "border border-transparent",
            "focus:bg-background/50 focus:border-border",
            "transition-all duration-200"
          )}
          placeholder="出生日期"
        />
