"use client"

import { motion } from "framer-motion"
import { Sparkles, ArrowRight } from "lucide-react"
import { cn } from "@/lib/utils"

type WuxingElement = "wood" | "fire" | "earth" | "metal" | "water"

interface InsightData {
  element: WuxingElement
  title: string
  insight: string
  insightCn: string
}

interface InstantInsightCardProps {
  data: InsightData
  onSaveProfile?: () => void
  className?: string
}

const ELEMENT_STYLES: Record<
  WuxingElement,
  { label: string; icon: string; color: string; bgClass: string }
> = {
  wood: {
    label: "木",
    icon: "🌳",
    color: "hsl(120 50% 40%)",
    bgClass: "bg-green-500/10 text-green-600",
  },
  fire: {
    label: "火",
    icon: "🔥",
    color: "hsl(0 80% 55%)",
    bgClass: "bg-red-500/10 text-red-600",
  },
  earth: {
    label: "土",
    icon: "🏔️",
    color: "hsl(40 70% 50%)",
    bgClass: "bg-yellow-500/10 text-yellow-700",
  },
  metal: {
    label: "金",
    icon: "⚔️",
    color: "hsl(30 5% 70%)",
    bgClass: "bg-gray-400/10 text-gray-600",
  },
  water: {
    label: "水",
    icon: "💧",
    color: "hsl(210 70% 50%)",
    bgClass: "bg-blue-500/10 text-blue-600",
  },
}

export function InstantInsightCard({
  data,
  onSaveProfile,
  className,
}: InstantInsightCardProps) {
  const elementStyle = ELEMENT_STYLES[data.element]

  return (
    <motion.div
      initial={{ opacity: 0, y: 30, scale: 0.95 }}
      animate={{ opacity: 1, y: 0, scale: 1 }}
      transition={{ duration: 0.6, ease: [0.16, 1, 0.3, 1] }}
      className={cn(
        "glass-card rounded-2xl p-6 sm:p-8 max-w-lg mx-auto",
        "text-center space-y-6",
        className
      )}
    >
      {/* Element Badge */}
      <motion.div
        initial={{ scale: 0 }}
        animate={{ scale: 1 }}
        transition={{ delay: 0.2, type: "spring", stiffness: 300 }}
        className="flex justify-center"
      >
        <div
          className={cn(
            "inline-flex items-center gap-2 px-4 py-2 rounded-full",
            elementStyle.bgClass
          )}
        >
          <span className="text-lg">{elementStyle.icon}</span>
          <span className="font-medium">{elementStyle.label} 命格</span>
        </div>
      </motion.div>

      {/* Title */}
      <motion.h2
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.3 }}
        className="serif-heading text-2xl sm:text-3xl"
        style={{ color: elementStyle.color }}
      >
        {data.title}
      </motion.h2>

      {/* Insight Text */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.4 }}
        className="space-y-3"
      >
        <p className="text-foreground leading-relaxed">{data.insight}</p>
        <p className="text-foreground-muted text-sm">{data.insightCn}</p>
      </motion.div>

      {/* Divider */}
      <motion.div
        initial={{ scaleX: 0 }}
        animate={{ scaleX: 1 }}
        transition={{ delay: 0.5 }}
        className="w-16 h-px bg-border mx-auto"
      />

      {/* CTA Button */}
      <motion.button
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.6 }}
        whileHover={{ scale: 1.02 }}
        whileTap={{ scale: 0.98 }}
        onClick={onSaveProfile}
        className={cn(
          "inline-flex items-center gap-2 px-6 py-3 rounded-full",
          "bg-foreground text-background font-medium",
          "shadow-lg hover:shadow-xl transition-all duration-200"
        )}
      >
        <Sparkles className="w-4 h-4" />
        <span>保存我的命盘</span>
        <ArrowRight className="w-4 h-4" />
      </motion.button>

      {/* Disclaimer */}
      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.7 }}
        className="text-xs text-foreground-muted"
      >
        登录后解锁完整命盘分析与每日运势
      </motion.p>
    </motion.div>
  )
}

