"use client"

import { useMemo } from "react"
import { cn } from "@/lib/utils"
import type { StreamEntry } from "@/stores/stream-store"

function formatTime(value: string) {
  try {
    return new Date(value).toLocaleTimeString("zh-CN", { hour: "2-digit", minute: "2-digit" })
  } catch {
    return ""
  }
}

export function StreamCard({ entry }: { entry: StreamEntry }) {
  const time = useMemo(() => formatTime(entry.created_at), [entry.created_at])
  const energyDelta = entry.energy_delta ?? 0
  const energyLabel = energyDelta > 0 ? `+${energyDelta}` : `${energyDelta}`

  return (
    <div className={cn("rounded-xl border border-border bg-card shadow-sm px-4 py-3")}>
      <div className="flex items-center justify-between text-xs text-muted-foreground">
        <span>👤 You</span>
        <span>{time}</span>
      </div>
      <p className="mt-2 text-sm leading-relaxed text-foreground">{entry.raw_content}</p>
      <div className="mt-3 flex flex-wrap items-center gap-2 text-xs text-muted-foreground">
        {(entry.tags || []).map((tag) => (
          <span key={tag} className="rounded-full bg-background-subtle px-2 py-0.5">
            {tag}
          </span>
        ))}
        <span className={cn("ml-auto font-medium", energyDelta >= 0 ? "text-success" : "text-destructive")}>
          ⚡ {energyLabel}
        </span>
      </div>
    </div>
  )
}

