"use client"

import { useState, useCallback } from "react"
import { motion, AnimatePresence } from "framer-motion"
import { Bot, Clock, Check, Flame } from "lucide-react"
import { cn } from "@/lib/utils"
import type { ActionCard as ActionCardType } from "@/stores/stream-store"

type ActionCardProps = {
  card: ActionCardType
  onComplete: (id: string) => void
  onDismiss: (id: string) => void
}

const CARD_TYPE_STYLES: Record<string, { icon: string; label: string; bg: string }> = {
  suggestion: { icon: "🧘", label: "建议", bg: "bg-accent/10" },
  confirmation: { icon: "✓", label: "确认", bg: "bg-success/10" },
  insight: { icon: "💡", label: "洞察", bg: "bg-blue-500/10" },
  milestone: { icon: "🎉", label: "里程碑", bg: "bg-yellow-500/10" },
}

export function ActionCard({ card, onComplete, onDismiss }: ActionCardProps) {
  const [isCompleting, setIsCompleting] = useState(false)
  const [holdProgress, setHoldProgress] = useState(0)

  const cardStyle = CARD_TYPE_STYLES[card.type] || CARD_TYPE_STYLES.suggestion

  const handleComplete = useCallback(() => {
    setIsCompleting(true)
    setTimeout(() => {
      onComplete(card.id)
    }, 300)
  }, [card.id, onComplete])

  return (
    <motion.div
      initial={{ opacity: 0, y: 20, scale: 0.95 }}
      animate={{ opacity: 1, y: 0, scale: 1 }}
      exit={{ opacity: 0, scale: 0.9 }}
      transition={{ duration: 0.4, ease: [0.16, 1, 0.3, 1] }}
      className={cn(
        "glass-card rounded-xl px-4 py-4",
        "border-l-4 border-l-accent",
        "hover:shadow-lg transition-all duration-300"
      )}
    >
      {/* Header */}
      <div className="flex items-center justify-between text-xs">
        <span className="inline-flex items-center gap-1.5 text-accent">
          <Bot className="w-3.5 h-3.5" />
          <span className="font-medium">Mentis</span>
        </span>
        <span className={cn("px-2 py-0.5 rounded-full text-foreground-muted", cardStyle.bg)}>
          {cardStyle.icon} {cardStyle.label}
        </span>
      </div>

      {/* Content */}
      <h3 className="mt-3 text-sm font-semibold text-foreground">{card.title}</h3>
      {card.description && (
        <p className="mt-1.5 text-sm text-foreground-muted leading-relaxed">
          {card.description}
        </p>
      )}

      {/* Actions */}
