import type { ActionCard as ActionCardType } from "@/stores/stream-store"

type ActionCardProps = {
  card: ActionCardType
  onComplete: (id: string) => void
  onDismiss: (id: string) => void
}

const CARD_TYPE_STYLES: Record<string, { icon: string; label: string; bg: string }> = {
  suggestion: { icon: "🧘", label: "建议", bg: "bg-accent/10" },
