/**
 * Mentis OS v3.0 - Drizzle Configuration
 */

import type { Config } from "drizzle-kit";

export default {
  schema: "./src/lib/db/schema.ts",
  out: "./drizzle",
  dialect: "postgresql",
  dbCredentials: {
    url: process.env.MENTIS_DB_URL || process.env.DATABASE_URL || "",
  },
  verbose: true,
  strict: true,
} satisfies Config;

"use client"

import { useEffect } from "react"
import { Omnibar } from "@/components/chat/omnibar"
import { DynamicAura } from "@/components/hud/dynamic-aura"
import { StreamFeed } from "./stream-feed"
import { TopStatusBar } from "./top-status-bar"
import { useStreamStore } from "@/stores/stream-store"

export function StreamScreen() {
  const {
    entries,
    actionCards,
    isStreaming,
    userState,
    sendInput,
    completeActionCard,
    dismissActionCard,
    subscribeRealtime,
    unsubscribeRealtime,
  } = useStreamStore()

  useEffect(() => {
    subscribeRealtime()
    return () => unsubscribeRealtime()
  }, [subscribeRealtime, unsubscribeRealtime])

  return (
    <div className="relative h-screen w-full overflow-hidden bg-background">
      <DynamicAura state={userState.aura_state} />
      <div className="relative z-10 flex h-full flex-col">
        <TopStatusBar
          energy={userState.energy_score}
          momentum={userState.momentum}
          aura={userState.aura_state}
        />
        <StreamFeed
          entries={entries}
          actionCards={actionCards}
          onCompleteCard={completeActionCard}
          onDismissCard={dismissActionCard}
        />
        <Omnibar onSend={sendInput} isStreaming={isStreaming} />
      </div>
    </div>
  )
}

/**
 * Mentis OS v3.0 - Drizzle ORM Schema
 *
 * 类型安全的数据库 Schema 定义
 */

import {
  pgTable,
  uuid,
  text,
  timestamp,
  integer,
  boolean,
  jsonb,
  varchar,
  real,
  index,
  uniqueIndex,
  check,
} from "drizzle-orm/pg-core";
import { relations, sql } from "drizzle-orm";

// =============================================================================
// Users
// =============================================================================

export const mentisUser = pgTable(
  "mentis_user",
  {
    id: uuid("id").primaryKey().defaultRandom(),
    email: text("email"),
    name: text("name"),
    tier: text("tier").notNull().default("free"),
    createdAt: timestamp("created_at", { withTimezone: true }).defaultNow(),
    updatedAt: timestamp("updated_at", { withTimezone: true }).defaultNow(),
  },
  (table) => ({
    emailLowerIdx: uniqueIndex("idx_mentis_user_email_lower").on(
      sql`lower(${table.email})`
    ),
  })
);

export type MentisUser = typeof mentisUser.$inferSelect;
export type NewMentisUser = typeof mentisUser.$inferInsert;

// =============================================================================
// User Profile
// =============================================================================

export const userProfile = pgTable("user_profile", {
  id: uuid("id").primaryKey().defaultRandom(),
  userId: uuid("user_id")
    .references(() => mentisUser.id, { onDelete: "cascade" })
    .unique(),
  birthTime: timestamp("birth_time", { withTimezone: true }),
  birthLocation: jsonb("birth_location").$type<{
    city?: string;
    lat?: number;
    lng?: number;
    timezone?: string;
  }>(),
  baziData: jsonb("bazi_data"),
  ziweiData: jsonb("ziwei_data"),
  mbtiType: varchar("mbti_type", { length: 4 }),
  mbtiRawScores: jsonb("mbti_raw_scores"),
  jungProfile: jsonb("jung_profile"),
  preferences: jsonb("preferences").$type<{
    preferred_interventions?: string[];
    disliked_interventions?: string[];
    notification_enabled?: boolean;
    password_hash?: string;
  }>(),
  notificationSettings: jsonb("notification_settings"),
  createdAt: timestamp("created_at", { withTimezone: true }).defaultNow(),
  updatedAt: timestamp("updated_at", { withTimezone: true }).defaultNow(),
});

export type UserProfile = typeof userProfile.$inferSelect;
export type NewUserProfile = typeof userProfile.$inferInsert;

// =============================================================================
// User State (Real-time)
// =============================================================================

export const userState = pgTable("user_state", {
  userId: uuid("user_id")
    .primaryKey()
    .references(() => mentisUser.id, { onDelete: "cascade" }),
  energyScore: integer("energy_score").default(50),
  momentum: integer("momentum").default(0),
  streakDays: integer("streak_days").default(0),
  currentMood: varchar("current_mood", { length: 50 }),
  moodIntensity: integer("mood_intensity"),
  auraState: varchar("aura_state", { length: 20 }).default("calm"),
  lastActiveAt: timestamp("last_active_at", { withTimezone: true }).defaultNow(),
  lastCheckinAt: timestamp("last_checkin_at", { withTimezone: true }),
  streakUpdatedAt: timestamp("streak_updated_at", { mode: "date" }),
  updatedAt: timestamp("updated_at", { withTimezone: true }).defaultNow(),
});

export type UserState = typeof userState.$inferSelect;
export type NewUserState = typeof userState.$inferInsert;

// =============================================================================
// Stream Entry
// =============================================================================

export const streamEntry = pgTable(
  "stream_entry",
  {
    id: uuid("id").primaryKey().defaultRandom(),
    userId: uuid("user_id").references(() => mentisUser.id, {
      onDelete: "cascade",
    }),
    type: varchar("type", { length: 30 }).notNull(),
    rawContent: text("raw_content").notNull(),
    mediaUrls: text("media_urls").array(),
    emotion: jsonb("emotion").$type<{
      primary: string;
      intensity: number;
      secondary?: string[];
      confidence?: number;
    }>(),
    context: jsonb("context").$type<{
      scene?: string;
      trigger?: string;
      target?: string;
      time_of_day?: string;
    }>(),
    tags: text("tags").array(),
    energyDelta: integer("energy_delta").default(0),
    embeddingId: varchar("embedding_id", { length: 100 }),
    matchedBehavior: varchar("matched_behavior", { length: 100 }),
    matchedConfidence: real("matched_confidence"),
    autoCheckin: boolean("auto_checkin"),
    source: varchar("source", { length: 50 }).default("app"),
    clientMetadata: jsonb("client_metadata"),
    createdAt: timestamp("created_at", { withTimezone: true }).defaultNow(),
  },
  (table) => ({
    userCreatedIdx: index("idx_stream_user_created").on(
      table.userId,
      table.createdAt
    ),
    typeIdx: index("idx_stream_type").on(table.type),
  })
);

export type StreamEntry = typeof streamEntry.$inferSelect;
export type NewStreamEntry = typeof streamEntry.$inferInsert;

// =============================================================================
// Action Task
// =============================================================================

export const actionTask = pgTable(
  "action_task",
  {
    id: uuid("id").primaryKey().defaultRandom(),
    userId: uuid("user_id").references(() => mentisUser.id, {
      onDelete: "cascade",
    }),
    type: varchar("type", { length: 50 }).notNull(),
    title: varchar("title", { length: 200 }).notNull(),
    description: text("description"),
    status: varchar("status", { length: 20 }).default("pending"),
    triggerTime: timestamp("trigger_time", { withTimezone: true }),
    expiresAt: timestamp("expires_at", { withTimezone: true }),
    completedAt: timestamp("completed_at", { withTimezone: true }),
    source: varchar("source", { length: 50 }).default("agent"),
    triggerContext: jsonb("trigger_context"),
    momentumReward: integer("momentum_reward").default(1),
    createdAt: timestamp("created_at", { withTimezone: true }).defaultNow(),
    updatedAt: timestamp("updated_at", { withTimezone: true }).defaultNow(),
  },
  (table) => ({
    userStatusIdx: index("idx_task_user_status").on(table.userId, table.status),
    triggerTimeIdx: index("idx_task_trigger_time").on(table.triggerTime),
  })
);

export type ActionTask = typeof actionTask.$inferSelect;
export type NewActionTask = typeof actionTask.$inferInsert;

// =============================================================================
// Checkin Log
// =============================================================================

export const checkinLog = pgTable(
  "checkin_log",
  {
    id: uuid("id").primaryKey().defaultRandom(),
    userId: uuid("user_id").references(() => mentisUser.id, {
      onDelete: "cascade",
    }),
    taskId: uuid("task_id").references(() => actionTask.id, {
      onDelete: "set null",
    }),
    streamId: uuid("stream_id").references(() => streamEntry.id, {
      onDelete: "set null",
    }),
    behaviorType: varchar("behavior_type", { length: 100 }).notNull(),
    source: varchar("source", { length: 20 }).notNull(),
    momentumDelta: integer("momentum_delta").default(1),
    note: text("note").default(""),
    createdAt: timestamp("created_at", { withTimezone: true }).defaultNow(),
  },
  (table) => ({
    userCreatedIdx: index("idx_checkin_user_created").on(
      table.userId,
      table.createdAt
    ),
  })
);

export type CheckinLog = typeof checkinLog.$inferSelect;
export type NewCheckinLog = typeof checkinLog.$inferInsert;

// =============================================================================
// Insight Record
// =============================================================================

export const insightRecord = pgTable(
  "insight_record",
  {
    id: uuid("id").primaryKey().defaultRandom(),
    userId: uuid("user_id").references(() => mentisUser.id, {
      onDelete: "cascade",
    }),
    type: varchar("type", { length: 50 }).notNull(),
    title: varchar("title", { length: 200 }),
    content: text("content").notNull(),
    sourceData: jsonb("source_data"),
    confidence: real("confidence"),
    status: varchar("status", { length: 20 }).default("draft"),
    publishedAt: timestamp("published_at", { withTimezone: true }),
    createdAt: timestamp("created_at", { withTimezone: true }).defaultNow(),
  },
  (table) => ({
    userCreatedIdx: index("idx_insight_user_created").on(
      table.userId,
      table.createdAt
    ),
  })
);

export type InsightRecord = typeof insightRecord.$inferSelect;
export type NewInsightRecord = typeof insightRecord.$inferInsert;

// =============================================================================
// Relations
// =============================================================================

export const mentisUserRelations = relations(mentisUser, ({ one, many }) => ({
  profile: one(userProfile, {
    fields: [mentisUser.id],
    references: [userProfile.userId],
  }),
  state: one(userState, {
    fields: [mentisUser.id],
    references: [userState.userId],
  }),
  streams: many(streamEntry),
  tasks: many(actionTask),
  checkins: many(checkinLog),
  insights: many(insightRecord),
}));

export const streamEntryRelations = relations(streamEntry, ({ one }) => ({
  user: one(mentisUser, {
    fields: [streamEntry.userId],
    references: [mentisUser.id],
  }),
}));

export const actionTaskRelations = relations(actionTask, ({ one, many }) => ({
  user: one(mentisUser, {
    fields: [actionTask.userId],
    references: [mentisUser.id],
  }),
  checkins: many(checkinLog),
}));

export const checkinLogRelations = relations(checkinLog, ({ one }) => ({
  user: one(mentisUser, {
    fields: [checkinLog.userId],
    references: [mentisUser.id],
  }),
  task: one(actionTask, {
    fields: [checkinLog.taskId],
    references: [actionTask.id],
  }),
  stream: one(streamEntry, {
    fields: [checkinLog.streamId],
    references: [streamEntry.id],
  }),
}));

export const insightRecordRelations = relations(insightRecord, ({ one }) => ({
  user: one(mentisUser, {
    fields: [insightRecord.userId],
    references: [mentisUser.id],
  }),
}));

"use client"

import { create } from "zustand"
import { streamMentisInput, type StreamEvent } from "@/lib/mentis-stream"
import { connectRealtime, type RealtimeMessage } from "@/lib/realtime"

export type StreamEntry = {
  id: string
  type: string
  raw_content: string
  created_at: string
  emotion?: { primary: string; intensity: number; secondary: string[] }
  context?: Record<string, unknown>
  tags?: string[]
  energy_delta?: number
}

export type ActionCard = {
  id: string
  type: string
  title: string
  description?: string
  momentum_reward?: number
  expires_at?: string
}

type CurrentMood = {
  primary: string
  intensity: number
  detected_at: string | null
}

type TodayStats = {
  stream_count: number
  checkin_count: number
  energy_high: number | null
  energy_low: number | null
}

type UserState = {
  energy_score: number
  momentum: number
  streak_days: number
  aura_state: string
  current_mood: CurrentMood | null
  today_stats: TodayStats | null
  pending_tasks: number
  unread_insights: number
  last_active_at: string | null
}

type StreamStore = {
  entries: StreamEntry[]
  actionCards: ActionCard[]
  isStreaming: boolean
  userState: UserState
  lastStreamId: string | null
  sendInput: (content: string) => void
  clearEntries: () => void
  completeActionCard: (id: string) => void
  dismissActionCard: (id: string) => void
  subscribeRealtime: () => void
  unsubscribeRealtime: () => void
}

const initialUserState: UserState = {
  energy_score: 50,
  momentum: 0,
  streak_days: 0,
  aura_state: "calm",
  current_mood: null,
  today_stats: null,
  pending_tasks: 0,
  unread_insights: 0,
  last_active_at: null,
}

let realtimeSocket: WebSocket | null = null

export const useStreamStore = create<StreamStore>((set, get) => ({
  entries: [],
  actionCards: [],
  isStreaming: false,
  userState: initialUserState,
  lastStreamId: null,

  sendInput: (content) => {
    if (!content.trim()) return
    set({ isStreaming: true })

    streamMentisInput(
      { content, type: "vibe_diary" },
      {
        onEvent: (event: StreamEvent) => {
          if (event.event === "stream_created") {
            set((state) => ({
              entries: [
                ...state.entries,
                {
                  id: event.data.id,
                  type: event.data.type,
                  raw_content: event.data.raw_content,
                  created_at: event.data.created_at,
                },
              ],
              lastStreamId: event.data.id,
            }))
            return
          }

          if (event.event === "extraction_complete") {
            const { lastStreamId } = get()
            if (!lastStreamId) return
            set((state) => ({
              entries: state.entries.map((entry) =>
                entry.id === lastStreamId
                  ? {
                      ...entry,
                      emotion: event.data.emotion,
                      context: event.data.context,
                      tags: event.data.tags,
                      energy_delta: event.data.energy_delta,
                    }
                  : entry
              ),
            }))
            return
          }

          if (event.event === "state_update") {
            set((state) => ({
              userState: {
                ...state.userState,
                energy_score: event.data.energy_score ?? state.userState.energy_score,
                momentum: event.data.momentum ?? state.userState.momentum,
                streak_days: event.data.streak_days ?? state.userState.streak_days,
                aura_state: event.data.aura_state ?? state.userState.aura_state,
              },
            }))
            return
          }

          if (event.event === "agent_response") {
            const card = (event.data as { card?: ActionCard }).card
            if (card) {
              set((state) => ({
                actionCards: [card, ...state.actionCards],
              }))
            }
            return
          }

          if (event.event === "done") {
            set({ isStreaming: false })
            return
          }
        },
        onError: () => {
          set({ isStreaming: false })
        },
      }
    )
  },

  clearEntries: () => set({ entries: [], actionCards: [], lastStreamId: null }),

  completeActionCard: (id) =>
    set((state) => ({
      actionCards: state.actionCards.filter((card) => card.id !== id),
    })),

  dismissActionCard: (id) =>
    set((state) => ({
      actionCards: state.actionCards.filter((card) => card.id !== id),
    })),

  subscribeRealtime: () => {
    if (realtimeSocket) return
    const token = process.env.NEXT_PUBLIC_MENTIS_BEARER?.trim()
      || process.env.NEXT_PUBLIC_MENTIS_USER_ID?.trim()
      || ""
    realtimeSocket = connectRealtime(token, (message: RealtimeMessage) => {
      if (message.type === "state_update" && message.data) {
        const data = message.data as Record<string, unknown>
        set((state) => ({
          userState: {
            ...state.userState,
            energy_score: Number(data.energy_score ?? state.userState.energy_score),
            momentum: Number(data.momentum ?? state.userState.momentum),
            streak_days: Number(data.streak_days ?? state.userState.streak_days),
            aura_state: String(data.aura_state ?? state.userState.aura_state),
            current_mood: data.current_mood as CurrentMood ?? state.userState.current_mood,
          },
        }))
      }
      if (message.type === "pending_tasks" && message.data) {
        const data = message.data as { tasks?: ActionCard[] }
        if (data.tasks) {
          set((state) => ({
            actionCards: [...data.tasks!, ...state.actionCards.filter(
              (c) => !data.tasks!.some((t) => t.id === c.id)
            )],
            userState: {
              ...state.userState,
              pending_tasks: data.tasks!.length,
            },
          }))
        }
      }
    })
  },

  unsubscribeRealtime: () => {
    if (!realtimeSocket) return
    realtimeSocket.close()
    realtimeSocket = null
  },
}))

