"use client"

import { useMemo } from "react"
import { motion } from "framer-motion"
import { cn } from "@/lib/utils"

type OrbState = "default" | "insight" | "loading" | "wood" | "fire" | "earth" | "metal" | "water"

interface MentisSoulOrbProps {
  state?: OrbState
  size?: "sm" | "md" | "lg" | "xl"
  className?: string
}

const ORB_GRADIENTS: Record<OrbState, { from: string; to: string }> = {
  default: {
    from: "hsl(25 60% 75%)",    // Soul Peach
    to: "hsl(200 40% 80%)",     // Mist Blue
  },
  insight: {
    from: "hsl(270 60% 55%)",   // Mentis Purple
    to: "hsl(290 40% 75%)",     // Morning Haze
  },
  loading: {
    from: "hsl(40 60% 70%)",
    to: "hsl(200 50% 75%)",
  },
  wood: {
    from: "hsl(120 50% 45%)",
    to: "hsl(140 40% 60%)",
  },
  fire: {
    from: "hsl(0 80% 55%)",
    to: "hsl(30 70% 60%)",
  },
  earth: {
    from: "hsl(40 70% 50%)",
    to: "hsl(25 60% 65%)",
  },
  metal: {
    from: "hsl(220 10% 80%)",
    to: "hsl(240 5% 90%)",
  },
  water: {
    from: "hsl(210 70% 50%)",
    to: "hsl(230 60% 65%)",
  },
}

const SIZE_CLASSES = {
  sm: "w-24 h-24",
  md: "w-36 h-36 sm:w-44 sm:h-44",
  lg: "w-48 h-48 sm:w-56 sm:h-56",
  xl: "w-64 h-64 sm:w-80 sm:h-80",
}

export function MentisSoulOrb({
  state = "default",
  size = "lg",
  className,
}: MentisSoulOrbProps) {
  const gradient = useMemo(() => ORB_GRADIENTS[state], [state])

  return (
    <div className={cn("relative", SIZE_CLASSES[size], className)}>
      {/* Outer Glow */}
      <motion.div
        className="absolute inset-0 rounded-full blur-3xl opacity-40"
        style={{
          background: `radial-gradient(circle, ${gradient.from} 0%, ${gradient.to} 100%)`,
        }}
        animate={{
          scale: [1, 1.1, 1],
          opacity: [0.3, 0.5, 0.3],
        }}
        transition={{
          duration: 4,
          repeat: Infinity,
          ease: "easeInOut",
        }}
      />

      {/* Middle Glow */}
      <motion.div
        className="absolute inset-4 rounded-full blur-2xl opacity-50"
        style={{
          background: `radial-gradient(circle, ${gradient.from} 0%, ${gradient.to} 100%)`,
        }}
        animate={{
          scale: [1, 1.05, 1],
          opacity: [0.4, 0.6, 0.4],
        }}
        transition={{
          duration: 3,
          repeat: Infinity,
          ease: "easeInOut",
          delay: 0.5,
        }}
      />

      {/* Core Orb */}
      <motion.div
        className="absolute inset-8 rounded-full"
        style={{
          background: `radial-gradient(circle at 30% 30%,
            ${gradient.from} 0%,
            ${gradient.to} 50%,
            ${gradient.from}88 100%)`,
          boxShadow: `
            inset 0 0 40px rgba(255, 255, 255, 0.3),
            0 0 60px ${gradient.from}66
          `,
        }}
        animate={{
          scale: [1, 1.02, 1],
        }}
        transition={{
          duration: 4,
          repeat: Infinity,
          ease: "easeInOut",
        }}
      />

      {/* Highlight Spot */}
      <motion.div
        className="absolute top-1/4 left-1/4 w-1/4 h-1/4 rounded-full blur-xl opacity-70"
        style={{
          background: "rgba(255, 255, 255, 0.8)",
        }}
        animate={{
          opacity: [0.5, 0.8, 0.5],
        }}
        transition={{
          duration: 3,
          repeat: Infinity,
          ease: "easeInOut",
        }}
      />

      {/* Sparkle Effect (for insight state) */}
      {state === "insight" && (
        <>
          {[...Array(6)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute w-1 h-1 rounded-full bg-white"
              style={{
                top: `${20 + Math.random() * 60}%`,
                left: `${20 + Math.random() * 60}%`,
              }}
              animate={{
                opacity: [0, 1, 0],
                scale: [0, 1.5, 0],
              }}
              transition={{
                duration: 2,
                repeat: Infinity,
                delay: i * 0.3,
                ease: "easeOut",
              }}
            />
          ))}
        </>
      )}

      {/* Loading Spinner (for loading state) */}
      {state === "loading" && (
        <motion.div
          className="absolute inset-0 rounded-full border-2 border-transparent border-t-white/50"
          animate={{ rotate: 360 }}
          transition={{
            duration: 1.5,
            repeat: Infinity,
            ease: "linear",
          }}
        />
      )}
    </div>
  )
}

"use client"

import { useEffect } from "react"
import { Omnibar } from "@/components/chat/omnibar"
import { DynamicAura } from "@/components/hud/dynamic-aura"
import { StreamFeed } from "./stream-feed"
import { TopStatusBar } from "./top-status-bar"
import { useStreamStore } from "@/stores/stream-store"

export function StreamScreen() {
  const {
    entries,
    actionCards,
    isStreaming,
    userState,
    sendInput,
    completeActionCard,
    dismissActionCard,
    subscribeRealtime,
    unsubscribeRealtime,
  } = useStreamStore()

  useEffect(() => {
    subscribeRealtime()
    return () => unsubscribeRealtime()
  }, [subscribeRealtime, unsubscribeRealtime])

  return (
    <div className="relative h-screen w-full overflow-hidden bg-background">
      <DynamicAura state={userState.aura_state} />
      <div className="relative z-10 flex h-full flex-col">
        <TopStatusBar
          energy={userState.energy_score}
          momentum={userState.momentum}
          aura={userState.aura_state}
        />
        <StreamFeed
          entries={entries}
          actionCards={actionCards}
          onCompleteCard={completeActionCard}
          onDismissCard={dismissActionCard}
        />
        <Omnibar onSubmit={sendInput} isLoading={isStreaming} />
      </div>
    </div>
  )
}

