  onConversationStart?: (id: string) => void;
  /**
   * ä» URL ä¼ å…¥çš„åˆå§‹æ¶ˆæ¯ï¼ˆå¦‚ä»é€šçŸ¥è·³è½¬ï¼‰
   */
  initialPrompt?: string | null;
  /**
