export interface LifecoachData {
  northStar?: string;

  monthlyProject?: {
    name: string;
    progress: number; // 0-100
    daysRemaining: number;
  };

  todayLevers: LeverItem[];

  weekRocks: RockItem[];
}

export interface LeverItem {
  id: string;
  text: string;
  completed: boolean;
}

export interface RockItem {
  id: string;
  text: string;
  role: string;
  completed: boolean;
}

