  actionLabel: string;
  actionRoute: string;
}

export interface SkillCardContent {
  headline: string;
  insights: string[];
  rating?: {
    overall: number;
    label: string;
    details?: Record<string, number>;
  };
  suggestions?: {
    do: string[];
    avoid: string[];
  };
}

