  data: DashboardDTO;
  meta?: {
    cached: boolean;
    generatedAt: string;
  };
}

export interface CheckinResponse {
  success: boolean;
  data: {
    streak: number;
    message: string;
    animation?: string;
    status: StatusData;
  };
}

