export function BentoDashboard({ data, className }: BentoDashboardProps) {
  return (
    <motion.div className="...">
      {/* Row 1: Greeting + Solar Term + Quote */}
      <GreetingSection 
        greeting={data.greeting}
        solarTerm={data.solarTerm}
        quote={data.quote}
      />

      {/* Row 2: Today's Fortune Card */}
      {data.fortuneData && (
        <FortuneCard 
          score={data.fortuneData.score}
          theme={data.fortuneData.theme}
          suggestions={data.fortuneData.suggestions}
          areaScores={data.fortuneData.areaScores}
        />
      )}

      {/* Row 3: Today's Priority (if exists) */}
      {data.todayLevers.length > 0 && (
        <TodayPriorityCard 
          lever={data.todayLevers[0]}
          fortuneHint={data.fortuneHint}
        />
      )}

      {/* Row 4: North Star + Weekly Progress */}
      <div className="grid grid-cols-2 gap-2">
        <NorthStarCard northStar={data.northStar} />
        <WeekProgressCard rocks={data.weekRocks} />
      </div>

      {/* Row 5: Streak + Check-in */}
      <StreakBar streak={data.streak} checkedIn={data.checkedIn} />
    </motion.div>
  );
}
