export function ChatContainer({
  skillId,
  scenario,
  skill = "bazi",
  conversationId: externalConversationId,
  voiceMode = "warm",
  onConversationStart,
  initialPrompt,
  onInitialPromptSent,
  dashboardData,
  isDashboardLoading,
  onCheckIn,
  onToggleLever,
  onToggleRock,
  forceIntro = false,
}: ChatContainerProps) {
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const initialPromptSentRef = useRef(false);

  // ç”Ÿæˆç¨³å®šçš„ conversationIdï¼ˆå¦‚æœå¤–éƒ¨æ²¡ä¼ å…¥ï¼‰
  // ä½¿ç”¨ useRef ç¡®ä¿åœ¨ç»„ä»¶ç”Ÿå‘½å‘¨æœŸå†…ä¿æŒä¸å˜
  const generatedIdRef = useRef<string | null>(null);
  if (!generatedIdRef.current) {
    generatedIdRef.current = generateUUID();
  }
  const conversationId = externalConversationId || generatedIdRef.current;

  // UI å±•ç¤ºç”¨çš„ skillï¼ˆä¸»é¢˜ã€ç©ºçŠ¶æ€ç­‰ï¼‰
  const displaySkill: SkillType = skill || (skillId as SkillType) || "bazi";

  // Skill Intro Card - é¦–æ¬¡ä½¿ç”¨æ—¶æ˜¾ç¤º
  const [showIntroCard, setShowIntroCard] = useState(false);
  const { data: introData } = useSkillIntro(displaySkill);
  const { mark: doMarkFirstUse } = useMarkFirstUse(displaySkill);

  // Use AI SDK powered chat hook
  // skillId å¯é€‰ï¼šä¸ä¼ æ—¶åç«¯ LLM è‡ªåŠ¨è·¯ç”±
  const {
    messages,
    isLoading,
    sendMessage,
    sendQuickPrompt,
    error,
  } = useVibeChat({
    skillId: skillId as SkillId | undefined,
    scenario,
    voiceMode,
    conversationId,
    onConversationStart,
    onError: (err) => {
      console.error("Chat error:", err);
      // Show user-friendly toast notification with retry option
      const errorMessage = err instanceof Error ? err.message : "æœªçŸ¥é”™è¯¯";
      const isNetworkError = errorMessage.includes("network") || errorMessage.includes("fetch");
      const isTimeoutError = errorMessage.includes("timeout") || errorMessage.includes("è¶…æ—¶");

      toast.error(
        isNetworkError ? "ç½‘ç»œè¿æ¥å¤±è´¥" : isTimeoutError ? "è¯·æ±‚è¶…æ—¶" : "å‘é€å¤±è´¥",
        {
          description: isNetworkError
            ? "è¯·æ£€æŸ¥æ‚¨çš„ç½‘ç»œè¿æ¥åé‡è¯•"
            : isTimeoutError
              ? "æœåŠ¡å™¨å“åº”æ—¶é—´è¿‡é•¿ï¼Œè¯·ç¨åé‡è¯•"
              : "è¯·ç¨åé‡è¯•ï¼Œæˆ–åˆ·æ–°é¡µé¢",
          duration: 5000,
        }
      );
    },
  });

  // æ£€æµ‹æ˜¯å¦é¦–æ¬¡ä½¿ç”¨æˆ–å¼ºåˆ¶æ˜¾ç¤ºä»‹ç»å¡
  useEffect(() => {
    if (introData?.is_first_use || forceIntro) {
      setShowIntroCard(true);
    }
  }, [introData?.is_first_use, forceIntro]);

  // å¤„ç†ä»‹ç»å¡ç‰‡åŠ¨ä½œ
  // ä» Discover è¿›å…¥æ—¶ï¼ˆforceIntro=trueï¼‰ï¼Œå…³é—­æ—¶ä¸æ ‡è®° first_use
  const handleIntroAction = useCallback((action: IntroCardAction) => {
    if (action.type === 'dismiss') {
      setShowIntroCard(false);
      if (!forceIntro) {
        doMarkFirstUse();
      }
    } else if (action.type === 'send_prompt') {
      setShowIntroCard(false);
      if (!forceIntro) {
        doMarkFirstUse();
      }
      sendQuickPrompt(action.prompt);
    }
  }, [forceIntro, doMarkFirstUse, sendQuickPrompt]);

  // Vercel rule: rerender-defer-reads - åªåœ¨ messages é•¿åº¦å˜åŒ–æ—¶æ»šåŠ¨
  const messagesLength = messages.length;
  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messagesLength, scrollToBottom]);

  // å¤„ç†ä» URL ä¼ å…¥çš„åˆå§‹æ¶ˆæ¯ï¼ˆå¦‚ä»é€šçŸ¥è·³è½¬ï¼‰
  // Vercel rule: rerender-dependencies - ä½¿ç”¨ ref é¿å… sendMessage ä¾èµ–
  const sendMessageRef = useRef(sendMessage);
  sendMessageRef.current = sendMessage;

  useEffect(() => {
    if (initialPrompt && !initialPromptSentRef.current && !isLoading) {
      initialPromptSentRef.current = true;
      sendMessageRef.current(initialPrompt).then(() => {
        onInitialPromptSent?.();
      });
    }
  }, [initialPrompt, isLoading, onInitialPromptSent]);

  // é‡ç½®æ ‡è®°å½“ initialPrompt å˜åŒ–æ—¶
  useEffect(() => {
    if (!initialPrompt) {
      initialPromptSentRef.current = false;
    }
  }, [initialPrompt]);

  // Handle send from ChatInput
  const handleSend = useCallback(
    async (content: string) => {
      await sendMessage(content);
    },
    [sendMessage]
  );

  // Handle quick prompt from empty state
  const handleQuickPrompt = useCallback(
    async (content: string) => {
      await sendQuickPrompt(content);
    },
    [sendQuickPrompt]
  );

  const hasMessages = messages.length > 0;

  // DailyGreeting æ•°æ®
  const dailyData = useDailyGreeting(displaySkill);
  const actionKey = useMemo(
    () => `vibelife-daily-action:${displaySkill}:${dailyData.isoDate}`,
    [displaySkill, dailyData.isoDate]
  );
  const [actionCompleted, setActionCompleted] = useState(false);

  useEffect(() => {
    if (typeof window === "undefined") return;
    setActionCompleted(getLocalStorage(actionKey) === "1");
  }, [actionKey]);

  const toggleAction = useCallback(() => {
    setActionCompleted((prev) => {
      const next = !prev;
      setLocalStorage(actionKey, next ? "1" : "0");
      return next;
    });
  }, [actionKey]);

  const shareText = useMemo(() => {
    const term = dailyData.solarTerm ? ` Â· ${dailyData.solarTerm}` : "";
    return [
      `VibeLife${term} Â· ${dailyData.isoDate}`,
      dailyData.greeting,
      `è¿åŠ¿æŒ‡æ•°ï¼š${dailyData.fortuneScore}/100`,
      `ä»Šæ—¥ä¸€æ­¥ï¼š${dailyData.actionText}`,
      "#VibeLife",
    ].join("\n");
  }, [dailyData]);

  // é»˜è®¤å›è°ƒï¼ˆå¦‚æœæ²¡æœ‰ä¼ å…¥ï¼‰
  const handleCheckIn = useCallback(async () => {
    if (onCheckIn) {
      await onCheckIn();
    } else {
      toast.success("ç­¾åˆ°æˆåŠŸï¼");
    }
  }, [onCheckIn]);

  const handleToggleLever = useCallback(async (leverId: string) => {
    if (onToggleLever) {
      await onToggleLever(leverId);
    }
  }, [onToggleLever]);

  const handleToggleRock = useCallback(async (rockId: string) => {
    if (onToggleRock) {
      await onToggleRock(rockId);
    }
  }, [onToggleRock]);

  // è½¬æ¢ Dashboard æ•°æ®ä¸º Bento Dashboard æ ¼å¼
  const bentoDashboardData = useBentoDashboardData(dashboardData);

  return (
    <div className="flex flex-col h-full" data-skill={displaySkill}>
      {/* Messages area - always flex-1 to push input to bottom */}
      <main
        id="main-content"
        className="flex-1 overflow-y-auto relative min-h-0"
        role="log"
        aria-label="å¯¹è¯æ¶ˆæ¯"
        aria-live="polite"
      >
        {!hasMessages ? (
          /* Empty state with Welcome Message or Skill Intro */
          <div className="flex flex-col items-center justify-center h-full py-4 md:py-8">
            {/* é¦–æ¬¡ä½¿ç”¨æ—¶æ˜¾ç¤º Skill ä»‹ç»å¡ */}
            {showIntroCard && (
              <div className="w-full max-w-3xl px-4 mb-4">
                <SkillIntroCard
                  skillId={displaySkill}
                  variant="compact"
                  dismissible
                  onAction={handleIntroAction}
                />
              </div>
            )}

            {isDashboardLoading ? (
              <div className="flex items-center justify-center">
                <div className="text-sm text-muted-foreground">åŠ è½½ä¸­â€¦</div>
              </div>
            ) : bentoDashboardData ? (
              <BentoDashboard
                data={bentoDashboardData}
                className="w-full"
              />
            ) : (
              <div className="flex items-center justify-center">
                <div className="text-sm text-muted-foreground">æ¬¢è¿å›æ¥</div>
              </div>
            )}
          </div>
        ) : (
          /* Messages list - æ‰€æœ‰è®¾å¤‡å±…ä¸­ï¼ŒPCç«¯å“åº”å¼å¢å®½ */
          <div className="px-4 pt-4 pb-2 max-w-3xl xl:max-w-4xl 2xl:max-w-5xl mx-auto">
            {messages.map((message) => (
              // Use chat-message-item class for content-visibility optimization (Vercel rule: rendering-content-visibility)
              <div key={message.id} className="chat-message-item">
                <ChatMessage
                  role={message.role as "user" | "assistant"}
                  content={getMessageContent(message)}
                  skill={displaySkill}
                  isStreaming={isLoading && message.role === "assistant" && message === messages[messages.length - 1]}
                  onSendMessage={sendMessage}
                />
              </div>
            ))}
            {/* Show loading indicator when waiting for first response chunk */}
            {isLoading && messages.length > 0 && messages[messages.length - 1].role === "user" && (
              <ChatMessage
                role="assistant"
                content=""
                skill={displaySkill}
                isStreaming
              />
            )}
            {/* Error is now handled via toast notification in onError callback */}
            <div ref={messagesEndRef} />
          </div>
        )}
      </main>

      {/* Smart Suggestions + Input - fixed at bottom */}
      <div className="flex-shrink-0">
        {/* æ™ºèƒ½æ¨è - ä»…åœ¨ç©ºçŠ¶æ€æ—¶æ˜¾ç¤º */}
        {!hasMessages && (
          <SmartSuggestions
            dashboardData={dashboardData}
            onSelect={handleQuickPrompt}
          />
        )}
        <ChatInput onSend={handleSend} disabled={isLoading} />
      </div>
    </div>
  );
}

