"use client";

/**
 * useBentoDashboardData - å°† Dashboard æ•°æ®è½¬æ¢ä¸º BentoDashboard æ ¼å¼
 *
 * ä»åŸå§‹ DashboardDTO æå–å¹¶è½¬æ¢æ•°æ®ä¸º BentoDashboard æ‰€éœ€æ ¼å¼
 */

import { useMemo } from "react";
import type { DashboardDTO } from "@/types/dashboard";

export interface BentoDashboardData {
  // é—®å€™æ•°æ®
  greeting: string;
  solarTerm?: string;
  fortuneHint?: string;
  // Lifecoach æ•°æ®
  northStar?: string;
  weekRocks: Array<{
    id: string;
    text: string;
    role?: string;
    completed: boolean;
  }>;
  todayLevers: Array<{
    id: string;
    text: string;
    completed: boolean;
  }>;
  // è¿åŠ¿æ•°æ®
  fortuneScore?: number;
  insights?: string[];
}

export function useBentoDashboardData(
  dashboard: DashboardDTO | null | undefined
): BentoDashboardData | null {
  return useMemo(() => {
    if (!dashboard) return null;

    // æå– bazi skill æ•°æ®
    const baziCard = dashboard.mySkills.find((s) => s.skillId === "bazi");

    // æ„å»º insights
    const insights = baziCard?.content.insights
      ? Array.isArray(baziCard.content.insights)
        ? baziCard.content.insights.slice(0, 3)
        : []
      : [];

    // æ ¹æ®æ—¶é—´ç”Ÿæˆé—®å€™è¯­
    const hour = new Date().getHours();
    let greeting = "æ—©ä¸Šå¥½";
    if (hour >= 5 && hour < 12) {
      greeting = "æ—©ä¸Šå¥½";
    } else if (hour >= 12 && hour < 18) {
      greeting = "ä¸‹åˆå¥½";
    } else if (hour >= 18 && hour < 22) {
      greeting = "æ™šä¸Šå¥½";
    } else {
      greeting = "å¤œæ·±äº†";
    }

    // å¦‚æœæœ‰ç”¨æˆ·åï¼Œæ·»åŠ åˆ°é—®å€™è¯­
    // greeting = dashboard.ambient.greeting || greeting;

    return {
      // é—®å€™æ•°æ®
      greeting: dashboard.ambient.greeting || greeting,
      solarTerm: dashboard.ambient.solarTerm,
      fortuneHint: baziCard?.content.headline || "ä»Šå¤©é€‚åˆç¨³æ­¥å‰è¡Œ",

      // Lifecoach æ•°æ®
      northStar: dashboard.lifecoach.northStar,
      weekRocks: dashboard.lifecoach.weekRocks || [],
      todayLevers: dashboard.lifecoach.todayLevers || [],

      // è¿åŠ¿æ•°æ®
      fortuneScore: baziCard?.content.rating?.overall,
      insights,
    };
  }, [dashboard]);
}

