    47â†’// Token Management
    48â†’// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
    49â†’
    50â†’let accessToken: string | null = null;
    51â†’let refreshToken: string | null = null;
    52â†’
    53â†’export function setTokens(access: string, refresh: string) {
    54â†’  accessToken = access;
    55â†’  refreshToken = refresh;
    56â†’  if (typeof window !== "undefined") {
    57â†’    localStorage.setItem("access_token", access);
    58â†’    localStorage.setItem("refresh_token", refresh);
    59â†’  }
    60â†’}
    61â†’
    62â†’export function getTokens() {
    63â†’  if (typeof window !== "undefined") {
    64â†’    accessToken = localStorage.getItem("access_token");
    65â†’    refreshToken = localStorage.getItem("refresh_token");
    66â†’  }
    67â†’  return { accessToken, refreshToken };
    68â†’}
    69â†’
    70â†’export function clearTokens() {
    71â†’  accessToken = null;
    72â†’  refreshToken = null;
    73â†’  if (typeof window !== "undefined") {
    74â†’    localStorage.removeItem("access_token");
    75â†’    localStorage.removeItem("refresh_token");
    76â†’    localStorage.removeItem("user");
    77â†’  }
    78â†’}
    79â†’
    80â†’// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
    81â†’// API Helpers
    82â†’// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
    83â†’
    84â†’async function fetchAPI(
    85â†’  endpoint: string,
    86â†’  options: RequestInit = {}
    87â†’): Promise<Response> {
    88â†’  const { accessToken } = getTokens();
    89â†’
    90â†’  const headers: Record<string, string> = {
    91â†’    "Content-Type": "application/json",
    92â†’    ...options.headers as Record<string, string>,
    93â†’  };
    94â†’
    95â†’  if (accessToken) {
    96â†’    headers["Authorization"] = `Bearer ${accessToken}`;
    97â†’  }
    98â†’
    99â†’  const response = await fetch(`${API_BASE}${endpoint}`, {
   100â†’    ...options,
   101â†’    headers,
   102â†’  });
   103â†’
   104â†’  // Handle 401 - try to refresh token
   105â†’  if (response.status === 401) {
   106â†’    const { refreshToken } = getTokens();
   107â†’    if (refreshToken) {
   108â†’      const refreshed = await refreshAccessToken();
   109â†’      if (refreshed) {
   110â†’        // IMPORTANT: Get fresh token after refresh
   111â†’        const { accessToken: newAccessToken } = getTokens();
   112â†’        const newHeaders = {
   113â†’          ...headers,
   114â†’          "Authorization": `Bearer ${newAccessToken}`,
   115â†’        };
   116â†’        return fetch(`${API_BASE}${endpoint}`, { ...options, headers: newHeaders });
   117â†’      }
   118â†’    }
   119â†’  }
   120â†’
   121â†’  return response;
   122â†’}
   123â†’
   124â†’async function refreshAccessToken(): Promise<boolean> {
   125â†’  try {
   126â†’    const { refreshToken: currentRefreshToken } = getTokens();
   127â†’    if (!currentRefreshToken) {
   128â†’      return false;
   129â†’    }
   130â†’
   131â†’    const response = await fetch("/api/auth/refresh", {
   132â†’      method: "POST",
   133â†’      headers: { "Content-Type": "application/json" },
   134â†’      body: JSON.stringify({ refresh_token: currentRefreshToken }),
   135â†’    });
   136â†’
   137â†’    if (response.ok) {
