   651â†’
   652â†’// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   653â†’// API Client (axios-like interface)
   654â†’// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   655â†’
   656â†’interface ApiClientResponse<T = unknown> {
   657â†’  data: T;
   658â†’  status: number;
   659â†’}
   660â†’
   661â†’interface ApiClientRequestConfig {
   662â†’  params?: Record<string, unknown>;
   663â†’  headers?: Record<string, string>;
   664â†’}
   665â†’
   666â†’function buildUrlWithParams(url: string, params?: Record<string, unknown>): string {
   667â†’  if (!params) return url;
   668â†’
   669â†’  const searchParams = new URLSearchParams();
   670â†’  for (const [key, value] of Object.entries(params)) {
   671â†’    if (value !== undefined && value !== null) {
   672â†’      searchParams.append(key, String(value));
   673â†’    }
   674â†’  }
   675â†’
   676â†’  const queryString = searchParams.toString();
   677â†’  if (!queryString) return url;
   678â†’
   679â†’  return url + (url.includes('?') ? '&' : '?') + queryString;
   680â†’}
   681â†’
   682â†’async function apiRequest<T>(
   683â†’  method: string,
   684â†’  url: string,
   685â†’  data?: unknown,
   686â†’  config?: ApiClientRequestConfig
   687â†’): Promise<ApiClientResponse<T>> {
   688â†’  const fullUrl = buildUrlWithParams(url, config?.params);
   689â†’
   690â†’  const response = await fetchAPI(fullUrl, {
   691â†’    method,
   692â†’    headers: config?.headers,
   693â†’    body: data ? JSON.stringify(data) : undefined,
   694â†’  });
   695â†’
   696â†’  const responseData = await response.json().catch(() => ({}));
   697â†’
   698â†’  if (!response.ok) {
   699â†’    const error = new Error((responseData as { detail?: string }).detail || 'Request failed') as Error & {
   700â†’      response: { status: number; data: unknown };
   701â†’    };
   702â†’    error.response = { status: response.status, data: responseData };
   703â†’    throw error;
   704â†’  }
   705â†’
   706â†’  return { data: responseData as T, status: response.status };
   707â†’}
   708â†’
   709â†’export const apiClient = {
   710â†’  get<T = unknown>(url: string, config?: ApiClientRequestConfig): Promise<ApiClientResponse<T>> {
   711â†’    return apiRequest<T>('GET', url, undefined, config);
   712â†’  },
   713â†’
   714â†’  post<T = unknown>(url: string, data?: unknown, config?: ApiClientRequestConfig): Promise<ApiClientResponse<T>> {
   715â†’    return apiRequest<T>('POST', url, data, config);
   716â†’  },
   717â†’
   718â†’  put<T = unknown>(url: string, data?: unknown, config?: ApiClientRequestConfig): Promise<ApiClientResponse<T>> {
   719â†’    return apiRequest<T>('PUT', url, data, config);
   720â†’  },
   721â†’
   722â†’  patch<T = unknown>(url: string, data?: unknown, config?: ApiClientRequestConfig): Promise<ApiClientResponse<T>> {
   723â†’    return apiRequest<T>('PATCH', url, data, config);
   724â†’  },
   725â†’
   726â†’  delete<T = unknown>(url: string, config?: ApiClientRequestConfig): Promise<ApiClientResponse<T>> {
   727â†’    return apiRequest<T>('DELETE', url, undefined, config);
   728â†’  },
   729â†’};
