import { NextRequest, NextResponse } from "next/server";
import type { DashboardResponse } from "@/types/dashboard";

/**
 * Dashboard API Route - Proxy to Backend
 *
 * ä»åç«¯ /api/v1/dashboard è·å–çœŸå®ç”¨æˆ·æ•°æ®
 * ä¸ä½¿ç”¨ä»»ä½• Mock Data
 */

const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:8100";

export async function GET(request: NextRequest) {
  const authorization = request.headers.get("Authorization");

  console.log("[Dashboard API] Authorization header:", authorization ? "present" : "missing");

  if (!authorization) {
    return NextResponse.json(
      { success: false, error: "Unauthorized" },
      { status: 401 }
    );
  }

  try {
    console.log("[Dashboard API] Fetching from:", `${API_URL}/api/v1/dashboard`);
    const response = await fetch(`${API_URL}/api/v1/dashboard`, {
      method: "GET",
      headers: {
        Authorization: authorization,
        "Content-Type": "application/json",
      },
    });

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({}));
      return NextResponse.json(
        {
          success: false,
          error: errorData.detail || "Failed to fetch dashboard data",
        },
        { status: response.status }
      );
    }

    const data: DashboardResponse = await response.json();

    return NextResponse.json(data, {
      headers: {
        "Cache-Control": "private, max-age=60",
      },
    });
  } catch (error) {
    console.error("[Dashboard API] Error details:", error instanceof Error ? error.message : error);
    console.error("[Dashboard API] Full error:", error);
    return NextResponse.json(
      {
        success: false,
        error: "Internal server error",
      },
      { status: 500 }
    );
  }
}

