"use client";

/**
 * MobileTabBar - ÁßªÂä®Á´ØÂ∫ïÈÉ®ÂØºËà™Ê†è v9.0
 * Based on: vibelife Dashboard ‚Üí Chat Êï¥ÂêàÊñπÊ°à
 *
 * V9 ÂèòÊõ¥:
 * - ÂØºËà™ÂÖ•Âè£‰ªé 5 ‰∏™ÁÆÄÂåñ‰∏∫ 3 ‰∏™: Chat / Journey / Me
 * - Dashboard Â∑≤Êï¥ÂêàÂà∞ Chat ‰∏≠
 * - Discover ÁßªÂà∞ Me È°µÈù¢
 *
 * ‚îå‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚î¨‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚î¨‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îê
 * ‚îÇ    ÂØπËØù    ‚îÇ    ÊóÖÁ®ã    ‚îÇ    ÊàëÁöÑ    ‚îÇ
 * ‚îÇ     üí¨     ‚îÇ     üß≠     ‚îÇ     üë§     ‚îÇ
 * ‚îî‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚î¥‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚î¥‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îò
 */

import { cn } from "@/lib/utils";
import {
  MessageSquare,
  LayoutDashboard,
  Compass,
  Sparkles,
  User,
} from "lucide-react";
import type { LucideIcon } from "lucide-react";
import { type TabType } from "./AppShell";

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// Types & Config
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

interface MobileTabBarProps {
  activeTab: TabType;
  onTabChange: (tab: TabType) => void;
  className?: string;
}

interface TabItem {
  id: TabType;
  icon: LucideIcon;
  label: string;
  ariaLabel: string;
}

// V9.1: 4 tab items - Chat / Journey / Discover / Me
const TAB_ITEMS: TabItem[] = [
  { id: "chat", icon: MessageSquare, label: "ÂØπËØù", ariaLabel: "ÂØπËØùËÅäÂ§©" },
  { id: "journey", icon: Compass, label: "ÊóÖÁ®ã", ariaLabel: "ÊàëÁöÑÊóÖÁ®ã" },
  { id: "discover", icon: Sparkles, label: "Êé¢Á¥¢", ariaLabel: "Êé¢Á¥¢ Skills" },
  { id: "me", icon: User, label: "ÊàëÁöÑ", ariaLabel: "‰∏™‰∫∫‰∏≠ÂøÉ" },
];

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// MobileTabBar Component
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

export function MobileTabBar({ activeTab, onTabChange, className }: MobileTabBarProps) {
  return (
    <nav
      className={cn(
        "relative flex items-center justify-around",
        "h-16 bg-card/95 backdrop-blur-sm border-t border-border",
        "safe-area-pb", // iOS safe area
        className
      )}
      role="tablist"
      aria-label="‰∏ªÂØºËà™"
    >
      {TAB_ITEMS.map((item) => {
        const Icon = item.icon;
        const isActive = activeTab === item.id;

        return (
          <button
            key={item.id}
            onClick={() => onTabChange(item.id)}
            role="tab"
            aria-selected={isActive}
            aria-label={item.ariaLabel}
            className={cn(
              "relative flex-1 flex flex-col items-center justify-center gap-1 py-2",
              "transition-all duration-200",
              isActive
                ? "text-skill-primary"
                : "text-muted-foreground hover:text-foreground"
            )}
          >
            {/* Active indicator bar - top position */}
            {isActive && (
              <span
                className="absolute -top-0.5 left-1/2 -translate-x-1/2 w-8 h-1 rounded-full bg-gradient-to-r from-skill-primary to-skill-primary/70"
                aria-hidden="true"
              />
            )}

            {/* Icon with background on active */}
            <div
              className={cn(
                "relative flex items-center justify-center w-10 h-7 rounded-full transition-all duration-200",
                isActive && "bg-skill-primary/10"
              )}
            >
              <Icon
                className={cn(
                  "w-5 h-5 transition-transform duration-200",
                  isActive && "scale-110"
                )}
              />
            </div>

            {/* Label */}
            <span
              className={cn(
                "text-xs transition-all duration-200",
                isActive ? "font-semibold" : "font-medium"
              )}
            >
              {item.label}
            </span>
          </button>
        );
      })}
    </nav>
  );
}

