     1‚Üí"use client";
     2‚Üí
     3‚Üíimport { useState, useCallback } from "react";
     4‚Üíimport { cn } from "@/lib/utils";
     5‚Üíimport type { AmbientData, StatusData, Quote } from "@/types/dashboard";
     6‚Üíimport { getGreetingEmoji } from "@/lib/dashboard/greeting";
     7‚Üíimport {
     8‚Üí  MotionDiv,
     9‚Üí  MotionButton,
    10‚Üí  MotionSpan,
    11‚Üí  AnimatePresence,
    12‚Üí} from "../shared/MotionComponents";
    13‚Üí
    14‚Üíinterface AmbientHeaderProps {
    15‚Üí  ambient: AmbientData;
    16‚Üí  status: StatusData;
    17‚Üí  onCheckIn: () => Promise<unknown>;
    18‚Üí  className?: string;
    19‚Üí}
    20‚Üí
    21‚Üí/**
    22‚Üí * AmbientHeader - Dashboard Layer 0
    23‚Üí *
    24‚Üí * ÂåÖÂê´Ôºö
    25‚Üí * - Êó∂Èó¥ÈóÆÂÄô + Êó•Êúü/ËäÇÊ∞î
    26‚Üí * - Êô∫ËÉΩÂêçË®Ä
    27‚Üí * - Á≠æÂà∞Áä∂ÊÄÅ + ËøûÁª≠Â§©Êï∞ + ËÉΩÈáè
    28‚Üí */
    29‚Üíexport function AmbientHeader({
    30‚Üí  ambient,
    31‚Üí  status,
    32‚Üí  onCheckIn,
    33‚Üí  className,
    34‚Üí}: AmbientHeaderProps) {
    35‚Üí  const [isCheckingIn, setIsCheckingIn] = useState(false);
    36‚Üí  const [showCelebration, setShowCelebration] = useState(false);
    37‚Üí
    38‚Üí  const handleCheckIn = useCallback(async () => {
    39‚Üí    if (status.checkedIn || isCheckingIn) return;
    40‚Üí
    41‚Üí    setIsCheckingIn(true);
    42‚Üí    try {
    43‚Üí      await onCheckIn();
    44‚Üí      setShowCelebration(true);
    45‚Üí      setTimeout(() => setShowCelebration(false), 2000);
    46‚Üí    } finally {
    47‚Üí      setIsCheckingIn(false);
    48‚Üí    }
    49‚Üí  }, [onCheckIn, status.checkedIn, isCheckingIn]);
    50‚Üí
    51‚Üí  const emoji = getGreetingEmoji();
    52‚Üí
    53‚Üí  return (
    54‚Üí    <div
    55‚Üí      className={cn(
    56‚Üí        "rounded-2xl p-6 backdrop-blur-sm",
    57‚Üí        className
    58‚Üí      )}
    59‚Üí      style={{
    60‚Üí        background: 'var(--bg-card)',
    61‚Üí        border: '1px solid var(--border-subtle)',
    62‚Üí        boxShadow: 'var(--shadow-card)',
    63‚Üí      }}
    64‚Üí    >
    65‚Üí      {/* Header Row: Greeting + Date */}
    66‚Üí      <div className="flex items-center justify-between mb-4">
    67‚Üí        <h1 className="text-xl font-semibold" style={{ color: 'var(--text-primary)' }}>
    68‚Üí          {emoji} {ambient.greeting}
    69‚Üí        </h1>
    70‚Üí        <div className="text-sm" style={{ color: 'var(--text-tertiary)' }}>
    71‚Üí          {ambient.date}
    72‚Üí          {ambient.solarTerm && (
    73‚Üí            <span className="ml-2" style={{ color: 'var(--gold-600)' }}>{ambient.solarTerm}</span>
    74‚Üí          )}
    75‚Üí        </div>
    76‚Üí      </div>
    77‚Üí
    78‚Üí      {/* Quote */}
    79‚Üí      <QuoteDisplay quote={ambient.quote} />
    80‚Üí
    81‚Üí      {/* Status Bar */}
    82‚Üí      <StatusBar
    83‚Üí        status={status}
    84‚Üí        onCheckIn={handleCheckIn}
    85‚Üí        isCheckingIn={isCheckingIn}
    86‚Üí        showCelebration={showCelebration}
    87‚Üí      />
    88‚Üí    </div>
    89‚Üí  );
