"use client";

import { useState, useCallback } from "react";
import { cn } from "@/lib/utils";
import type { AmbientData, StatusData, Quote } from "@/types/dashboard";
import { getGreetingEmoji } from "@/lib/dashboard/greeting";
import {
  MotionDiv,
  MotionButton,
  MotionSpan,
  AnimatePresence,
} from "../shared/MotionComponents";

interface AmbientHeaderProps {
  ambient: AmbientData;
  status: StatusData;
  onCheckIn: () => Promise<unknown>;
  className?: string;
}

/**
 * AmbientHeader - Dashboard Layer 0
 *
 * ÂåÖÂê´Ôºö
 * - Êó∂Èó¥ÈóÆÂÄô + Êó•Êúü/ËäÇÊ∞î
 * - Êô∫ËÉΩÂêçË®Ä
 * - Á≠æÂà∞Áä∂ÊÄÅ + ËøûÁª≠Â§©Êï∞ + ËÉΩÈáè
 */
export function AmbientHeader({
  ambient,
  status,
  onCheckIn,
  className,
}: AmbientHeaderProps) {
  const [isCheckingIn, setIsCheckingIn] = useState(false);
  const [showCelebration, setShowCelebration] = useState(false);

  const handleCheckIn = useCallback(async () => {
    if (status.checkedIn || isCheckingIn) return;

    setIsCheckingIn(true);
    try {
      await onCheckIn();
      setShowCelebration(true);
      setTimeout(() => setShowCelebration(false), 2000);
    } finally {
      setIsCheckingIn(false);
    }
  }, [onCheckIn, status.checkedIn, isCheckingIn]);

  const emoji = getGreetingEmoji();

  return (
    <div
      className={cn(
        "rounded-2xl p-6 backdrop-blur-sm",
        className
      )}
      style={{
        background: 'var(--bg-card)',
        border: '1px solid var(--border-subtle)',
        boxShadow: 'var(--shadow-card)',
      }}
    >
      {/* Header Row: Greeting + Date */}
      <div className="flex items-center justify-between mb-4">
        <h1 className="text-xl font-semibold" style={{ color: 'var(--text-primary)' }}>
          {emoji} {ambient.greeting}
        </h1>
        <div className="text-sm" style={{ color: 'var(--text-tertiary)' }}>
          {ambient.date}
          {ambient.solarTerm && (
            <span className="ml-2" style={{ color: 'var(--gold-600)' }}>{ambient.solarTerm}</span>
          )}
        </div>
      </div>

      {/* Quote */}
      <QuoteDisplay quote={ambient.quote} />

      {/* Status Bar */}
      <StatusBar
        status={status}
        onCheckIn={handleCheckIn}
        isCheckingIn={isCheckingIn}
        showCelebration={showCelebration}
      />
    </div>
  );
}

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// Sub-components
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

function QuoteDisplay({ quote }: { quote: Quote }) {
  return (
    <div
      className="mb-4 py-3"
      style={{ borderTop: '1px solid var(--border-subtle)', borderBottom: '1px solid var(--border-subtle)' }}
    >
      <p style={{ color: 'var(--text-secondary)', fontStyle: 'italic' }}>
        &ldquo;{quote.text}&rdquo;
        {quote.author && (
          <span className="ml-2" style={{ color: 'var(--text-tertiary)', fontStyle: 'normal' }}>‚Äî {quote.author}</span>
        )}
      </p>
    </div>
  );
}

interface StatusBarProps {
  status: StatusData;
  onCheckIn: () => void;
  isCheckingIn: boolean;
  showCelebration: boolean;
}

function StatusBar({
  status,
  onCheckIn,
  isCheckingIn,
  showCelebration,
}: StatusBarProps) {
  const { streak, checkedIn, energy } = status;

  // Ê†πÊçÆËøûÁª≠Â§©Êï∞ÊòæÁ§∫ÁÅ´ÁÑ∞Êï∞Èáè
  const getFlameCount = (days: number) => {
    if (days >= 30) return 5;
    if (days >= 14) return 4;
    if (days >= 7) return 3;
    if (days >= 3) return 2;
    return 1;
  };

  const flames = "üî•".repeat(getFlameCount(streak));

  return (
    <div className="flex items-center justify-between">
      <div className="flex items-center gap-6">
        {/* Streak */}
        <div className="flex items-center gap-1.5">
          <AnimatePresence mode="wait">
            {showCelebration ? (
              <MotionSpan
                key="celebration"
                initial={{ scale: 1 }}
                animate={{ scale: [1, 1.3, 1] }}
                exit={{ scale: 1 }}
                className="text-lg"
              >
                {flames}
              </MotionSpan>
            ) : (
              <span className="text-lg">{flames}</span>
            )}
          </AnimatePresence>
          <span className="text-sm font-medium" style={{ color: 'var(--text-secondary)' }}>
            {streak}Â§©ËøûÁª≠
          </span>
        </div>

        {/* Energy */}
        {energy !== undefined && (
          <div className="flex items-center gap-1.5">
            <span className="text-lg">üí™</span>
            <span className="text-sm font-medium" style={{ color: 'var(--text-secondary)' }}>
              ËÉΩÈáè {energy}%
            </span>
          </div>
        )}

        {/* Checked In Status */}
        <div className="flex items-center gap-1.5">
          <span style={{ color: checkedIn ? 'var(--color-success)' : 'var(--text-disabled)' }}>
            {checkedIn ? "‚úì" : "‚ñ°"}
          </span>
          <span
            className="text-sm font-medium"
            style={{ color: checkedIn ? 'var(--color-success)' : 'var(--text-tertiary)' }}
          >
            {checkedIn ? "Â∑≤Á≠æÂà∞" : "Êú™Á≠æÂà∞"}
          </span>
        </div>
      </div>

      {/* Check-in Button */}
      {!checkedIn && (
        <MotionButton
          onClick={onCheckIn}
          disabled={isCheckingIn}
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          className="px-4 py-2 rounded-lg text-sm font-medium text-white disabled:opacity-50 disabled:cursor-not-allowed"
          style={{
            background: 'linear-gradient(135deg, var(--accent-primary) 0%, var(--accent-secondary) 100%)',
            boxShadow: 'var(--shadow-soft)',
          }}
        >
          {isCheckingIn ? (
            <span className="flex items-center gap-2">
              <MotionSpan
                animate={{ rotate: 360 }}
                transition={{ repeat: Infinity, duration: 1, ease: "linear" }}
              >
                ‚óè
              </MotionSpan>
              Á≠æÂà∞‰∏≠...
            </span>
          ) : (
            "ÂºÄÂßã‰ªäÂ§©"
          )}
        </MotionButton>
      )}

      {/* Celebration Animation */}
      <AnimatePresence>
        {showCelebration && (
          <MotionDiv
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            className="fixed inset-0 flex items-center justify-center pointer-events-none z-50"
          >
            <div
              className="rounded-2xl p-8 text-center"
              style={{
                background: 'rgba(255, 255, 255, 0.95)',
                backdropFilter: 'blur(8px)',
                boxShadow: 'var(--shadow-elevated)',
              }}
            >
              <MotionDiv
                animate={{ scale: [1, 1.2, 1] }}
                transition={{ duration: 0.5 }}
                className="text-4xl mb-2"
              >
                üî• {streak}Â§©
              </MotionDiv>
              <p style={{ color: 'var(--text-secondary)' }}>Â§™Ê£í‰∫ÜÔºÅÊñ∞ÁöÑ‰∏ÄÂ§©ÂºÄÂßã‰∫ÜÔºÅ</p>
            </div>
          </MotionDiv>
        )}
      </AnimatePresence>
    </div>
  );
}

export default AmbientHeader;

