"use client";

import { memo } from "react";
import { cn } from "@/lib/utils";
import type { LifecoachData, LeverItem as LeverItemType, RockItem as RockItemType } from "@/types/dashboard";
import { MotionDiv, MotionButton, MotionLi } from "../shared/MotionComponents";

interface LifecoachCardProps {
  data: LifecoachData;
  onToggleLever: (leverId: string) => Promise<void>;
  onToggleRock: (rockId: string) => Promise<void>;
  onClickNorthStar?: () => void;
  className?: string;
}

/**
 * LifecoachCard - Dashboard Layer 1
 *
 * ÂêàÂπ∂ÁöÑ Lifecoach Âç°ÁâáÔºåÂåÖÂê´Ôºö
 * - ÂåóÊûÅÊòüÊÑøÊôØ
 * - ÊúàÂ∫¶È°πÁõÆËøõÂ∫¶
 * - ‰ªäÊó•Êù†ÊùÜ
 * - Êú¨Âë®Â§ßÁü≥Â§¥
 */
export function LifecoachCard({
  data,
  onToggleLever,
  onToggleRock,
  onClickNorthStar,
  className,
}: LifecoachCardProps) {
  const { northStar, monthlyProject, todayLevers, weekRocks } = data;

  const completedLevers = todayLevers.filter((l) => l.completed).length;
  const completedRocks = weekRocks.filter((r) => r.completed).length;

  return (
    <div
      className={cn(
        "rounded-2xl p-6 backdrop-blur-sm",
        className
      )}
      style={{
        background: 'var(--bg-card)',
        border: '1px solid var(--border-subtle)',
        boxShadow: 'var(--shadow-card)',
      }}
    >
      {/* Header */}
      <div className="flex items-center gap-2 mb-4">
        <span className="text-xl">üß≠</span>
        <h2 className="text-lg font-semibold" style={{ color: 'var(--text-primary)' }}>LIFECOACH</h2>
      </div>

      {/* North Star */}
      {northStar && (
        <NorthStarSection
          vision={northStar}
          onClick={onClickNorthStar}
        />
      )}

      {/* Main Grid: Monthly Project + Today Levers */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
        {/* Monthly Project */}
        {monthlyProject && (
          <MonthlyProgressSection project={monthlyProject} />
        )}

        {/* Today Levers */}
        <TodayLeversSection
          levers={todayLevers}
          completed={completedLevers}
          total={todayLevers.length}
          onToggle={onToggleLever}
        />
      </div>

      {/* Week Rocks */}
      <WeekRocksSection
        rocks={weekRocks}
        completed={completedRocks}
        total={weekRocks.length}
        onToggle={onToggleRock}
      />
    </div>
  );
}

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// Sub-components
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

interface NorthStarSectionProps {
  vision: string;
  onClick?: () => void;
}

function NorthStarSection({ vision, onClick }: NorthStarSectionProps) {
  return (
    <button
      onClick={onClick}
      className="w-full text-left mb-4 p-3 rounded-lg transition-colors focus:outline-none"
      style={{
        background: 'linear-gradient(135deg, var(--vellum-100) 0%, var(--gold-100) 100%)',
        border: '1px solid var(--vellum-300)',
      }}
    >
      <div className="flex items-start gap-2">
        <span className="text-lg">‚≠ê</span>
        <div>
          <span className="text-sm font-medium" style={{ color: 'var(--gold-700)' }}>ÂåóÊûÅÊòü</span>
          <p style={{ color: 'var(--text-secondary)' }} className="mt-0.5">&ldquo;{vision}&rdquo;</p>
        </div>
      </div>
    </button>
  );
}

interface MonthlyProgressSectionProps {
  project: NonNullable<LifecoachData["monthlyProject"]>;
}

function MonthlyProgressSection({ project }: MonthlyProgressSectionProps) {
  const { name, progress, daysRemaining } = project;

  return (
    <div
      className="p-4 rounded-lg"
      style={{
        background: 'var(--bg-secondary)',
        border: '1px solid var(--border-subtle)',
      }}
    >
      <div className="flex items-center gap-2 mb-2">
        <span className="text-lg">üìä</span>
        <h3 className="text-sm font-medium" style={{ color: 'var(--text-tertiary)' }}>ÊúàÂ∫¶È°πÁõÆ</h3>
      </div>
      <p className="font-medium mb-3" style={{ color: 'var(--text-primary)' }}>{name}</p>

      {/* Progress Bar */}
      <div className="mb-2">
        <div className="h-2 rounded-full overflow-hidden" style={{ background: 'var(--vellum-200)' }}>
          <MotionDiv
            initial={{ scaleX: 0 }}
            animate={{ scaleX: 1 }}
            transition={{ duration: 0.8, ease: [0.4, 0, 0.2, 1] }}
            style={{
              width: `${progress}%`,
              transformOrigin: "left",
              background: progress >= 70
                ? 'var(--color-success)'
                : progress >= 40
                ? 'var(--gold-500)'
                : 'var(--color-info)',
            }}
            className="h-full rounded-full"
          />
        </div>
        <div className="flex justify-between mt-1 text-xs" style={{ color: 'var(--text-tertiary)' }}>
          <span>{progress}%</span>
          <span>ËøòÂâ© {daysRemaining} Â§©</span>
        </div>
      </div>
    </div>
  );
}

interface TodayLeversSectionProps {
  levers: LeverItemType[];
  completed: number;
  total: number;
  onToggle: (leverId: string) => Promise<void>;
}

function TodayLeversSection({
  levers,
  completed,
  total,
  onToggle,
}: TodayLeversSectionProps) {
  const allCompleted = completed === total && total > 0;

  return (
    <div
      className="p-4 rounded-lg"
      style={{
        background: 'var(--bg-secondary)',
        border: '1px solid var(--border-subtle)',
      }}
    >
      <div className="flex items-center gap-2 mb-2">
        <span className="text-lg">‚òÄÔ∏è</span>
        <h3 className="text-sm font-medium" style={{ color: 'var(--text-tertiary)' }}>‰ªäÊó•Êù†ÊùÜ</h3>
        {allCompleted && <span className="text-lg">üéâ</span>}
      </div>

      <ul className="space-y-2 mb-3">
        {levers.map((lever) => (
          <LeverItem
            key={lever.id}
            lever={lever}
            onToggle={() => onToggle(lever.id)}
          />
        ))}
      </ul>

      <div className="pt-2" style={{ borderTop: '1px solid var(--border-subtle)' }}>
        <span className="text-sm" style={{ color: 'var(--text-tertiary)' }}>
          ËøõÂ∫¶: {completed}/{total}
        </span>
      </div>
    </div>
  );
}

interface LeverItemProps {
  lever: LeverItemType;
  onToggle: () => void;
}

const LeverItem = memo(function LeverItem({ lever, onToggle }: LeverItemProps) {
  return (
    <MotionLi
      whileTap={{ scale: 0.98 }}
      className="flex items-center gap-2"
    >
      <button
        onClick={onToggle}
        className="flex items-center gap-2 w-full text-left p-1.5 rounded transition-colors focus:outline-none"
        style={{ background: 'transparent' }}
      >
        <span
          className="w-5 h-5 flex items-center justify-center rounded border"
          style={{
            background: lever.completed ? 'var(--color-success)' : 'transparent',
            borderColor: lever.completed ? 'var(--color-success)' : 'var(--border-strong)',
            color: lever.completed ? 'white' : 'transparent',
          }}
        >
          {lever.completed && "‚úì"}
        </span>
        <span
          className="text-sm"
          style={{
            color: lever.completed ? 'var(--text-disabled)' : 'var(--text-secondary)',
            textDecoration: lever.completed ? 'line-through' : 'none',
          }}
        >
          {lever.text}
        </span>
      </button>
    </MotionLi>
  );
});

interface WeekRocksSectionProps {
  rocks: RockItemType[];
  completed: number;
  total: number;
  onToggle: (rockId: string) => Promise<void>;
}

function WeekRocksSection({
  rocks,
  completed,
  total,
  onToggle,
}: WeekRocksSectionProps) {
  return (
    <div className="pt-4" style={{ borderTop: '1px solid var(--border-subtle)' }}>
      <div className="flex items-center justify-between mb-3">
        <div className="flex items-center gap-2">
          <span className="text-lg">üìÖ</span>
          <h3 className="text-sm font-medium" style={{ color: 'var(--text-tertiary)' }}>Êú¨Âë®Â§ßÁü≥Â§¥</h3>
        </div>
        <span className="text-sm" style={{ color: 'var(--text-tertiary)' }}>
          ({completed}/{total})
        </span>
      </div>

      <div className="flex flex-wrap gap-2">
        {rocks.map((rock) => (
          <RockChip
            key={rock.id}
            rock={rock}
            onToggle={() => onToggle(rock.id)}
          />
        ))}
      </div>
    </div>
  );
}

interface RockChipProps {
  rock: RockItemType;
  onToggle: () => void;
}

const RockChip = memo(function RockChip({ rock, onToggle }: RockChipProps) {
  return (
    <MotionButton
      onClick={onToggle}
      whileHover={{ scale: 1.02 }}
      whileTap={{ scale: 0.98 }}
      className="px-3 py-2 rounded-lg text-sm border transition-colors focus:outline-none"
      style={{
        background: rock.completed ? 'var(--vellum-100)' : 'var(--bg-card)',
        borderColor: rock.completed ? 'var(--vellum-400)' : 'var(--border-default)',
        color: rock.completed ? 'var(--gold-700)' : 'var(--text-secondary)',
      }}
    >
      <span className="mr-1.5">
        {rock.completed ? "‚úì" : "‚óã"}
      </span>
      {rock.text}
      <span className="ml-1.5 text-xs" style={{ color: 'var(--text-disabled)' }}>({rock.role})</span>
    </MotionButton>
  );
});

export default LifecoachCard;

