"use client";

import { useState, useCallback } from "react";
import { cn } from "@/lib/utils";
import type { StatusData } from "@/types/dashboard";

interface AmbientStatusBarProps {
  status: StatusData;
  onCheckIn: () => Promise<unknown>;
  className?: string;
}

/**
 * AmbientStatusBar - Chat Á©∫Áä∂ÊÄÅ‰∏≠ÁöÑÁÆÄÂåñÁâà Ambient
 *
 * Ê®™ÂêëÂ±ïÁ§∫Ê†∏ÂøÉÊåáÊ†áÔºöËøûÁª≠Â§©Êï∞„ÄÅËÉΩÈáè„ÄÅÁ≠æÂà∞Áä∂ÊÄÅ
 * ËûçÂêàÂà∞ Chat Á©∫Áä∂ÊÄÅÔºå‰∏çÈáçÂ§çÊòæÁ§∫ÈóÆÂÄôÂíåÂêçË®Ä
 */
export function AmbientStatusBar({
  status,
  onCheckIn,
  className,
}: AmbientStatusBarProps) {
  const [isCheckingIn, setIsCheckingIn] = useState(false);

  const handleCheckIn = useCallback(async () => {
    if (status.checkedIn || isCheckingIn) return;

    setIsCheckingIn(true);
    try {
      await onCheckIn();
    } finally {
      setIsCheckingIn(false);
    }
  }, [onCheckIn, status.checkedIn, isCheckingIn]);

  const { streak, checkedIn, energy } = status;

  // Ê†πÊçÆËøûÁª≠Â§©Êï∞ÊòæÁ§∫ÁÅ´ÁÑ∞Êï∞Èáè
  const getFlameCount = (days: number) => {
    if (days >= 30) return 5;
    if (days >= 14) return 4;
    if (days >= 7) return 3;
    if (days >= 3) return 2;
    return 1;
  };

  const flames = "üî•".repeat(getFlameCount(streak));

  return (
    <div
      className={cn(
        "flex items-center justify-between px-4 py-3 rounded-lg",
        className
      )}
      style={{
        background: 'var(--bg-card)',
        border: '1px solid var(--border-subtle)',
      }}
    >
      {/* Â∑¶‰æßÔºöÁä∂ÊÄÅÊåáÊ†á */}
      <div className="flex items-center gap-6">
        {/* Streak */}
        <div className="flex items-center gap-1.5">
          <span className="text-lg">{flames}</span>
          <span className="text-sm font-medium" style={{ color: 'var(--text-secondary)' }}>
            {streak}Â§©ËøûÁª≠
          </span>
        </div>

        {/* Energy */}
        {energy !== undefined && (
          <div className="flex items-center gap-1.5">
            <span className="text-lg">üí™</span>
            <span className="text-sm font-medium" style={{ color: 'var(--text-secondary)' }}>
              ËÉΩÈáè {energy}%
            </span>
          </div>
        )}

        {/* Checked In Status */}
        <div className="flex items-center gap-1.5">
          <span style={{ color: checkedIn ? 'var(--color-success)' : 'var(--text-disabled)' }}>
            {checkedIn ? "‚úì" : "‚ñ°"}
          </span>
          <span
            className="text-sm font-medium"
            style={{ color: checkedIn ? 'var(--color-success)' : 'var(--text-tertiary)' }}
          >
            {checkedIn ? "Â∑≤Á≠æÂà∞" : "Êú™Á≠æÂà∞"}
          </span>
        </div>
      </div>

      {/* Âè≥‰æßÔºöÁ≠æÂà∞ÊåâÈíÆ */}
      {!checkedIn && (
        <button
          onClick={handleCheckIn}
          disabled={isCheckingIn}
          className="px-4 py-1.5 rounded-lg text-sm font-medium text-white disabled:opacity-50 disabled:cursor-not-allowed transition-all hover:scale-105"
          style={{
            background: 'linear-gradient(135deg, var(--accent-primary) 0%, var(--accent-secondary) 100%)',
            boxShadow: 'var(--shadow-soft)',
          }}
        >
          {isCheckingIn ? "Á≠æÂà∞‰∏≠..." : "ÂºÄÂßã‰ªäÂ§©"}
        </button>
      )}
    </div>
  );
}

