// Êú¨Âú∞ fallback Êï∞ÊçÆÁîüÊàê
function getLocalGreetingData(skill: SkillType) {
  const now = new Date();
  const hour = now.getHours();
  const month = now.getMonth();
  const day = now.getDate();
  const isoDate = now.toISOString().slice(0, 10);

  // Á°ÆÂÆöÊó∂ÊÆµ
  type TimeOfDay = "dawn" | "morning" | "afternoon" | "evening" | "night";
  let timeOfDay: TimeOfDay = "morning";
  let greeting = "Êñ∞ÁöÑ‰∏ÄÂ§©ÂºÄÂßã‰∫Ü";

  if (hour >= 5 && hour < 7) {
    timeOfDay = "dawn";
    greeting = "Êô®ÂÖâÁÜπÂæÆ";
  } else if (hour >= 7 && hour < 12) {
    timeOfDay = "morning";
    greeting = "ÊÑø‰Ω†ËÉΩÈáèÊª°Êª°";
  } else if (hour >= 12 && hour < 18) {
    timeOfDay = "afternoon";
    greeting = "‰øùÊåÅ‰∏ìÊ≥®";
  } else if (hour >= 18 && hour < 21) {
    timeOfDay = "evening";
    greeting = "ËæõËã¶‰∏ÄÂ§©‰∫Ü";
  } else {
    timeOfDay = "night";
    greeting = "Â§úÊ∑±‰∫ÜÔºåÊó©ÁÇπ‰ºëÊÅØ";
