  const solarTerms = [
    "Â∞èÂØí", "Â§ßÂØí", "Á´ãÊò•", "Èõ®Ê∞¥", "ÊÉäËõ∞", "Êò•ÂàÜ",
    "Ê∏ÖÊòé", "Ë∞∑Èõ®", "Á´ãÂ§è", "Â∞èÊª°", "ËäíÁßç", "Â§èËá≥",
    "Â∞èÊöë", "Â§ßÊöë", "Á´ãÁßã", "Â§ÑÊöë", "ÁôΩÈú≤", "ÁßãÂàÜ",
    "ÂØíÈú≤", "ÈúúÈôç", "Á´ãÂÜ¨", "Â∞èÈõ™", "Â§ßÈõ™", "ÂÜ¨Ëá≥",
  ];
  const termIdx = month * 2 + (day >= 15 ? 1 : 0);
  const solarTerm = solarTerms[termIdx % 24];

  const seasonTips: Record<string, string> = {
    Â∞èÂØí: "ÂÜ¨ËóèÊó∂ËäÇÔºåÂà©‰∫éÂõûÈ°æ‰∏éËßÑÂàí", Â§ßÂØí: "‰∏•ÂØí‰πãÈôÖÔºåËìÑÂäõÂæÖÂèë",
    Á´ãÊò•: "‰∏áÁâ©Â§çËãèÔºåÈÄÇÂêàÂêØÂä®Êñ∞ËÆ°Âàí", Èõ®Ê∞¥: "Ê∂¶Áâ©Êó†Â£∞ÔºåÈùôÂøÉÊªãÂÖª",
    ÊÉäËõ∞: "Êò•Èõ∑ÊÉäÈÜíÔºåË°åÂä®Ëµ∑Êù•", Êò•ÂàÜ: "ÊòºÂ§úÂπ≥ÂàÜÔºåË∞ÉÂíåË∫´ÂøÉ",
    Ê∏ÖÊòé: "Â§©Ê∏ÖÂú∞ÊòéÔºåÊï¥ÁêÜÊÄùÁª™", Ë∞∑Èõ®: "Èõ®ÁîüÁôæË∞∑ÔºåÊí≠ÁßçÂ∏åÊúõ",
    Á´ãÂ§è: "Â§èÊ∞îÊ∏êÁõõÔºå‰øùÊåÅÊ¥ªÂäõ", Â∞èÊª°: "Â∞èÊúâÊâÄÊàêÔºåÁªßÁª≠Âä™Âäõ",
    ËäíÁßç: "Êí≠ÁßçÊî∂Ëé∑ÔºåÂã§Âãâ‰∏çÊáà", Â§èËá≥: "Èò≥Ê∞îÈºéÁõõÔºåÈÄÇÂ∫¶‰ºëÊÅØ",
    Â∞èÊöë: "ÊöëÊ∞îÂàùÂçáÔºåÊ∏ÖÂøÉÂÆÅÁ•û", Â§ßÊöë: "ÈÖ∑ÊöëÈöæËÄêÔºåÈùô‰ª•ÂÖªÂøÉ",
    Á´ãÁßã: "ÁßãÊÑèÊ∏êËµ∑ÔºåÊî∂Ëé∑Êó∂ËäÇ", Â§ÑÊöë: "ÊöëÊ∞îÊ∏êÊ∂àÔºåË∞ÉÊï¥ËäÇÂ•è",
    ÁôΩÈú≤: "ÁßãÂáâÊ∏êÊµìÔºåÊî∂ÊïõÂøÉÁ•û", ÁßãÂàÜ: "ÁßãÈ´òÊ∞îÁàΩÔºåÂπ≥Ë°°Â∑•‰Ωú‰∏éÁîüÊ¥ª",
    ÂØíÈú≤: "Èú≤Ê∞¥Ê∏êÂØíÔºåÊ≥®ÊÑè‰øùÂÖª", ÈúúÈôç: "ÈúúÈôçÊ∞¥Ê≥ΩÔºåÊ≤âÊ∑ÄÁßØÁ¥Ø",
    Á´ãÂÜ¨: "ÂÜ¨Â≠£Êù•‰∏¥ÔºåÂÇ®Â§áËÉΩÈáè", Â∞èÈõ™: "Èõ™Ëä±ÂàùÈôçÔºåÂÜÖÁúÅÊó∂Âàª",
    Â§ßÈõ™: "ÁëûÈõ™ÂÖÜ‰∏∞ÔºåÈùôÂæÖÊù•Âπ¥", ÂÜ¨Ëá≥: "‰∏ÄÈò≥ÂàùÁîüÔºåÂê¶ÊûÅÊ≥∞Êù•",
  };

  const baziHints: Record<string, string> = {
    Â∞èÂØí: "Ê∞¥Êó∫‰πãÂ≠£ÔºåÂà©‰∫éÊÄùËÄÉ‰∏éËßÑÂàí", Â§ßÂØí: "Ê∞¥Ê∞îÊúÄÁõõÔºåÈÄÇÂêàÂÜÖÁúÅÊ≤âÊ∑Ä",
    Á´ãÊò•: "Êú®Ê∞îÂàùÁîüÔºåÂÆúÂºÄÂêØÊñ∞‰∫ã‰∏ö", Êò•ÂàÜ: "Êú®Ê∞îÊó∫ÁõõÔºåÈÄÇÂêàÊãìÂ±ï‰∫∫ËÑâ",
    Á´ãÂ§è: "ÁÅ´Ê∞îÊ∏êÂçáÔºåÂà©‰∫éË°®ËææÂ±ïÁé∞", Â§èËá≥: "ÁÅ´Ê∞îÊ≠£ÁõõÔºåÊ≥®ÊÑèÂπ≥Ë°°",
    Á´ãÁßã: "ÈáëÊ∞îÂàùÁîüÔºåÈÄÇÂêàÂÜ≥Êñ≠ÂèñËàç", ÁßãÂàÜ: "ÈáëÊ∞îÊó∫ÁõõÔºåÂà©‰∫éÊî∂Ëé∑ÊàêÊûú",
    Á´ãÂÜ¨: "Ê∞¥Ê∞îÂàùÁîüÔºåÂÆú‰ºëÂÖªÁîüÊÅØ", ÂÜ¨Ëá≥: "‰∏ÄÈò≥Â§çÂßãÔºåÂéöÁßØËñÑÂèë",
  };

  const dateStr = `${month + 1}Êúà${day}Êó•`;
  const seedStr = `${isoDate}:${skill}:${solarTerm}:${timeOfDay}`;
  let hash = 0;
  for (let i = 0; i < seedStr.length; i += 1) {
    hash = (hash * 31 + seedStr.charCodeAt(i)) % 100000;
  }
  const fortuneScore = Math.max(32, Math.min(92, 42 + (hash % 51)));
  const fortuneHint = skill === "bazi"
    ? (baziHints[solarTerm] || "‰ªäÂ§©ÈÄÇÂêàÁ®≥‰ΩèËäÇÂ•èÔºåÊääÂäõÊ∞îÁî®Âú®ÂÖ≥ÈîÆÂ§Ñ„ÄÇ")
    : (seasonTips[solarTerm] || "‰ªäÂ§©ÈÄÇÂêàÊääÊ≥®ÊÑèÂäõÊî∂ÂõûÂà∞Ëá™Â∑±Ë∫´‰∏ä„ÄÇ");

  const actionText =
    timeOfDay === "dawn" || timeOfDay === "morning"
      ? "ÂÜô‰∏ã‰ªäÂ§©ÊúÄÈáçË¶ÅÁöÑ‰∏Ä‰ª∂‰∫ãÔºåÂπ∂Áî® 25 ÂàÜÈíüÂêØÂä®ÂÆÉ"
      : timeOfDay === "afternoon"
        ? "Êää‰∏Ä‰ª∂ÊÇ¨ËÄåÊú™ÂÜ≥ÁöÑÂ∞è‰∫ãÊî∂Â∞æÔºåÁªôËá™Â∑±‰∏Ä‰∏™Á°ÆÂÆöÊÑü"
        : timeOfDay === "evening"
          ? "Áî® 10 ÂàÜÈíüÂ§çÁõò‰ªäÂ§©ÔºöÂÅöÂØπ‰∫Ü‰ªÄ‰πà„ÄÅ‰∏ã‰∏ÄÊ≠•ÊòØ‰ªÄ‰πà"
          : "ÂÖ≥Êú∫ÂâçÂÜô‰∏ÄÂè•ÊÑüË∞¢ÔºåÁªôÂ§ßËÑë‰∏Ä‰∏™ÊüîËΩØÁöÑÁªìÂ∞æ";

  return {
    greeting, solarTerm, timeOfDay, date: dateStr, isoDate,
    todayTip: seasonTips[solarTerm] || "‰∏ìÊ≥®ÂΩì‰∏ãÔºåÁ®≥Ê≠•ÂâçË°å",
    baziHint: skill === "bazi" ? (baziHints[solarTerm] || null) : null,
    fortuneScore, fortuneHint, actionText, isFromApi: false,
  };
}

// ÁîüÊàêÂΩìÊó•ÈóÆÂÄôÊï∞ÊçÆ - ‰ΩøÁî®Êú¨Âú∞ÁÆóÊ≥ïÁîüÊàê
// NOTE: Âéü API Ë∞ÉÁî®Â∑≤ÁßªÈô§ÔºåÂõ†ÂêéÁ´Ø /api/v1/fortune/greeting Êú™ÂÆûÁé∞
// Êú¨Âú∞ÁÆóÊ≥ïÂ∑≤Êèê‰æõÂÆåÊï¥ÂäüËÉΩÔºöËäÇÊ∞î„ÄÅÊó∂Ëæ∞„ÄÅËøêÂäøËØÑÂàÜÁ≠â
function useDailyGreeting(skill: SkillType) {
  // ÂàùÂßãÁä∂ÊÄÅ‰ΩøÁî®ÈªòËÆ§ÂÄºÔºåÈÅøÂÖç hydration mismatch
  const [data, setData] = useState(() => ({
    greeting: "Êñ∞ÁöÑ‰∏ÄÂ§©ÂºÄÂßã‰∫Ü",
    timeOfDay: "morning" as "dawn" | "morning" | "afternoon" | "evening" | "night",
    solarTerm: "Á´ãÊò•",
    isoDate: new Date().toISOString().slice(0, 10),
    todayTip: "‰∏ìÊ≥®ÂΩì‰∏ãÔºåÁ®≥Ê≠•ÂâçË°å",
    baziHint: null as string | null,
    fortuneScore: 75,
    fortuneHint: "ËøêÂäøÂπ≥Á®≥",
    actionText: "‰ªäÊó•ÂÆú",
    isFromApi: false,
  }));
  const [loading] = useState(false);

  // Âú®ÂÆ¢Êà∑Á´ØËÆ°ÁÆóÂÆûÈôÖÁöÑÈóÆÂÄôÊï∞ÊçÆ
  useEffect(() => {
    setData(getLocalGreetingData(skill));
  }, [skill]);

  return { ...data, loading };
}

// Memoized ChatEmptyState (Vercel rule: rerender-extract-component)
const ChatEmptyState = memo(function ChatEmptyState({ skill, onQuickPrompt }: { skill: SkillType; onQuickPrompt?: (prompt: string) => void }) {
  const config = SKILL_EMPTY_STATE[skill] || SKILL_EMPTY_STATE.bazi;
  const quickPrompts = SKILL_QUICK_PROMPTS[skill] || SKILL_QUICK_PROMPTS.bazi;
  const dailyData = useDailyGreeting(skill);
  const actionKey = useMemo(
    () => `vibelife-daily-action:${skill}:${dailyData.isoDate}`,
    [skill, dailyData.isoDate]
  );
  const [actionCompleted, setActionCompleted] = useState(false);

  useEffect(() => {
    if (typeof window === "undefined") return;
    // Use cached localStorage (Vercel rule: js-cache-storage)
    setActionCompleted(getLocalStorage(actionKey) === "1");
  }, [actionKey]);

  const toggleAction = useCallback(() => {
    setActionCompleted((prev) => {
      const next = !prev;
      // Use cached localStorage (Vercel rule: js-cache-storage)
      setLocalStorage(actionKey, next ? "1" : "0");
      return next;
    });
  }, [actionKey]);

  const shareText = useMemo(() => {
    const term = dailyData.solarTerm ? ` ¬∑ ${dailyData.solarTerm}` : "";
    return [
      `VibeLife${term} ¬∑ ${dailyData.date}`,
      dailyData.greeting,
      `ËøêÂäøÊåáÊï∞Ôºö${dailyData.fortuneScore}/100`,
      `‰ªäÊó•‰∏ÄÊ≠•Ôºö${dailyData.actionText}`,
      "#VibeLife",
    ].join("\n");
  }, [dailyData]);

  return (
    <div className="flex flex-col items-center justify-center w-full md:max-w-xl mx-auto px-4 py-6">
      {/* Daily Greeting ÊØèÊó•ÈóÆÂÄô */}
      <DailyGreeting
        greeting={dailyData.greeting}
        solarTerm={dailyData.solarTerm}
        timeOfDay={dailyData.timeOfDay}
        date={dailyData.date}
        todayTip={dailyData.todayTip}
        baziHint={dailyData.baziHint || undefined}
        fortuneScore={dailyData.fortuneScore}
        fortuneHint={dailyData.fortuneHint}
        actionItem={{ text: dailyData.actionText, completed: actionCompleted }}
        onActionToggle={toggleAction}
        shareText={shareText}
        className="mb-6 w-full"
      />

      {/* Animated glyph with enhanced aura */}
      <div className="relative flex items-center justify-center mb-4">
        {/* Outer pulsing ring */}
        <div className="absolute w-16 h-16 rounded-full border border-skill-primary/20 animate-pulse-slow" />
        <div className="absolute w-24 h-24 rounded-full border border-skill-primary/10 animate-pulse-slower" />

        {/* Breath aura */}
        <BreathAura
          skill={skill}
          size="sm"
          position="center"
          intensity="medium"
          className="opacity-40"
        />

        {/* Central glyph */}
        <VibeGlyph
          size="sm"
          skill={skill}
          showAura={true}
          animate={true}
          className="relative z-10"
        />
      </div>

      {/* Title */}
      <h3 className="text-base md:text-lg font-serif font-semibold text-foreground mb-1 text-center">
        {config.title}
      </h3>

      {/* Subtitle */}
      <p className="text-sm text-muted-foreground max-w-sm text-center mb-4">
        {config.subtitle}
      </p>

      {/* Quick prompt suggestions */}
      <div className="w-full space-y-3">
        <p className="text-xs text-muted-foreground/60 text-center">
          ËØïËØïËøôÊ†∑ÈóÆ
        </p>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
          {quickPrompts.slice(0, 4).map((prompt, index) => (
            <button
              key={index}
              onClick={() => onQuickPrompt?.(prompt.question)}
              className={`
                group relative px-3 py-3 text-left
                bg-gradient-to-br ${prompt.gradient}
                border border-border/50 hover:border-skill-primary/30
                rounded-xl transition-all duration-200
                hover:shadow-md hover:-translate-y-0.5
              `}
            >
              <div className="flex items-start gap-2.5">
                <span className="text-lg flex-shrink-0">{prompt.icon}</span>
                <div className="flex-1 min-w-0">
                  <span className="text-[10px] font-medium text-muted-foreground/70 uppercase tracking-wide">
                    {prompt.category}
                  </span>
                  <p className="text-sm text-foreground line-clamp-2 mt-0.5">
                    {prompt.question}
                  </p>
                </div>
              </div>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
});

export function ChatContainer({
  skillId,
  scenario,
  skill = "bazi",
  conversationId,
  voiceMode = "warm",
  onConversationStart,
  initialPrompt,
  onInitialPromptSent,
}: ChatContainerProps) {
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const initialPromptSentRef = useRef(false);

  // UI Â±ïÁ§∫Áî®ÁöÑ skillÔºà‰∏ªÈ¢ò„ÄÅÁ©∫Áä∂ÊÄÅÁ≠âÔºâ
  const displaySkill: SkillType = skill || (skillId as SkillType) || "bazi";

  // Use AI SDK powered chat hook
  // skillId ÂèØÈÄâÔºö‰∏ç‰º†Êó∂ÂêéÁ´Ø LLM Ëá™Âä®Ë∑ØÁî±
  const {
    messages,
    isLoading,
    sendMessage,
    sendQuickPrompt,
    error,
  } = useVibeChat({
    skillId: skillId as SkillId | undefined,
    scenario,
    voiceMode,
    conversationId,
    onConversationStart,
    onError: (err) => {
      console.error("Chat error:", err);
      // Show user-friendly toast notification with retry option
      const errorMessage = err instanceof Error ? err.message : "Êú™Áü•ÈîôËØØ";
      const isNetworkError = errorMessage.includes("network") || errorMessage.includes("fetch");
      const isTimeoutError = errorMessage.includes("timeout") || errorMessage.includes("Ë∂ÖÊó∂");

      toast.error(
        isNetworkError ? "ÁΩëÁªúËøûÊé•Â§±Ë¥•" : isTimeoutError ? "ËØ∑Ê±ÇË∂ÖÊó∂" : "ÂèëÈÄÅÂ§±Ë¥•",
        {
          description: isNetworkError
            ? "ËØ∑Ê£ÄÊü•ÊÇ®ÁöÑÁΩëÁªúËøûÊé•ÂêéÈáçËØï"
            : isTimeoutError
              ? "ÊúçÂä°Âô®ÂìçÂ∫îÊó∂Èó¥ËøáÈïøÔºåËØ∑Á®çÂêéÈáçËØï"
              : "ËØ∑Á®çÂêéÈáçËØïÔºåÊàñÂà∑Êñ∞È°µÈù¢",
          duration: 5000,
        }
      );
    },
  });

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages, scrollToBottom]);

  // Â§ÑÁêÜ‰ªé URL ‰º†ÂÖ•ÁöÑÂàùÂßãÊ∂àÊÅØÔºàÂ¶Ç‰ªéÈÄöÁü•Ë∑≥ËΩ¨Ôºâ
  useEffect(() => {
    if (initialPrompt && !initialPromptSentRef.current && !isLoading) {
      initialPromptSentRef.current = true;
      sendMessage(initialPrompt).then(() => {
        onInitialPromptSent?.();
      });
    }
  }, [initialPrompt, isLoading, sendMessage, onInitialPromptSent]);

  // ÈáçÁΩÆÊ†áËÆ∞ÂΩì initialPrompt ÂèòÂåñÊó∂
  useEffect(() => {
    if (!initialPrompt) {
      initialPromptSentRef.current = false;
    }
  }, [initialPrompt]);

  // Handle send from ChatInput
  const handleSend = useCallback(
    async (content: string) => {
      await sendMessage(content);
    },
    [sendMessage]
  );

  // Handle quick prompt from empty state
  const handleQuickPrompt = useCallback(
    async (content: string) => {
      await sendQuickPrompt(content);
    },
    [sendQuickPrompt]
  );

  const hasMessages = messages.length > 0;

  return (
    <div className="flex flex-col h-full" data-skill={displaySkill}>
      {/* Messages area - always flex-1 to push input to bottom */}
      <main
        id="main-content"
        className="flex-1 overflow-y-auto relative min-h-0"
        role="log"
        aria-label="ÂØπËØùÊ∂àÊÅØ"
        aria-live="polite"
      >
        {!hasMessages ? (
          /* Empty state - centered within scrollable area */
          <div className="flex flex-col items-center justify-end h-full pb-4 px-4">
            <ChatEmptyState skill={displaySkill} onQuickPrompt={handleQuickPrompt} />
          </div>
        ) : (
          /* Messages list - ÊâÄÊúâËÆæÂ§áÂ±Ö‰∏≠ */
          <div className="px-4 pt-4 pb-2 max-w-3xl mx-auto">
            {messages.map((message) => (
              // Use chat-message-item class for content-visibility optimization (Vercel rule: rendering-content-visibility)
              <div key={message.id} className="chat-message-item">
                <ChatMessage
                  role={message.role as "user" | "assistant"}
                  content={getMessageContent(message)}
                  skill={displaySkill}
                  isStreaming={isLoading && message.role === "assistant" && message === messages[messages.length - 1]}
                  onSendMessage={sendMessage}
                />
              </div>
            ))}
            {/* Show loading indicator when waiting for first response chunk */}
            {isLoading && messages.length > 0 && messages[messages.length - 1].role === "user" && (
              <ChatMessage
                role="assistant"
                content=""
                skill={displaySkill}
                isStreaming
              />
            )}
            {/* Error is now handled via toast notification in onError callback */}
            <div ref={messagesEndRef} />
          </div>
        )}
      </main>

      {/* Input - fixed at bottom */}
      <div className="flex-shrink-0">
        <ChatInput onSend={handleSend} disabled={isLoading} />
      </div>
    </div>
  );
}

