          }}
          vibeIdData={vibeIdData}
          baziData={baziData}
          zodiacData={zodiacData}
          voiceMode={voiceMode}
          onVoiceModeChange={setVoiceMode}
          onLogout={logout}
        />
      );
    default:
      return null;
  }
});

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// Chat Content - ‰∏ªËÅäÂ§©Âå∫Âüü
// ÊîØÊåÅ URL ÂèÇÊï∞Ôºö?skill=xxx&prompt=xxx (Áî®‰∫é‰ªéÈÄöÁü•Ë∑≥ËΩ¨ÂºÄÂßãÂØπËØù)
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

const ChatContent = memo(function ChatContent() {
  const searchParams = useSearchParams();
  const router = useRouter();
  const { voiceMode } = useAppShell();
  const { skill, setSkill } = useSkill();

  // ‰ªé URL ÂèÇÊï∞Ëé∑Âèñ skill„ÄÅscenario Âíå prompt
  const urlSkill = searchParams.get("skill");
  const urlScenario = searchParams.get("scenario");
  const urlPrompt = searchParams.get("prompt");
  const [initialPrompt, setInitialPrompt] = useState<string | null>(null);
  const [scenario, setScenario] = useState<string | null>(null);

  // Â§ÑÁêÜ URL ÂèÇÊï∞
  useEffect(() => {
    // Â¶ÇÊûú URL ÊåáÂÆö‰∫Ü skillÔºåÂàáÊç¢Âà∞ËØ• skill
    if (urlSkill && urlSkill !== skill) {
      setSkill(urlSkill as typeof skill);
    }

    // Â¶ÇÊûú URL ÊåáÂÆö‰∫Ü scenarioÔºåËÆæÁΩÆ scenario
    if (urlScenario) {
      setScenario(urlScenario);
    }

    // Â¶ÇÊûú URL ÊåáÂÆö‰∫Ü promptÔºåËÆæÁΩÆÂàùÂßãÊ∂àÊÅØ
    if (urlPrompt) {
      const decoded = decodeURIComponent(urlPrompt);
      setInitialPrompt(decoded);

      // Ê∏ÖÁêÜ URL ÂèÇÊï∞ÔºåÈÅøÂÖçÂà∑Êñ∞Êó∂ÈáçÂ§çÂèëÈÄÅ
      const newUrl = window.location.pathname;
      router.replace(newUrl, { scroll: false });
    }
  }, [urlSkill, urlScenario, urlPrompt, skill, setSkill, router]);

  return (
    <LuminousPaper skill={skill} variant="default" className="h-full relative">
      <BreathAura skill={skill} size="xl" position="top" intensity="low" className="opacity-20" />
      {/*
        v7.2: Ëá™Âä®Ë∑ØÁî±Ê®°Âºè
        - ‰∏ç‰º† skillIdÔºåËÆ©ÂêéÁ´Ø CoreAgent ÈÄöËøá LLM Ëá™Âä®ËØÜÂà´Áî®Êà∑ÊÑèÂõæ
        - skill ‰ªÖÁî®‰∫é UI Â±ïÁ§∫Ôºà‰∏ªÈ¢ò„ÄÅÁ©∫Áä∂ÊÄÅÁ≠âÔºâ
        - Áî®Êà∑ËØ¥"ÊòüÂ∫ßÂàÜÊûê"‰ºöËá™Âä®Ë∑ØÁî±Âà∞ zodiacÔºåËØ¥"ÂÖ´Â≠ó"‰ºöË∑ØÁî±Âà∞ bazi
        - initialPrompt: ‰ªéÈÄöÁü•Ë∑≥ËΩ¨Êó∂ÁöÑÈ¢ÑËÆæÊ∂àÊÅØ
        - scenario: ‰ªé URL ‰º†ÂÖ•ÁöÑ scenario/rule ID
      */}
      <ChatContainer
        skill={skill}
        scenario={scenario || undefined}
        voiceMode={voiceMode}
        initialPrompt={initialPrompt}
        onInitialPromptSent={() => setInitialPrompt(null)}
      />
    </LuminousPaper>
  );
});

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// Journey Content - ÊàëÁöÑÊóÖÁ®ã
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

const JourneyLoadingFallback = memo(function JourneyLoadingFallback() {
  return (
    <div className="p-4 space-y-4 animate-pulse">
      <div className="h-40 bg-muted rounded-xl" />
      <div className="h-32 bg-muted rounded-xl" />
      <div className="h-24 bg-muted rounded-xl" />
    </div>
  );
});

const JourneyContent = memo(function JourneyContent() {
  return <JourneyContentComponent />;
});

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// Discover Content - Êé¢Á¥¢ Skills
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

const DiscoverLoadingFallback = memo(function DiscoverLoadingFallback() {
  return (
    <div className="p-4 space-y-4 animate-pulse">
      <div className="h-32 bg-muted rounded-xl" />
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="h-48 bg-muted rounded-xl" />
        <div className="h-48 bg-muted rounded-xl" />
      </div>
    </div>
  );
});

const DiscoverContent = memo(function DiscoverContent() {
  return <DiscoverContentComponent />;
});

// Me Content - ‰∏™‰∫∫‰∏≠ÂøÉÂÜÖÂÆπ
const MeContent = memo(function MeContent() {
  const { skill } = useSkill();
  const { user, logout } = useAuth();
  const { voiceMode, setVoiceMode } = useAppShell();

  return (
    <MePanel
      skill={skill}
      user={{
        name: user?.display_name || user?.vibe_id || "Áî®Êà∑",
        email: user?.email || user?.phone || "",
        isPro: false
      }}
      voiceMode={voiceMode}
      onVoiceModeChange={setVoiceMode}
      onLogout={logout}
    />
  );
});

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// Chat Page - ‰∏ªÁªÑ‰ª∂
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

export default function ChatPage() {
  const { skill } = useSkill();

  return (
    <AppShell
      skill={skill}
      journeyContent={
        <Suspense fallback={<JourneyLoadingFallback />}>
          <JourneyContent />
        </Suspense>
      }
      discoverContent={
        <Suspense fallback={<DiscoverLoadingFallback />}>
          <DiscoverContent />
        </Suspense>
      }
      meContent={
        <Suspense fallback={<PanelLoadingFallback />}>
          <MeContent />
        </Suspense>
      }
    >
      <Suspense fallback={<ChatLoadingFallback />}>
        <ChatContent />
      </Suspense>
    </AppShell>
  );
}

