import { VibeGlyph, type SkillType } from "@/components/core";
import { type TabType } from "./AppShell";

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// Types & Config
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

interface NavBarProps {
  skill: SkillType;
  activeTab: TabType;
  onTabChange: (tab: TabType) => void;
  className?: string;
}

interface NavItem {
  id: TabType;
  icon: LucideIcon;
  label: string;
  description?: string;
}

// V9.1: 4 navigation items - Chat / Journey / Discover / Me
const NAV_ITEMS: NavItem[] = [
  {
    id: "chat",
    icon: MessageSquare,
    label: "ÂØπËØù",
    description: "‰∏é Vibe ÂØπËØù",
  },
  {
    id: "journey",
    icon: Compass,
    label: "ÊóÖÁ®ã",
    description: "ÊàëÁöÑÊóÖÁ®ã",
  },
  {
    id: "discover",
    icon: Sparkles,
    label: "Êé¢Á¥¢",
    description: "ÂèëÁé∞Êõ¥Â§ö Skills",
  },
  {
    id: "me",
    icon: User,
    label: "ÊàëÁöÑ",
    description: "ËÆæÁΩÆ/ËÆ¢ÈòÖ",
  },
];

const STORAGE_KEY = "vibelife-navbar-expanded";

// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
// NavBar Component
// ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê

export function NavBar({ skill, activeTab, onTabChange, className }: NavBarProps) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [hoveredItem, setHoveredItem] = useState<string | null>(null);
  const [isMounted, setIsMounted] = useState(false);

  // ‰ªé localStorage ÊÅ¢Â§çÁä∂ÊÄÅÔºà‰ªÖÂÆ¢Êà∑Á´ØÔºâ
  useEffect(() => {
    setIsMounted(true);
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved !== null) {
      setIsExpanded(saved === "true");
    }
  }, []);

  // ‰øùÂ≠òÁä∂ÊÄÅÂà∞ localStorage
  const toggleExpanded = () => {
    setIsExpanded((prev) => {
      const newValue = !prev;
      if (typeof window !== "undefined") {
        localStorage.setItem(STORAGE_KEY, String(newValue));
      }
      return newValue;
    });
  };

  return (
    <motion.nav
      initial={false}
      animate={{ width: isExpanded ? 200 : 64 }}
      transition={{ duration: 0.2, ease: "easeInOut" }}
      className={cn(
        "h-full flex flex-col bg-card border-r border-border",
        className
      )}
    >
      {/* Logo Area */}
      <div className="h-16 flex items-center justify-center border-b border-border">
        <VibeGlyph
          size="sm"
          skill={skill}
          showAura={false}
          animate={false}
        />
        <AnimatePresence>
          {isExpanded && (
            <motion.span
              initial={{ opacity: 0, width: 0 }}
              animate={{ opacity: 1, width: "auto" }}
              exit={{ opacity: 0, width: 0 }}
              className="ml-2 font-serif font-semibold text-foreground overflow-hidden whitespace-nowrap"
            >
              VibeLife
            </motion.span>
          )}
        </AnimatePresence>
      </div>

      {/* Nav Items */}
      <div className="flex-1 py-4 space-y-1">
        {NAV_ITEMS.map((item) => {
          const Icon = item.icon;
          const isActive = activeTab === item.id;

          return (
            <div key={item.id} className="relative px-2">
              <button
                onClick={() => onTabChange(item.id)}
                onMouseEnter={() => setHoveredItem(item.id)}
                onMouseLeave={() => setHoveredItem(null)}
                aria-label={item.label}
                title={item.description}
                className={cn(
                  "w-full flex items-center gap-3 px-3 py-2.5 rounded-lg transition-all duration-200",
                  isActive
                    ? "bg-skill-primary/10 text-skill-primary"
                    : "text-muted-foreground hover:bg-muted hover:text-foreground"
                )}
              >
                <Icon className="w-5 h-5 flex-shrink-0" />
                <AnimatePresence>
                  {isExpanded && (
                    <motion.span
                      initial={{ opacity: 0, width: 0 }}
                      animate={{ opacity: 1, width: "auto" }}
                      exit={{ opacity: 0, width: 0 }}
                      className="text-sm font-medium overflow-hidden whitespace-nowrap"
                    >
                      {item.label}
                    </motion.span>
                  )}
                </AnimatePresence>
              </button>

              {/* Tooltip - ‰ªÖÊî∂Ëµ∑Áä∂ÊÄÅÊó∂ÊòæÁ§∫ */}
              {!isExpanded && hoveredItem === item.id && (
                <motion.div
                  initial={{ opacity: 0, x: -4 }}
                  animate={{ opacity: 1, x: 0 }}
                  className="absolute left-full top-1/2 -translate-y-1/2 ml-2 z-50"
                >
                  <div className="bg-foreground text-background px-3 py-1.5 rounded-md text-sm whitespace-nowrap shadow-lg">
                    {item.label}
                    <div className="absolute left-0 top-1/2 -translate-y-1/2 -translate-x-1 w-2 h-2 bg-foreground rotate-45" />
                  </div>
                </motion.div>
              )}
            </div>
          );
        })}
      </div>

      {/* Expand/Collapse Toggle */}
      <div className="p-2 border-t border-border">
        <button
          onClick={toggleExpanded}
          aria-label={isExpanded ? "Êî∂Ëµ∑‰æßËæπÊ†è" : "Â±ïÂºÄ‰æßËæπÊ†è"}
          className={cn(
            "w-full flex items-center gap-2 px-3 py-2.5 rounded-lg transition-all duration-200",
            "text-muted-foreground hover:bg-muted hover:text-foreground"
          )}
        >
          {isExpanded ? (
            <>
              <ChevronLeft className="w-4 h-4" />
              <span className="text-sm font-medium">Êî∂Ëµ∑</span>
            </>
          ) : (
            <ChevronRight className="w-4 h-4" />
          )}
        </button>
      </div>
    </motion.nav>
  );
}

