        <h2 className="font-serif text-lg font-semibold text-foreground">
          ‰∫ÜËß£Ëá™Â∑±
        </h2>

        {/* VibeID Card */}
        {vibeIdData ? (
          <VibeIDCard
            data={vibeIdData}
            variant="compact"
