          data={baziData}
          onExplore={() => router.push("/chat?skill=bazi&prompt=ÁúãÁúãÊàëÁöÑÂëΩÁõò")}
        />

        {/* Zodiac Summary */}
        <ZodiacSummaryCard
          data={zodiacData}
          onExplore={() => router.push("/chat?skill=zodiac&prompt=ÂàÜÊûêÊàëÁöÑÊòüÁõò")}
        />
      </section>

      {/* Settings Section (Collapsible) */}
