   584→### 2.3 VibeID 钩子函数
   585→
   586→```typescript
   587→// src/skills/vibe-id/hooks/useVibeID.ts
   588→
   589→import { useCallback } from 'react';
   590→import useSWR from 'swr';
   591→import { create } from 'zustand';
   592→import type { VibeIDProfile, ArchetypeId, DimensionId } from '@/types/vibe-id';
   593→import { getArchetypeMeta } from '../constants/archetypes';
   594→
   595→// ============================================================================
   596→// Store
   597→// ============================================================================
   598→
   599→interface VibeIDState {
   600→  profile: VibeIDProfile | null;
   601→  isLoading: boolean;
   602→  error: Error | null;
   603→
   604→  // Actions
   605→  setProfile: (profile: VibeIDProfile) => void;
   606→  clearProfile: () => void;
   607→}
   608→
   609→export const useVibeIDStore = create<VibeIDState>((set) => ({
   610→  profile: null,
   611→  isLoading: false,
   612→  error: null,
   613→
   614→  setProfile: (profile) => set({ profile, isLoading: false, error: null }),
   615→  clearProfile: () => set({ profile: null }),
   616→}));
   617→
   618→// ============================================================================
   619→// API Fetcher
   620→// ============================================================================
   621→
   622→const fetcher = async (url: string): Promise<VibeIDProfile> => {
   623→  const res = await fetch(url, {
   624→    credentials: 'include',
   625→    headers: { 'Content-Type': 'application/json' }
   626→  });
   627→
   628→  if (!res.ok) {
   629→    const error = new Error('Failed to fetch VibeID profile');
   630→    throw error;
   631→  }
   632→
   633→  return res.json();
   634→};
   635→
   636→// ============================================================================
   637→// Main Hook
   638→// ============================================================================
   639→
   640→interface UseVibeIDOptions {
   641→  userId?: string;
   642→  autoFetch?: boolean;
   643→}
   644→
   645→interface UseVibeIDReturn {
   646→  // Data
   647→  profile: VibeIDProfile | null;
   648→  isLoading: boolean;
   649→  error: Error | null;
   650→
   651→  // Computed
   652→  coreArchetype: ReturnType<typeof getArchetypeMeta> | null;
   653→  innerArchetype: ReturnType<typeof getArchetypeMeta> | null;
   654→  outerArchetype: ReturnType<typeof getArchetypeMeta> | null;
   655→  shadowArchetype: ReturnType<typeof getArchetypeMeta> | null;
   656→
   657→  // Actions
   658→  getArchetype: (dimension: DimensionId) => ReturnType<typeof getArchetypeMeta> | null;
   659→  getTopArchetypes: (count?: number) => Array<{ id: ArchetypeId; score: number }>;
   660→  refresh: () => Promise<void>;
   661→}
   662→
   663→export function useVibeID(options: UseVibeIDOptions = {}): UseVibeIDReturn {
   664→  const { userId, autoFetch = true } = options;
   665→  const { profile: storeProfile, setProfile } = useVibeIDStore();
   666→
   667→  // SWR for data fetching with automatic deduplication (Vercel rule: client-swr-dedup)
   668→  const { data, error, isLoading, mutate } = useSWR<VibeIDProfile>(
   669→    autoFetch && userId ? `/api/vibe-id/${userId}` : null,
   670→    fetcher,
   671→    {
   672→      revalidateOnFocus: false,
   673→      revalidateOnReconnect: false,
   674→      dedupingInterval: 60000, // 1 minute deduplication
   675→      onSuccess: (data) => setProfile(data),
   676→    }
   677→  );
   678→
   679→  const profile = data || storeProfile;
   680→
   681→  // Get archetype for a specific dimension
   682→  const getArchetype = useCallback((dimension: DimensionId) => {
   683→    if (!profile) return null;
   684→    const archetypeId = profile.archetypes[dimension];
   685→    return getArchetypeMeta(archetypeId);
   686→  }, [profile]);
   687→
   688→  // Get top N archetypes by score
   689→  const getTopArchetypes = useCallback((count = 4) => {
   690→    if (!profile?.scores) return [];
   691→
   692→    return Object.entries(profile.scores)
   693→      .map(([id, score]) => ({ id: id as ArchetypeId, score }))
   694→      .sort((a, b) => b.score - a.score)
   695→      .slice(0, count);
   696→  }, [profile]);
   697→
   698→  // Refresh profile data
   699→  const refresh = useCallback(async () => {
   700→    await mutate();
   701→  }, [mutate]);
   702→
   703→  return {
   704→    profile,
   705→    isLoading,
   706→    error: error || null,
   707→
   708→    // Computed archetypes
   709→    coreArchetype: getArchetype('core'),
   710→    innerArchetype: getArchetype('inner'),
   711→    outerArchetype: getArchetype('outer'),
   712→    shadowArchetype: getArchetype('shadow'),
   713→
   714→    // Actions
   715→    getArchetype,
   716→    getTopArchetypes,
   717→    refresh,
   718→  };
   719→}
   720→```
