                        "type": p.type,
                        "required": p.required,
                        "description": p.description,
                        "enum": p.enum,
                        "default": p.default,
                    }
                    for p in tool.parameters
                ],
                "when_to_call": tool.when_to_call,
            })

        return {
            "version": "7.1",
            "skill_id": skill_id,
            "tools": tools,
        }

