'use client'

/**
 * Landing Step - Phase 1: 相遇 (0-15秒)
 * 设计文档 v5.0: 极简生日输入，降低门槛
 */

import { useState, useRef, useEffect } from 'react'
import { motion } from 'framer-motion'
import { useOnboarding } from '../context'

export default function LandingStep() {
  const { setBirthInfo, nextStep } = useOnboarding()
  const [year, setYear] = useState('')
  const [month, setMonth] = useState('')
  const [day, setDay] = useState('')
  const [hour, setHour] = useState('')
  const [showHour, setShowHour] = useState(false)

  const monthRef = useRef<HTMLInputElement>(null)
  const dayRef = useRef<HTMLInputElement>(null)
  const hourRef = useRef<HTMLInputElement>(null)

  const isValid = year.length === 4 && parseInt(year) >= 1900 && parseInt(month) >= 1 && parseInt(month) <= 12 && parseInt(day) >= 1 && parseInt(day) <= 31

  useEffect(() => {
    if (year.length === 4) monthRef.current?.focus()
  }, [year])

  useEffect(() => {
    if (month.length === 2) dayRef.current?.focus()
  }, [month])

  useEffect(() => {
    if (day.length === 2 && showHour) hourRef.current?.focus()
  }, [day, showHour])

  const handleSubmit = () => {
    if (!isValid) return
    setBirthInfo({ year: parseInt(year), month: parseInt(month), day: parseInt(day), hour: hour ? parseInt(hour) : null })
    nextStep()
  }

  return (
    <div className="min-h-screen bg-bg-primary flex flex-col items-center justify-center px-4">
      <motion.div initial={{ opacity: 0, y: -10 }} animate={{ opacity: 1, y: 0 }} className="text-center mb-10">
        <h1 className="font-serif text-brand text-text-primary mb-2">VibeLife</h1>
        <p className="text-helper text-text-secondary">探索内心世界</p>
      </motion.div>

      <motion.div initial={{ opacity: 0, scale: 0.98 }} animate={{ opacity: 1, scale: 1 }} transition={{ delay: 0.1 }} className="card w-full max-w-sm">
        <div className="text-center mb-6">
          <p className="text-body text-text-primary mb-1">欢迎来到你的内心空间</p>
          <p className="text-helper text-text-secondary">你的生日是？</p>
        </div>

        <div className="flex items-center gap-2 mb-4">
          <div className="flex-1">
            <input type="text" inputMode="numeric" maxLength={4} placeholder="1990" value={year} onChange={e => setYear(e.target.value.replace(/\D/g, ''))} className="input text-center text-lg font-medium" autoFocus />
            <span className="block text-small text-text-secondary text-center mt-1">年</span>
          </div>
          <div className="w-16">
            <input ref={monthRef} type="text" inputMode="numeric" maxLength={2} placeholder="5" value={month} onChange={e => setMonth(e.target.value.replace(/\D/g, ''))} className="input text-center text-lg font-medium" />
            <span className="block text-small text-text-secondary text-center mt-1">月</span>
          </div>
          <div className="w-16">
            <input ref={dayRef} type="text" inputMode="numeric" maxLength={2} placeholder="15" value={day} onChange={e => setDay(e.target.value.replace(/\D/g, ''))} className="input text-center text-lg font-medium" />
            <span className="block text-small text-text-secondary text-center mt-1">日</span>
          </div>
        </div>

        {!showHour ? (
          <button onClick={() => setShowHour(true)} className="w-full text-helper text-text-secondary hover:text-accent mb-6 transition-colors">
            + 添加出生时间（可选）
          </button>
        ) : (
          <div className="mb-6 text-center">
            <input ref={hourRef} type="text" inputMode="numeric" maxLength={2} placeholder="08" value={hour} onChange={e => setHour(e.target.value.replace(/\D/g, ''))} className="input w-20 text-center text-lg font-medium" />
            <span className="block text-small text-text-secondary mt-1">时（0-23）· 不知道也没关系</span>
          </div>
        )}

        <button onClick={handleSubmit} disabled={!isValid} className={isValid ? 'btn-primary w-full' : 'btn w-full bg-bg-secondary text-text-disabled cursor-not-allowed'}>
          开始探索
        </button>
      </motion.div>
    </div>
  )
}

