        // 功能色
        success: "var(--color-success)",
        warning: "var(--color-warning)",
        error: "var(--color-error)",
        info: "var(--color-info)",

        // ═══════════════════════════════════════════════════════════════
        // v7.1 品牌色板 - Vellum, Gold, Mystic, Ink
        // ═══════════════════════════════════════════════════════════════
        vellum: {
          50: "var(--vellum-50)",
