      },

      // ═══════════════════════════════════════════════════════════════
      // 阴影系统 - v7.1 Linear 风格多层阴影
      // ═══════════════════════════════════════════════════════════════
      boxShadow: {
        subtle: "var(--shadow-subtle)",
        soft: "var(--shadow-soft)",
        elevated: "var(--shadow-elevated)",
        float: "var(--shadow-float)",
        // v7.1 Card 专用多层阴影 - 无边框设计
        card: "var(--shadow-card)",
        "card-hover": "var(--shadow-card-hover)",
      },

      // ═══════════════════════════════════════════════════════════════
      // 边框色
      // ═══════════════════════════════════════════════════════════════
      borderColor: {
        subtle: "var(--border-subtle)",
        DEFAULT: "var(--border-default)",
        strong: "var(--border-strong)",
        focus: "var(--border-focus)",
      },

      // ═══════════════════════════════════════════════════════════════
      // 间距系统
      // ═══════════════════════════════════════════════════════════════
      spacing: {
        "4.5": "1.125rem",
        "5.5": "1.375rem",
      },

      // ═══════════════════════════════════════════════════════════════
      // 动画系统 - v7.1 Spring easing + 新动画
      // ═══════════════════════════════════════════════════════════════
      animation: {
        "fade-in": "fadeIn var(--duration-normal) var(--ease-out-expo)",
        "fade-in-up": "fadeInUp var(--duration-slow) var(--ease-out-expo)",
        "scale-in": "scaleIn var(--duration-normal) var(--ease-spring)",
        shimmer: "shimmer 2s ease-in-out infinite",
        "pulse-soft": "pulse-soft 1.4s ease-in-out infinite",
        // v7.1 新增动画
        "slide-in-up": "slideInUp var(--duration-normal) var(--ease-out-expo)",
        "slide-in-down": "slideInDown var(--duration-normal) var(--ease-out-expo)",
        "slide-in-left": "slideInLeft var(--duration-normal) var(--ease-out-expo)",
        "slide-in-right": "slideInRight var(--duration-normal) var(--ease-out-expo)",
        "spring-bounce": "springBounce 0.5s var(--ease-spring)",
        "spring-scale": "springScale 0.3s var(--ease-spring)",
      },
      keyframes: {
        fadeIn: {
          "0%": { opacity: "0" },
          "100%": { opacity: "1" },
        },
        fadeInUp: {
          "0%": { opacity: "0", transform: "translateY(8px)" },
          "100%": { opacity: "1", transform: "translateY(0)" },
        },
        scaleIn: {
          "0%": { opacity: "0", transform: "scale(0.95)" },
          "100%": { opacity: "1", transform: "scale(1)" },
        },
        shimmer: {
          "0%": { backgroundPosition: "-200% 0" },
          "100%": { backgroundPosition: "200% 0" },
        },
        "pulse-soft": {
          "0%, 100%": { opacity: "0.4", transform: "scale(0.8)" },
          "50%": { opacity: "1", transform: "scale(1)" },
        },
        // v7.1 新增关键帧
        slideInUp: {
          "0%": { opacity: "0", transform: "translateY(10px)" },
          "100%": { opacity: "1", transform: "translateY(0)" },
        },
        slideInDown: {
          "0%": { opacity: "0", transform: "translateY(-10px)" },
          "100%": { opacity: "1", transform: "translateY(0)" },
        },
        slideInLeft: {
          "0%": { opacity: "0", transform: "translateX(-10px)" },
          "100%": { opacity: "1", transform: "translateX(0)" },
        },
        slideInRight: {
          "0%": { opacity: "0", transform: "translateX(10px)" },
          "100%": { opacity: "1", transform: "translateX(0)" },
        },
        springBounce: {
          "0%": { transform: "scale(0.9)" },
          "50%": { transform: "scale(1.05)" },
          "100%": { transform: "scale(1)" },
        },
        springScale: {
          "0%": { transform: "scale(0.95)", opacity: "0" },
          "100%": { transform: "scale(1)", opacity: "1" },
        },
      },
      transitionTimingFunction: {
        spring: "var(--ease-spring)",
        "out-expo": "var(--ease-out-expo)",
        "in-out": "var(--ease-in-out)",
      },
      transitionDuration: {
        fast: "var(--duration-fast)",
        normal: "var(--duration-normal)",
        slow: "var(--duration-slow)",
      },
    },
  },
  plugins: [],
};

export default config;

