            <input ref={hourRef} type="text" inputMode="numeric" maxLength={2} placeholder="08" value={hour} onChange={e => setHour(e.target.value.replace(/\D/g, ''))} className="input w-20 text-center text-lg font-medium" />
            <span className="block text-small text-text-secondary mt-1">时（0-23）· 不知道也没关系</span>
          </div>
        )}

        <button onClick={handleSubmit} disabled={!isValid} className={isValid ? 'btn-primary w-full' : 'btn w-full bg-bg-secondary text-text-disabled cursor-not-allowed'}>
          开始探索
        </button>
      </motion.div>
    </div>
  )
