        console.error('Zodiac API error:', err)
      }
      setDone(prev => [...prev, 1])
      setCurrent(2)
    }

    // 执行 API 调用
    fetchBazi()
    setTimeout(fetchZodiac, 2000)

    // 完成后设置数据并跳转
    const t3 = setTimeout(() => {
      setDone(prev => [...prev, 2])
