'use client'

/**
 * Loading Step - Phase 2: 洞察 (15-45秒)
 * 设计文档 v5.0: 八字排盘 + 星座分析
 */

import { useEffect, useState, useRef } from 'react'
import { motion } from 'framer-motion'
import { useOnboarding } from '../context'

const API_BASE = process.env.NEXT_PUBLIC_API_BASE || 'http://localhost:8000/api/v1'
const STEPS = ['解读八字', '分析星座', '融合洞察']

export default function LoadingStep() {
  const { state, setBaziData, goToStep } = useOnboarding()
  const [current, setCurrent] = useState(0)
  const [done, setDone] = useState<number[]>([])
  const [baziResult, setBaziResult] = useState<Record<string, unknown> | null>(null)
  const [zodiacResult, setZodiacResult] = useState<Record<string, unknown> | null>(null)
  const fetchedRef = useRef(false)

  useEffect(() => {
    if (!state.birthInfo || fetchedRef.current) {
      if (!state.birthInfo) goToStep('landing')
      return
    }
    fetchedRef.current = true

    // 构建日期字符串
    const { year, month, day, hour } = state.birthInfo
    const birthDate = `${year}-${String(month).padStart(2, '0')}-${String(day).padStart(2, '0')}`
    const birthTime = hour ? `${String(hour).padStart(2, '0')}:00` : '12:00'
    const gender = state.gender || 'unknown'

    // 调用八字计算 API
    const fetchBazi = async () => {
      try {
        const res = await fetch(`${API_BASE}/bazi/chart`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ birth_date: birthDate, birth_time: birthTime, gender })
        })
        if (res.ok) {
          const data = await res.json()
          if (data.success && data.chart) {
            setBaziResult(data.chart)
          }
        }
      } catch (err) {
        console.error('Bazi API error:', err)
      }
      setDone(prev => [...prev, 0])
      setCurrent(1)
    }

    // 调用星座计算 API
    const fetchZodiac = async () => {
      try {
        const res = await fetch(`${API_BASE}/zodiac/chart`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ birth_date: birthDate, birth_time: birthTime, birth_place: 'Shanghai' })
        })
        if (res.ok) {
          const data = await res.json()
          if (data.success && data.chart) {
            setZodiacResult(data.chart)
          }
        }
      } catch (err) {
        console.error('Zodiac API error:', err)
      }
      setDone(prev => [...prev, 1])
      setCurrent(2)
    }

    // 执行 API 调用
    fetchBazi()
    setTimeout(fetchZodiac, 2000)

    // 完成后设置数据并跳转
    const t3 = setTimeout(() => {
      setDone(prev => [...prev, 2])
    }, 5000)

    const t4 = setTimeout(() => {
      const pillars = baziResult?.four_pillars as Record<string, { stem: string; branch: string }> | undefined
      setBaziData({
        yearPillar: pillars?.year || { stem: '甲', branch: '午' },
        monthPillar: pillars?.month || { stem: '丙', branch: '寅' },
        dayPillar: pillars?.day || { stem: '戊', branch: '申' },
        hourPillar: state.birthInfo?.hour ? (pillars?.hour || { stem: '壬', branch: '子' }) : null,
        dayMaster: (baziResult?.day_master as { stem?: string; element?: string })?.stem
          ? `${(baziResult?.day_master as { stem: string }).stem}${(baziResult?.day_master as { element?: string })?.element === 'wood' ? '木' : (baziResult?.day_master as { element?: string })?.element === 'fire' ? '火' : (baziResult?.day_master as { element?: string })?.element === 'earth' ? '土' : (baziResult?.day_master as { element?: string })?.element === 'metal' ? '金' : '水'}`
          : '戊土',
        dayMasterElement: (baziResult?.day_master as { element?: string })?.element || '土',
        insight: '你是一个需要稳定感的人，却总被内心的不安驱动着去追求更多。',
        fortune: { score: 78, luckyColor: '深蓝', luckyNumber: 7, advice: '今日适合处理重要决策' },
        sunSign: (zodiacResult?.sun_sign_chinese as string) || '金牛座',
        moonSign: (zodiacResult?.moon_sign_chinese as string) || '巨蟹座',
        risingSign: (zodiacResult?.rising_sign_chinese as string) || '天秤座',
      })
      goToStep('aha')
    }, 6000)

    return () => { clearTimeout(t3); clearTimeout(t4) }
  }, [state.birthInfo, state.gender, setBaziData, goToStep, baziResult, zodiacResult])

  return (
    <div className="min-h-screen bg-bg-primary flex flex-col items-center justify-center px-4">
      <motion.h1 initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="font-serif text-page text-text-primary mb-8">
        正在为你绘制专属画像...
      </motion.h1>

      <motion.div initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.2 }} className="card w-full max-w-xs p-5 mb-6">
        <div className="space-y-3">
          {STEPS.map((step, i) => (
            <div key={i} className="flex items-center gap-3">
              <div className={`w-6 h-6 rounded-full flex items-center justify-center text-small font-medium ${done.includes(i) ? 'bg-accent text-white' : i === current ? 'bg-accent/20 text-accent' : 'bg-bg-secondary text-text-disabled'}`}>
                {done.includes(i) ? '✓' : i === current ? '◉' : '○'}
              </div>
              <span className={`text-body ${done.includes(i) || i === current ? 'text-text-primary' : 'text-text-disabled'}`}>{step}</span>
            </div>
          ))}
        </div>
      </motion.div>

      {done.includes(0) && (
        <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} className="card w-full max-w-xs p-5 mb-4">
          <h3 className="font-serif text-card-title text-text-primary mb-4">🌟 八字命盘</h3>
          <div className="grid grid-cols-4 gap-2 text-center">
            {(() => {
              const pillars = baziResult?.four_pillars as Record<string, { stem: string; branch: string }> | undefined
              const values = [
                pillars?.year ? `${pillars.year.stem}${pillars.year.branch}` : '甲午',
                pillars?.month ? `${pillars.month.stem}${pillars.month.branch}` : '丙寅',
                pillars?.day ? `${pillars.day.stem}${pillars.day.branch}` : '戊申',
                state.birthInfo?.hour ? (pillars?.hour ? `${pillars.hour.stem}${pillars.hour.branch}` : '壬子') : '—'
              ]
              return values.map((v, i) => (
                <div key={i}>
                  <p className="text-small text-text-secondary mb-1">{['年柱', '月柱', '日柱', '时柱'][i]}</p>
                  <p className="font-serif text-body text-text-primary">{v}</p>
                </div>
              ))
            })()}
          </div>
          <p className="text-helper text-text-secondary mt-3 pt-3 border-t border-subtle">
            日主：<span className="text-text-primary font-medium">
              {(baziResult?.day_master as { description?: string })?.description?.slice(0, 4) || '戊土'}
            </span> · 格局：<span className="text-text-primary font-medium">
              {(baziResult?.pattern as { name?: string })?.name || '食神格'}
            </span>
          </p>
        </motion.div>
      )}

      {done.includes(1) && (
        <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} className="card w-full max-w-xs p-5">
          <h3 className="font-serif text-card-title text-text-primary mb-3">🔮 星座能量</h3>
          <p className="text-helper text-text-secondary">
            太阳：<span className="text-text-primary font-medium">{(zodiacResult?.sun_sign_chinese as string) || '金牛座'} ♉</span>
          </p>
          {state.birthInfo?.hour && (
            <>
              <p className="text-helper text-text-secondary">
                月亮：<span className="text-text-primary font-medium">{(zodiacResult?.moon_sign_chinese as string) || '巨蟹座'} ♋</span>
              </p>
              <p className="text-helper text-text-secondary">
                上升：<span className="text-text-primary font-medium">{(zodiacResult?.rising_sign_chinese as string) || '天秤座'} ♎</span>
              </p>
            </>
          )}
        </motion.div>
      )}
    </div>
  )
}

