'use client'

/**
 * Aha Step - Phase 3: 共鸣 (45-75秒)
 * 设计文档 v7.1: 核心洞察 + Identity Prism 预览
 *
 * 优化 v2.0:
 * - ✅ 移除 Framer Motion,用 CSS transitions (-200KB bundle)
 * - ✅ 静态 JSX 提升
 * - ✅ 延迟动画使用 CSS animation-delay
 */

import { useState, memo } from 'react'
import { useOnboarding } from '../context'

// ═══════════════════════════════════════════════════════════════════
// 静态 JSX 提升
// ═══════════════════════════════════════════════════════════════════

const InsightHeader = (
  <h2 className="font-serif text-xl text-text-primary text-center mb-6">核心洞察</h2>
)

const InsightContent = (
  <div className="border-t border-b border-subtle py-5 mb-5">
    <p className="text-base text-text-primary leading-relaxed text-center">
      你是一个需要稳定感的人，<br />却总被内心的不安驱动着<br />去追求更多。
    </p>
    <p className="text-base text-text-primary leading-relaxed text-center mt-4">
      这种矛盾让你时常感到<br />疲惫，却又停不下来。
    </p>
  </div>
)

const IdentityPrismPreview = memo(function IdentityPrismPreview({ onNext }: { onNext: () => void }) {
  return (
    <div
      className="card w-full max-w-sm p-5 mb-6 border-amber/30 animate-slide-in-up"
      style={{ animationDelay: '200ms', animationFillMode: 'both' }}
    >
      <div className="flex items-center gap-2 mb-3">
        <span className="text-xl">💎</span>
        <h3 className="font-serif text-lg text-text-primary">Identity Prism 初见</h3>
      </div>
      <p className="text-sm text-text-secondary mb-4">
        基于八字和星座的初步分析，我们发现你有着独特的「稳中求变」的生命特质...
      </p>
      <button
        onClick={onNext}
        className="w-full py-3 rounded-md text-sm font-medium text-accent bg-accent/10 hover:bg-accent/20 transition-colors duration-normal"
      >
        🔒 查看完整画像
      </button>
    </div>
  )
})

// ═══════════════════════════════════════════════════════════════════
// 八字柱显示组件
// ═══════════════════════════════════════════════════════════════════

const PillarDisplay = memo(function PillarDisplay({
  pillar,
  label
}: {
  pillar: { stem: string; branch: string } | null
  label: string
}) {
  return (
    <div className="text-center">
      <p className="text-xs text-text-disabled mb-1">{label}</p>
      <div className="w-10 h-10 bg-white border border-subtle rounded-sm flex items-center justify-center text-xs text-text-secondary">
        {pillar ? `${pillar.stem}${pillar.branch}` : '—'}
      </div>
    </div>
  )
})

// ═══════════════════════════════════════════════════════════════════
// 主组件
// ═══════════════════════════════════════════════════════════════════

export default function AhaStep() {
  const { state, nextStep } = useOnboarding()
  const [answer, setAnswer] = useState<'yes' | 'no' | null>(null)

  if (!state.baziData) return null

  const pillars = [
    { pillar: state.baziData.yearPillar, label: '年' },
    { pillar: state.baziData.monthPillar, label: '月' },
    { pillar: state.baziData.dayPillar, label: '日' },
    { pillar: state.baziData.hourPillar, label: '时' },
  ]

  return (
    <div className="min-h-screen bg-bg-primary flex flex-col items-center justify-center px-4 py-8">
      {/* 核心洞察卡片 */}
      <div className="card w-full max-w-sm p-6 mb-6 animate-slide-in-up">
        {InsightHeader}
        {InsightContent}

        <p className="text-sm text-text-secondary text-center mb-5">
          你最近是否正在经历一些重要的人生选择？
        </p>

        <div className="flex gap-3">
          <button
            onClick={() => setAnswer('yes')}
            className={`flex-1 py-3 rounded-md text-base font-medium transition-all duration-normal ${
              answer === 'yes'
                ? 'bg-accent text-white'
                : 'bg-bg-secondary text-text-primary hover:bg-bg-tertiary'
            }`}
          >
            是的
          </button>
          <button
            onClick={() => setAnswer('no')}
            className={`flex-1 py-3 rounded-md text-base font-medium transition-all duration-normal ${
              answer === 'no'
                ? 'bg-accent text-white'
                : 'bg-bg-secondary text-text-primary hover:bg-bg-tertiary'
            }`}
          >
            不太是
          </button>
        </div>
      </div>

      {/* Identity Prism 预览 */}
      <IdentityPrismPreview onNext={nextStep} />

      {/* 继续探索按钮 */}
      <button
        onClick={nextStep}
        className="btn-primary w-full max-w-sm animate-fade-in hover:scale-[1.02] active:scale-[0.98] transition-transform duration-normal"
        style={{ animationDelay: '400ms', animationFillMode: 'both' }}
      >
        继续探索
      </button>

      {/* 八字四柱展示 */}
      <div
        className="flex gap-2 mt-8 animate-fade-in"
        style={{ animationDelay: '600ms', animationFillMode: 'both' }}
      >
        {pillars.map(({ pillar, label }) => (
          <PillarDisplay key={label} pillar={pillar} label={label} />
        ))}
      </div>
    </div>
  )
}

