'use client'

/**
 * Persona Step - Stage 4
 *
 * 人格选择：暖心闺蜜 / 毒舌损友 / 人生导师
 * 目标：让用户选择对话风格，增加参与感
 * 不显示情绪价值分数
 */

import { useState } from 'react'
import { motion } from 'framer-motion'
import { ArrowRight } from 'lucide-react'
import { useOnboarding } from '../context'
import { PERSONAS, type PersonaType } from '../types'

const PERSONA_LIST: PersonaType[] = ['warm', 'sharp', 'wise']

// 根据性别获取人格显示名称
function getPersonaDisplayName(personaId: PersonaType, gender: string | null): string {
  if (personaId === 'warm') {
    if (gender === 'male') return '暖心好友'
    if (gender === 'female') return '暖心闺蜜'
    return '暖心伙伴'
  }
  return PERSONAS[personaId].name
}

export default function PersonaStep() {
  const { state, setPersona, nextStep } = useOnboarding()
  const [selected, setSelected] = useState<PersonaType | null>(null)

  const handleSelect = (persona: PersonaType) => {
    setSelected(persona)
  }

  const handleContinue = () => {
    if (selected) {
      setPersona(selected)
      nextStep()
    }
  }

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="w-full max-w-2xl"
    >
      {/* Title */}
      <motion.div
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-center mb-8"
      >
        <h2 className="font-serif text-2xl font-bold text-foreground mb-2">
          选择你喜欢的对话风格
        </h2>
        <p className="text-muted-foreground">
