"use client";

/**
 * Login Page - v8.0 OAuth Only
 *
 * Supports:
 * - Google OAuth
 * - Apple OAuth
 * - WeChat QR Code Login
 *
 * No email/password authentication.
 */

import { useState, useCallback } from "react";
import { useRouter } from "next/navigation";
import { OAuthButtons } from "@/components/auth/OAuthButtons";
import { AuthLayout, AuthCard } from "@/components/auth/AuthLayout";

export default function LoginPage() {
  const router = useRouter();
  const [error, setError] = useState("");

  const handleOAuthSuccess = useCallback(() => {
    router.push("/");
  }, [router]);

  const handleOAuthError = useCallback((errorMsg: string) => {
    setError(errorMsg);
  }, []);

  return (
    <AuthLayout>
      <AuthCard
        title="欢迎来到 VibeLife"
        subtitle="选择登录方式继续"
      >
        {/* Error Display */}
        {error && (
          <div className="mb-4 p-3 rounded-lg bg-error/10 border border-error/20">
            <p className="text-error text-sm text-center">{error}</p>
          </div>
        )}

        {/* OAuth Buttons */}
        <OAuthButtons
          onSuccess={handleOAuthSuccess}
          onError={handleOAuthError}
        />

        {/* Privacy Notice */}
        <div className="mt-6 pt-4 border-t border-border-default">
          <p className="text-xs text-text-tertiary text-center">
            继续即表示你同意我们的
            <a href="/terms" className="text-accent-primary hover:underline mx-1">
              服务条款
            </a>
            和
            <a href="/privacy" className="text-accent-primary hover:underline mx-1">
              隐私政策
            </a>
          </p>
        </div>
      </AuthCard>
    </AuthLayout>
  );
}

'use client'

/**
 * Onboarding Page - v5.0
 *
 * 90秒旅程：相遇 → 洞察 → 共鸣 → 期待
 *
 * 优化 v2.0:
 * - ✅ 动态导入步骤组件 (Vercel best practice: bundle-dynamic-imports)
 * - ✅ Suspense 边界实现流式加载 (Vercel best practice: async-suspense-boundaries)
 * - ✅ 减少首屏 bundle 大小
 */

import { Suspense } from 'react'
import dynamic from 'next/dynamic'
import { useOnboarding } from './context'

// ═══════════════════════════════════════════════════════════════════
// 动态导入步骤组件 - 减少首屏 bundle (Vercel best practice: bundle-dynamic-imports)
// ═══════════════════════════════════════════════════════════════════

// LandingStep 不动态导入,因为它是首屏必需
import LandingStep from './steps/LandingStep'

// 其他步骤动态导入,只在需要时加载
const LoadingStep = dynamic(() => import('./steps/LoadingStep'), {
  loading: () => <LoadingSkeleton />,
  ssr: false
})

const AhaStep = dynamic(() => import('./steps/AhaStep'), {
  loading: () => <LoadingSkeleton />,
  ssr: false
})

const ConversionStep = dynamic(() => import('./steps/ConversionStep'), {
  loading: () => <LoadingSkeleton />,
  ssr: false
})

// ═══════════════════════════════════════════════════════════════════
// Loading Skeleton - Suspense fallback
// ═══════════════════════════════════════════════════════════════════

function LoadingSkeleton() {
  return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="flex flex-col items-center gap-4">
        <div className="w-16 h-16 rounded-full border-4 border-accent-primary/20 border-t-accent-primary animate-spin" />
        <p className="text-sm text-text-secondary">加载中...</p>
      </div>
    </div>
  )
}

// ═══════════════════════════════════════════════════════════════════
// 主组件 - 使用 Suspense 边界
// ═══════════════════════════════════════════════════════════════════

export default function OnboardingPage() {
  const { state } = useOnboarding()

  // LandingStep 无需 Suspense,因为它已经在客户端
  if (state.step === 'landing') {
    return <LandingStep />
  }

  // 其他步骤使用 Suspense 边界,实现流式加载
  return (
    <Suspense fallback={<LoadingSkeleton />}>
      {state.step === 'loading' && <LoadingStep />}
      {state.step === 'aha' && <AhaStep />}
      {state.step === 'conversion' && <ConversionStep />}
    </Suspense>
  )
}

'use client'

/**
 * Identity Page - v7.2 性能优化版
 * 基于设计文档 v5.0 重构
 *
 * v7.2 优化 (Vercel Best Practices):
 * - 使用 SWR 替代手动 fetch (rule: client-swr-dedup)
 * - 提取内联组件为 memo 组件 (rule: rerender-memo)
 * - 使用缓存的 localStorage 读取 (rule: js-cache-storage)
 * - 使用 useCallback 稳定回调 (rule: rerender-functional-setstate)
 *
 * 核心功能：
 * - 四维原型展示（Core/Inner/Outer/Shadow）
 * - 12 原型雷达图
 * - 底层密码（八字+星座）
 * - 成长方向
 */

import { useState, useCallback, memo, useMemo } from 'react'
import { useRouter } from 'next/navigation'
import useSWR from 'swr'
import { PCLayout } from '@/components/layout/PCLayout'
import {
  VibeIDCard,
  VibeIDCardSkeleton,
  ArchetypeRadar,
  ArchetypeList,
  ArchetypeDetail,
  type VibeIDDisplay,
  type ArchetypeScores,
  type ArchetypeType,
  getArchetypeMeta,
} from '@/skills/vibe-id'
import { getCachedUser } from '@/utils/storage'

const API_BASE = process.env.NEXT_PUBLIC_API_BASE || 'http://localhost:8000/api/v1'

// ═══════════════════════════════════════════════════════════════════════════
// SWR Fetcher
// ═══════════════════════════════════════════════════════════════════════════

const fetcher = async (url: string) => {
  const res = await fetch(url)
  if (!res.ok) throw new Error('Failed to fetch')
  return res.json()
}

// ═══════════════════════════════════════════════════════════════════════════
// Mock Data (用于开发/演示)
// ═══════════════════════════════════════════════════════════════════════════

const MOCK_VIBE_ID: VibeIDDisplay = {
  primaryArchetype: 'Creator',
  primaryEmoji: '🎨',
  primaryName: '创造者',
  primaryNickname: '审美在线本人',
  primarySlogan: '表达是我的呼吸',

  secondaryArchetype: 'Magician',
  secondaryEmoji: '🔮',

  coreSummary: '🎨 创造者 / 审美在线本人',
  innerSummary: '🔮 魔术师',
  outerSummary: '🤗 照顾者',
  shadowSummary: '🏠 凡人',
  shadowTension: '独特 vs 归属',

  summary: '你的核心原型是【创造者】，副原型是【魔术师】。你的八字是伤官格，用神为财，这决定了你的人生底色是创造者型的表达驱动者。',
  growthTip: '完成作品而非追求完美，让创意真正落地。',

  baziBrief: '伤官格 · 用神为财 · 身偏旺',
  zodiacBrief: '太阳狮子 · 月亮双鱼 · 上升天蝎',

  confidence: 'high',
  consistencyType: 'similar',
}

const MOCK_SCORES: ArchetypeScores = {
  Innocent: 45,
  Explorer: 62,
  Sage: 58,
  Hero: 55,
  Outlaw: 48,
  Magician: 78,
  Regular: 35,
  Lover: 65,
  Jester: 52,
  Caregiver: 70,
  Creator: 92,
  Ruler: 42,
}

const MOCK_MEMORIES = [
  { content: '你最近工作压力较大', date: '2天前' },
  { content: '关注职业发展方向', date: '1周前' },
  { content: '对感情问题有困扰', date: '2周前' },
]

// ═══════════════════════════════════════════════════════════════════════════
// Memoized Sub-Components (Vercel rule: rerender-memo)
// ═══════════════════════════════════════════════════════════════════════════

interface AsideContentProps {
  archetypeScores: ArchetypeScores | null
  vibeIdData: VibeIDDisplay | null
  memories: Array<{ content: string; date: string }>
  onArchetypeClick: (archetype: ArchetypeType) => void
  onNavigate: (path: string) => void
}

const AsideContent = memo(function AsideContent({
  archetypeScores,
  vibeIdData,
  memories,
  onArchetypeClick,
  onNavigate,
}: AsideContentProps) {
  return (
    <div className="space-y-6">
      {/* 12 原型雷达图 */}
      {archetypeScores && (
        <div className="pc-aside-section">
          <h3 className="pc-aside-title">🎯 原型分布</h3>
          <div className="card p-4">
            <ArchetypeRadar
              scores={archetypeScores}
              highlightArchetype={vibeIdData?.primaryArchetype}
              size={240}
              showLabels
              showScores={false}
              onArchetypeClick={onArchetypeClick}
            />
          </div>
        </div>
      )}

      {/* 原型排行 */}
      {archetypeScores && (
        <div className="pc-aside-section">
          <h3 className="pc-aside-title">📊 原型排行</h3>
          <div className="card p-4">
            <ArchetypeList
              scores={archetypeScores}
              limit={5}
              onArchetypeClick={onArchetypeClick}
            />
          </div>
        </div>
      )}

      {/* 记忆画像 */}
      <div className="pc-aside-section">
        <h3 className="pc-aside-title">💡 VibeLife 记住了</h3>
        <div className="space-y-2">
          {memories.map((memory, i) => (
            <div key={i} className="p-3 bg-[var(--bg-callout)] rounded-lg">
              <p className="text-sm text-text-primary">{memory.content}</p>
              <p className="text-xs text-text-secondary mt-1">{memory.date}</p>
            </div>
          ))}
        </div>
        <button className="w-full mt-3 text-sm text-accent text-center">查看全部记忆 →</button>
      </div>

      {/* 快捷操作 */}
      <div className="pc-aside-section">
        <h3 className="pc-aside-title">⚡ 快捷操作</h3>
        <div className="space-y-2">
          <button
            onClick={() => onNavigate('/chat')}
            className="w-full p-3 bg-[var(--bg-card-alt)] rounded-lg text-left text-sm text-text-primary hover:bg-[var(--bg-secondary)] transition-colors"
          >
            💬 继续对话，让我更了解你
          </button>
          <button
            onClick={() => onNavigate('/chat?skill=bazi')}
            className="w-full p-3 bg-[var(--bg-card-alt)] rounded-lg text-left text-sm text-text-primary hover:bg-[var(--bg-secondary)] transition-colors"
          >
            🌟 查看八字命盘
          </button>
          <button
            onClick={() => onNavigate('/chat?skill=zodiac')}
            className="w-full p-3 bg-[var(--bg-card-alt)] rounded-lg text-left text-sm text-text-primary hover:bg-[var(--bg-secondary)] transition-colors"
          >
            🔮 查看星座解读
          </button>
        </div>
      </div>
    </div>
  )
})

// ═══════════════════════════════════════════════════════════════════════════
// Four Dimensions Display Component
// ═══════════════════════════════════════════════════════════════════════════

interface FourDimensionsProps {
  vibeIdData: VibeIDDisplay
}

const FourDimensions = memo(function FourDimensions({ vibeIdData }: FourDimensionsProps) {
  const dimensions = useMemo(() => [
    { icon: '🎯', label: 'Core 灵魂本质', value: vibeIdData.coreSummary },
    { icon: '💫', label: 'Inner 内在世界', value: vibeIdData.innerSummary },
    { icon: '🌟', label: 'Outer 外在呈现', value: vibeIdData.outerSummary },
    {
      icon: '🌑',
      label: 'Shadow 阴影倾向',
      value: vibeIdData.shadowSummary,
      extra: vibeIdData.shadowTension,
    },
  ], [vibeIdData])

  return (
    <div className="card p-5 mb-4 fade-in lg:hidden" style={{ animationDelay: '100ms' }}>
      <h2 className="font-serif text-card-title text-text-primary mb-4 text-center">
        🎯 四维原型
      </h2>
      <div className="space-y-3">
        {dimensions.map((item, i) => (
          <div key={i} className="bg-bg-secondary rounded-md p-4">
            <p className="text-helper text-text-secondary mb-1">
              {item.icon} {item.label}
            </p>
            <p className="text-body text-text-primary font-medium">{item.value}</p>
            {item.extra && (
              <p className="text-small text-text-secondary mt-1">核心张力：{item.extra}</p>
            )}
          </div>
        ))}
      </div>
    </div>
  )
})

// ═══════════════════════════════════════════════════════════════════════════
// Main Component
// ═══════════════════════════════════════════════════════════════════════════

export default function IdentityPage() {
  const router = useRouter()
  const [selectedArchetype, setSelectedArchetype] = useState<ArchetypeType | null>(null)

  // Get user ID from cached localStorage (Vercel rule: js-cache-storage)
  const userId = useMemo(() => {
    const user = getCachedUser()
    return user?.user_id || null
  }, [])

  // Use SWR for data fetching with automatic deduplication (Vercel rule: client-swr-dedup)
  const { data, isLoading } = useSWR(
    userId ? `${API_BASE}/vibe-id/profile?user_id=${userId}` : null,
    fetcher,
    {
      revalidateOnFocus: false,
      revalidateOnReconnect: false,
      dedupingInterval: 60000, // 1 minute deduplication
      fallbackData: { display: MOCK_VIBE_ID, scores: MOCK_SCORES },
      onError: () => {
        // Silently use mock data on error
      },
    }
  )

  const vibeIdData: VibeIDDisplay = data?.display || MOCK_VIBE_ID
  const archetypeScores: ArchetypeScores = data?.scores || MOCK_SCORES

  // Stable callbacks (Vercel rule: rerender-functional-setstate)
  const handleArchetypeClick = useCallback((archetype: ArchetypeType) => {
    setSelectedArchetype(archetype)
  }, [])

  const handleNavigate = useCallback((path: string) => {
    router.push(path)
  }, [router])

  const handleClearSelection = useCallback(() => {
    setSelectedArchetype(null)
  }, [])

  // Memoized aside content props
  const asideProps = useMemo(() => ({
    archetypeScores,
    vibeIdData,
    memories: MOCK_MEMORIES,
    onArchetypeClick: handleArchetypeClick,
    onNavigate: handleNavigate,
  }), [archetypeScores, vibeIdData, handleArchetypeClick, handleNavigate])

  // ═══════════════════════════════════════════════════════════════════════════
  // Render - 统一响应式布局
  // ═══════════════════════════════════════════════════════════════════════════

  // 主内容区域 (响应式)
  const mainContent = (
    <div className="space-y-6">
      {/* 移动端导航栏 */}
      <nav className="nav lg:hidden">
        <div className="nav-container">
          <button onClick={() => router.back()} className="text-text-secondary">
            ← 返回
          </button>
          <h1 className="font-serif text-card-title text-text-primary">VibeID</h1>
          <button className="text-text-secondary">···</button>
        </div>
      </nav>

      {/* 页面标题 */}
      <div className="text-center mb-4 lg:mb-8 fade-in px-4 lg:px-0">
        <h1 className="font-serif text-xl lg:text-2xl text-text-primary mb-2">💎 你的 VibeID</h1>
        <p className="text-text-secondary text-sm lg:text-base">人是多面的。你被理解、被允许、被赋能。</p>
      </div>

      {isLoading ? (
        <div className="px-4 lg:px-0">
          <VibeIDCardSkeleton variant="full" className="hidden lg:block" />
          <VibeIDCardSkeleton variant="compact" className="lg:hidden" />
        </div>
      ) : vibeIdData ? (
        <div className="px-4 lg:px-0">
          {/* VibeID 卡片 - PC端完整版 */}
          <div className="fade-in hidden lg:block">
            <VibeIDCard
              data={vibeIdData}
              variant="full"
              showExplanation
              onExplore={() => handleNavigate('/chat')}
            />
          </div>

          {/* VibeID 卡片 - 移动端紧凑版 */}
          <div className="fade-in lg:hidden mb-6">
            <VibeIDCard
              data={vibeIdData}
              variant="compact"
              onExplore={() => handleNavigate('/chat')}
            />
          </div>

          {/* 四维原型 - 仅移动端 */}
          <FourDimensions vibeIdData={vibeIdData} />

          {/* 底层密码 - 仅移动端 */}
          <div className="card p-5 mb-4 fade-in lg:hidden" style={{ animationDelay: '200ms' }}>
            <h2 className="font-serif text-card-title text-text-primary mb-4">📍 底层密码</h2>
            <div className="space-y-2">
              <div className="flex items-start gap-2">
                <span className="text-xs text-text-secondary w-12 flex-shrink-0">八字</span>
                <span className="text-sm text-text-primary">{vibeIdData.baziBrief}</span>
              </div>
              <div className="flex items-start gap-2">
                <span className="text-xs text-text-secondary w-12 flex-shrink-0">星盘</span>
                <span className="text-sm text-text-primary">{vibeIdData.zodiacBrief}</span>
              </div>
            </div>
          </div>

          {/* 成长方向 - 仅移动端 */}
          <div className="card p-5 mb-4 fade-in lg:hidden" style={{ animationDelay: '300ms' }}>
            <h2 className="font-serif text-card-title text-text-primary mb-3">✨ 成长方向</h2>
            <p className="text-sm text-text-secondary leading-relaxed">&ldquo;{vibeIdData.growthTip}&rdquo;</p>
          </div>

          {/* 原型雷达图 - 仅移动端 */}
          {archetypeScores && (
            <div className="card p-5 fade-in lg:hidden" style={{ animationDelay: '400ms' }}>
              <h2 className="font-serif text-card-title text-text-primary mb-4 text-center">
                🎯 原型分布
              </h2>
              <div className="flex justify-center">
                <ArchetypeRadar
                  scores={archetypeScores}
                  highlightArchetype={vibeIdData.primaryArchetype}
                  size={280}
                  showLabels
                  interactive={false}
                />
              </div>
            </div>
          )}

          {/* 选中的原型详情 - PC端 */}
          {selectedArchetype && (
            <div className="fade-in hidden lg:block" style={{ animationDelay: '100ms' }}>
              <div className="flex items-center justify-between mb-4">
                <h2 className="font-serif text-lg text-text-primary">
                  {getArchetypeMeta(selectedArchetype).emoji} {getArchetypeMeta(selectedArchetype).name} 详情
                </h2>
                <button
                  onClick={handleClearSelection}
                  className="text-sm text-text-secondary hover:text-text-primary"
                >
                  关闭 ×
                </button>
              </div>
              <ArchetypeDetail
                archetype={selectedArchetype}
                score={archetypeScores?.[selectedArchetype]}
                isCore={selectedArchetype === vibeIdData.primaryArchetype}
              />
            </div>
          )}

          {/* 12 原型完整列表 - PC端 */}
          {archetypeScores && !selectedArchetype && (
            <div className="card p-6 fade-in hidden lg:block" style={{ animationDelay: '200ms' }}>
              <h2 className="font-serif text-lg text-text-primary mb-4">📊 12 原型完整分布</h2>
              <ArchetypeList
                scores={archetypeScores}
                showAll
                onArchetypeClick={handleArchetypeClick}
              />
            </div>
          )}
        </div>
      ) : (
        <div className="card p-8 text-center mx-4 lg:mx-0">
          <p className="text-text-secondary">暂无 VibeID 数据</p>
          <button
            onClick={() => handleNavigate('/onboarding')}
            className="mt-4 px-6 py-2 bg-accent text-white rounded-lg"
          >
            开始生成
          </button>
        </div>
      )}
    </div>
  )

  return (
    <>
      {/* PC端: 使用 PCLayout 三栏布局 */}
      <div className="hidden lg:block">
        <PCLayout
          breadcrumb={[{ label: '首页', href: '/' }, { label: 'VibeID' }]}
          aside={<AsideContent {...asideProps} />}
        >
          {mainContent}
        </PCLayout>
      </div>

      {/* 移动端: 简单单栏布局 */}
      <div className="min-h-screen bg-bg-primary lg:hidden">
        <main className="max-w-lg mx-auto pb-24">
          {mainContent}
        </main>

        {/* 底部输入栏 */}
        <div className="fixed bottom-0 left-0 right-0 p-4 bg-bg-primary safe-bottom">
          <div className="max-w-lg mx-auto">
            <div className="omnibar">
              <input
                type="text"
                placeholder="Type '/' for commands..."
                className="omnibar-input"
                onFocus={() => handleNavigate('/chat')}
              />
              <button className="omnibar-send">▶</button>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

